        "components": [
          {
            "component_name": "Deep State Evaluator",
            "primary_agent": "Mnemosyne",
            "secondary_agent": "Ebing",
            "function": "Evaluate 叶凌霜's state: Just finished combat, adrenaline likely still elevated but masked by her calm demeanor. Mission focus is high. Physical state is peak (due to plot armor/previous intervention). Environment is transitioning to night.",
            "input_data": ["Aggregated Context", "Core Objective Function"],
            "output_data": ["Deep Assessment Report of Current State (Focused, Determined, Physically Capable, Approaching Nightfall)"]
          },
          {
            "component_name": "Action/Option Generator",
