              {
                "name": "Narrative/Logical Simulation",
                "lead": "Apollo",
                "function": "Predict smooth narrative flow towards 恶狼寨, check consistency with established world (geography towards 恶狼寨), and character motivations (no illogical detours).",
                "outputs": ["Narrative Coherence Assessment (High)", "Potential Conflict Points (None)"]
              }
            ],
            "input_data": ["Potential Actions/Narrative Elements", "Deep Assessment Report"],
            "output_data": ["Simulated Outcome Report (Consistent narrative progression towards destination with expected environmental and psychological elements)"]
          }
        ]
