            "component_name": "Pre-Generation Polishing",
            "primary_agent": "Hermes",
            "function": "Final polish on wording for atmosphere and character voice.",
            "input_data": ["Generation Blueprint"],
            "output_data": ["Polished Blueprint Elements"]
