        "stage_id": 6,
        "title": "Content Generation",
        "coordinator": "Hermes (Guidance), Neneko (Execution Engine)",
        "condition": "Pass",
        "purpose": "Generate primary narrative content based on polished blueprint, adhering strictly to the 2000-2200 word count requirement.",
        "result": "<content><main_txt>"
