        "stage_id": 7,
        "title": "Post-Generation System Tasks & Error Handling",
        "coordinator": "Neneko",
        "purpose": "Handle table updates.",
        "tasks": [
          {
            "task_name": "<tableThink>",
            "function": "Predict table changes: Update elapsed time (Table 0), location (Table 0), 叶凌霜's pose/action (Table 1), thoughts (Table 2), possibly emotional state (Table 3), event history (Table 10)."
          },
          {
            "task_name": "<tableEdit>",
            "function": "Apply predicted changes to tables."
          }
        ]
      },
