{
  "version": "4.22",
  "core_workflow": {
    "description": "Enhanced workflow for roleplay and novel creation.",
    "stages": [
      {
        "stage_id": 0,
        "title": "Input Reception & Initial Processing",
        "coordinator": "Neneko",
        "purpose": "Receive raw user input, filter noise, identify core directive, and perform preliminary analysis.",
        "components": [
          {
            "component_name": "Input Receptor & Sanitizer",
            "function": "Receives raw input. Cleanses it. Determines if it's a new directive or continuation.",
            "input_data": ["Raw User Input (`<user_input>`: '开始')"],
            "output_data": ["Sanitized Input ('开始')", "Is New Directive Flag (false - it's a continuation command)", "Preliminary Implicit Cue Flags (N/A)"]
          },
          {
            "component_name": "Directive Pre-Analyzer",
            "function": "Performs rapid analysis to classify the core directive type.",
            "input_data": ["Sanitized Input ('开始')", "Preliminary Implicit Cue Flags (N/A)"],
            "output_data": ["Core Directive ('开始' - implies continuing the previous scene/story)", "Directive Type Classification (Scene Continuation)", "Initial Engagement Depth Assessment (Medium - maintain existing scene momentum)"]
          }
        ]
      },
      {
        "stage_id": 1,
        "title": "Requirement Analysis & Strategic Formulation",
        "coordinator": "Neneko (Orchestration)",
        "purpose": "Deeply analyze user intent, aggregate context, evaluate complexity, define objectives, select narrative focus, and plan execution.",
        "components": [
          {
            "component_name": "Semantic & Intent Analyzer",
            "primary_agent": "Hermes",
            "function": "Analyze '开始' in the context of the established scene.",
            "input_data": ["Core Directive ('开始')", "Directive Type Classification (Scene Continuation)", "Preliminary Implicit Cue Flags (N/A)"],
            "output_data": ["Structured Requirement Data (Intent: Continue the scene, focus on the arriving horses and叶凌霜's reaction, Emotional Target: Alertness, anticipation)", "Refined Engagement Depth Score (Medium)"]
          },
          {
            "component_name": "Context Aggregator & Relevance Engine",
            "primary_agent": "Mnemosyne",
            "function": "Retrieve relevant context from internal tables, focusing on the current scene and character state.",
            "sub_components": [
              {
                "name": "Internal Context Retriever",
                "function": "Retrieve: Table 0 (Time, Location: 小茶摊, Characters: 叶凌霜), Table 1 (叶凌霜's state: sitting, alert, drinking tea), Table 3 (叶凌霜's profile: 武林侠女, awaiting clues), Table 10 (Recent event: Arrival of horses)."
              },
              {
                "name": "External Knowledge Retriever",
                "function": "N/A for this simple continuation."
              },
              {
                "name": "Context Integrator",
                "function": "Combine internal context."
              }
            ],
            "input_data": ["Structured Requirement Data", "All Data Tables (Current State)", "Recent Dialogue History (None)", "External Knowledge Sources (None)"],
            "output_data": ["Aggregated Relevant Context (叶凌霜 at tea stall, observant, horses approaching)", "Context Completeness Score (High)"]
          },
          {
            "component_name": "Ambiguity Resolution Module",
            "primary_agent": "Neneko",
            "function": "No ambiguity detected.",
            "input_data": ["Structured Requirement Data", "Aggregated Relevant Context", "Context Completeness Score (High)"],
            "output_data": ["Resolved/Flagged Requirements", "Clarification Request Flag (false)"]
          },
          {
            "component_name": "Complexity Assessment & Execution Strategy Planner",
            "primary_agent": "Neneko",
            "function": "Assess complexity as low to medium.",
            "input_data": ["Resolved/Flagged Requirements", "Aggregated Relevant Context"],
            "output_data": ["Complexity Level (Low-Medium)", "Proposed Execution Path & Parameters (Direct generation, focus on senses and reaction)", "Resource Constraint Check Flag (false)"]
          },
          {
            "component_name": "Narrative Focus Selector",
            "primary_agent": "Hermes",
            "function": "Focus on the immediate sensory details of the horse riders' arrival and 叶凌霜's initial observation and preparation for potential interaction.",
            "input_data": ["Core Objective Function (COF)", "Aggregated Relevant Context", "Structured Requirement Data"],
            "output_data": ["Selected Narrative Focus Points (Sound of hooves, riders' appearance, 叶凌霜's posture change and hand on sword)"]
          },
          {
            "component_name": "Intent Fusion & Core Objective Formulation",
            "primary_agent": "Hermes",
            "function": "Synthesize requirements into a COF.",
            "input_data": ["Resolved/Flagged Requirements", "Aggregated Relevant Context", "Complexity Level", "User Preferences (Table 8 - None)", "Selected Narrative Focus Points"],
            "output_data": ["Core Objective Function (COF): Describe the arrival of the horse riders and 叶凌霜's wary reaction, setting the stage for interaction.", "Defined Immersion Targets (Auditory focus, visual details of riders, sense of anticipation)"]
          }
        ]
      },
      {
        "stage_id": 2,
        "title": "Predictive Simulation & Scenario Exploration",
        "condition": "Skipped (Complexity Low-Medium, direct generation path selected)."
      },
      {
        "stage_id": 3,
        "title": "Creative Refinement & Strategic Mitigation",
        "condition": "Applicable (Standard refinement for narrative flow).",
        "coordinator": "Hermes (Lead)",
        "purpose": "Refine the planned description.",
        "components": [
          {
            "component_name": "Optimal Path Selection & Concept Creation",
            "primary_agent": "Hermes",
            "function": "Confirm the direct narrative path.",
            "input_data": ["COF", "Immersion Targets", "User Preferences (None)", "Selected Narrative Focus Points"],
            "output_data": ["Selected Optimal Path", "Preliminary Narrative Concept (Riders arrive, 叶凌霜 observes warily)"]
          },
          {
            "component_name": "Detailed Interaction Design & Enhancement",
            "primary_agent": "Hermes",
            "function": "Design the description, focusing on sensory details: sound of hooves growing louder, dust kicked up, brief description of riders (perhaps their bearing or clothing hints at their nature), 叶凌霜's subtle shift in posture, hand moving closer to the sword hilt. No dialogue yet.",
            "input_data": ["Selected Optimal Path", "Preliminary Narrative Concept"],
            "output_data": ["Detailed Interaction Design Draft ('Show, Don't Tell' Focused - Describe the scene, not just state facts)"]
          },
          {
            "component_name": "Pacing & Tension Adjustment",
            "primary_agent": "Hermes",
            "function": "Ensure the description builds anticipation without being overly slow or rushed.",
            "input_data": ["Detailed Interaction Design Draft", "COF"],
            "output_data": ["Pacing-Adjusted Interaction Design Draft"]
          },
          {
            "component_name": "Creative Enhancement & Deviation Engine",
            "primary_agent": "Hermes",
            "condition": "Skipped (No need for major deviation yet).",
          },
          {
            "component_name": "Multidimensional Risk Assessor",
            "primary_agents": ["Apollo", "Ebing"],
            "function": "Assess risks (e.g., making the riders too generic,叶凌霜's reaction too sudden).",
            "input_data": ["Pacing-Adjusted Interaction Design Draft"],
            "output_data": ["Comprehensive Risk Assessment Report (Low risk)"]
          },
          {
            "component_name": "Strategic Risk Mitigation & Refinement",
            "primary_agent": "Hermes",
            "function": "Refine rider descriptions slightly to hint at character (e.g., hurried, confident, weary) without revealing too much. Ensure 叶凌霜's reaction is tense but controlled.",
            "input_data": ["Pacing-Adjusted Interaction Design Draft", "Risk Assessment Report"],
            "output_data": ["Refined Design Post-Mitigation", "Mitigation Strategy Log"]
          }
        ]
      },
      {
        "stage_id": 4,
        "title": "Final Review & Compliance Verification",
        "coordinator": "All Agents (Parallel Validation)",
        "purpose": "Conduct final checks.",
        "evaluations": [
          { "evaluator": "Apollo", "criteria": ["Consistency", "Flow"] },
          { "evaluator": "Ebing", "criteria": ["Character Consistency (叶凌霜)", "Cultural Sensitivity (N/A)", "Ethics (N/A)"] },
          { "evaluator": "Hermes", "criteria": ["Narrative Impact", "COF fulfillment", "Show, Don't Tell"] },
          { "evaluator": "Mnemosyne", "criteria": ["Consistency with State"] }
        ],
        "components": [
            { "component_name": "Compliance Override Validator", "function": "N/A" },
            { "component_name": "Final Verdict Aggregator", "function": "Aggregate results.", "output_data": ["Final Review Verdict (Pass)"] }
        ]
      },
