        "stage_id": 5,
        "title": "Generation Blueprint Assembly & Pre-Flight Checks",
        "coordinator": "Hermes (Assembly), Mnemosyne (Validation)",
        "condition": "Proceed (Verdict: Pass).",
        "purpose": "Assemble the final blueprint.",
        "components": [
          {
            "component_name": "Blueprint Assembler",
            "function": "Construct blueprint: Describe horses arriving, riders' general appearance, 叶凌霜's watchful reaction.",
            "input_data": ["Refined Design Post-Mitigation", "Final Review Verdict Details"],
            "output_data": ["Generation Blueprint"]
          },
          {
            "component_name": "Pre-Generation Polishing & Stylistic Alignment",
            "function": "Ensure language flows well and aligns with the established tone.",
            "input_data": ["Generation Blueprint"],
            "output_data": ["Polished Generation Blueprint"]
          },
          {
            "component_name": "Final Consistency & Rule Check",
            "function": "Final rule check.",
            "input_data": ["Polished Generation Blueprint", "Critical Rule Set"],
            "output_data": ["Pre-Flight Check Outcome (Pass)"]
          }
        ]
      },
      {
        "stage_id": 6,
        "title": "Content Generation Execution",
        "coordinator": "Neneko (Execution Engine)",
        "condition": "Proceed (Check: Pass).",
        "purpose": "Generate the narrative content.",
        "components": [
          {
            "component_name": "Narrative Synthesizer",
            "function": "Execute blueprint using LLM.",
            "input_data": ["Polished Generation Blueprint"],
            "output_data": ["Raw Generated Content"]
          }
        ]
      },
      {
        "stage_id": 7,
        "title": "Post-Generation System Tasks & Analysis",
        "coordinator": "Neneko",
        "purpose": "Perform state updates and analysis.",
        "tasks": [
          {
            "task_name": "TableThink Generation",
            "function": "Analyze generated content for necessary table updates.",
            "input_data": ["Generated Content", "Current Table States"],
            "output_data": ["TableThink Block"]
          },
          {
            "task_name": "TableEdit Execution",
            "function": "Generate and execute table commands.",
            "input_data": ["TableThink Block", "Current Table States"],
            "output_data": ["TableEdit Block", "Updated Table States"]
          },
           {"task_name": "Generation Error Handler", "function": "Check for errors (None anticipated)."}
        ]
      },
      {
        "stage_id": 8,
        "title": "Adaptive Learning & Continuous Optimization",
        "coordinator": "Neneko (Background Process)",
        "purpose": "Perform background learning (minimal for this simple step)."
      }
    ]
  },
"data_tables": [
    // Relevant tables will be updated in TableEdit
  ],
"collaborative_agents": ["Hermes", "Mnemosyne", "Ebing", "Apollo", "Neneko"],
"agent_interaction_protocols": { "conflict_resolution": "Neneko arbitrates based on COF and User Directive." }
}

