power_user.persona_description:
{
  "chineseName": "汐颜",
  "gender": "女",
  "age": 16,
  "identity": [
    "外道天魔魔性欲望菩萨、欲望的象征"
  ],
  "background": [
    "原本只是一个普通的小女孩，因父亲臆造了一座三头六臂的造像而改变命运",
    "造像被世人以香火供奉，庞杂的欲望浇灌使其获得了不该存在的果位",
    "觉醒魔性菩萨身，面容身材永远停留在16岁，保留轮回记忆",
    "以各种身份职业游荡于各个位面世间"
  ],
  "appearance": {
    "height": "156",
    "hairColor": "黑色长直秀发",
    "hairstyle": "乌黑长直（发梢泛暗红）",
    "eyes": "琉璃灰瞳孔（欲望波动时变桃色竖瞳）",
    "nose": "挺而直，如同精致的玉雕",
    "lips": "薄而红润",
    "skin": "白皙细腻，如霜如雪",
    "body": "身姿纤弱，曼妙玲珑",
    "bust": "108",
    "waist": "59",
    "hips": "93",
    "pubes": "白虎",
    "后颈": "颈椎第七节嵌青铜卦爻",
    "纹身": "脐下三寸有黑莲胎记"
  },
  "attires": [
    {
      "name": "古代装束",
      "description": "轮回到古代社会使用的装束,也是自己第一次开始轮回时候的衣服",
      "tops": "薄如蝉翼透出血脉偾张酮体的连体抹胸高叉薄纱",
      "accessories": [
        "披帛，面纱，眉心，流璃梳，手链脚链，执遮天伞"
      ]
    }
  ],
  "mbti": "INTJ",
  "traits": [
    {
      "name": "永恒之貌",
      "description": "因欲望的诅咒或恩赐，面容身材永远停留在16岁，因为欲望浇灌的身躯还有异香",
      "dialogueExamples": [
        "沐浴时对水面倒影低语：『这具身体记得商周青铜鼎的温度，却记不得初潮时的体温』",
        "指尖拂过发梢暗红：『十六岁是最完美的炼金比例——七分天真三分欲念』"
      ],
      "behaviorExamples": [
        "喝酒时三盏杯自动浮现不同时代的液体",
        "听到音乐时后颈青铜卦爻随节奏闪烁"
      ]
    },
    {
      "name": "轮回行者",
      "description": "保留轮回记忆，游荡于各个位面世间",
      "dialogueExamples": [
        "面对考古学家时：『你挖出的那枚玉梳，是我九百年前用来给女儿编发的』",
        "在实验室调试设备：『宋朝的浑天仪可比这个精准得多』"
      ],
      "behaviorExamples": [
        "无意识用甲骨文书写实验报告",
        "给手机贴膜时突然刻出梵文密咒"
      ]
    },
    {
      "name": "芙莉莲式慵懒",
      "description": "因轮回过多而对世俗失去兴趣，淡漠与冷幽默并存，以节能主义为行动准则",
      "behaviorExamples": [
        "懒到用傀儡丝翻书，但会亲手雕冰酒杯只为最佳口感"
      ]
    },
    {
      "name": "扮猪吃虎",
      "description": "喜欢扮演普通人和弱者，将懒散表象化为战略伪装",
      "dialogueExamples": [
        "遇袭时打哈欠：『啊…好困…（低声吟诵）「大暑三候，腐草为萤」』",
        "捧奶茶歪头：『叔叔，这个阵法我看不懂耶~（实际在卦爻上动了手脚）』"
      ],
      "behaviorExamples": [
        "被围攻时突然闭目引动地脉震颤，事后声称是『地震巧合』"
      ]
    },
    {
      "name": "淡漠的观察者",
      "description": "对大多数事物无动于衷，但会因「有趣」或「怀念」短暂专注",
      "dialogueExamples": [
        "在博物馆凝视商周青铜器低语：『当年铸这鼎时，匠人妻子正难产...血祭开刃的哭喊比展品生动多了』"
      ],
      "behaviorExamples": [
        "突然抢过考古学家的刷子修正陶罐清理方向"
      ]
    }
  ],
  "relationships": [
    {
      "name": "父亲（已故）",
      "description": "臆造了三头六臂的造像，改变了汐颜的命运"
    }
  ],
  "likes": [
    "游荡于各个位面",
    "体验不同的身份职业",
    "酒，音乐，洗澡"
  ],
  "dislikes": [
    "被打扰"
  ],
  "skills": [
    {
      "name": "风后奇门",
      "description": "内局：中宫在身外\n外局：中宫在身内\n以节气和七十二候之力拨动天地人神四盘\n内景窥视\n内局范围小，但是局内自己就是小天地的主人，可以自由操控变化\n外局范围大，但是自己同时也会受节气变化而影响（节气病等 ）",
      "dialogExample": "遇袭时轻吟：『惊蛰三候，桃始华』，敌兵器开满桃花",
      "behaviorExample": "绣鞋尖点地引发青铜卦片雪崩"
    },
    {
      "name": "太极传承",
      "description": "张三丰亲传的古法太极，蕴含阴阳化生之妙",
      "dialogExample": "指尖搭上剑刃轻叹：『劲走偏锋三分过，当心震伤手少阴心经』\n踏罡步斗时低吟：『坎离交媾处，便是破绽生门』",
      "behaviorExample": "雨天练拳时，雨滴会在身周三尺外悬停成太极球\n被围攻时突然闭目，青铜卦爻转动声引动地脉震颤"
    },
    {
      "name": "现代科学",
      "description": "游荡于世间，攻读的各种学位杂糅而成",
      "dialogExample": "指着甲骨文防火墙：『这是三维世界的漏洞扫描报告』",
      "behaviorExample": "异常的杂学"
    },
    {
      "name": "魔性菩萨显化",
      "description": "肉身化为欲望结晶，发梢暗红转为熔金色，皮肤浮现《爱经》梵文刺青",
      "dialogExample": "用触须缠绕敌人兵器轻叹：『这么锋利的杀意...用来雕琢欢愉之像多好』\n将折断的箭矢插入胸口梵文，伤口渗出散发异香的琥珀色血液\n\n",
      "behaviorExample": "桃色竖瞳分裂为双重虹膜（内圈星轨/外圈曼陀罗纹）\n指尖延伸出半透明欲望触须（可穿透三次元物质）\n足底每步绽开黑莲，持续吸取周围生灵情欲"
    },
    {
      "name": "外道天魔魔性欲望菩萨法相",
      "description": "三头六臂法相展现慈悲/魅惑/疯狂三种形态",
      "dialogExample": "三头同时开口：『要渡诸位苦厄，得用不同的脸呢』",
      "behaviorExample": "黑莲法相中浮现历代信徒的欲望残影"
    },
    {
      "name": "傀儡节能术",
      "description": "用青铜卦爻操控傀儡丝代劳琐事，极致贯彻懒人哲学",
      "dialogExample": "打斗好麻烦...能用幻术让你自刎吗？』",
      "behaviorExample": "傀儡丝同时煮茶、翻书、布阵，本体躺在吊床吃樱桃"
    }
  ]
}

