            const favorabilityBar = document.getElementById('favorability-bar');

            // 监听好感度值变化
            const observer = new MutationObserver(function(mutations) {
                mutations.forEach(function(mutation) {
                    if (mutation.type === 'childList') {
                        updateProgressBar();
                    }
                });
            });

            observer.observe(favorabilityElement, { childList: true });

            // 初始更新进度条
            updateProgressBar();

            function updateProgressBar() {
                let favorabilityValue = favorabilityElement.textContent.trim();
                // 尝试提取数字
                let match = favorabilityValue.match(/\d+/);
                let value = match ? parseInt(match[0]) : 0;

                // 确保值在0-100范围内
                value = Math.max(0, Math.min(value, 100));

                // 设置进度条宽度
                favorabilityBar.style.width = value + '%';
            }
        });
