  } catch (error) {
    // Catch errors from initial setup or unexpected issues in the main try block
    console.error(`[总结脚本2] ${summaryType} 流程中发生未捕获错误:`, error);
    triggerSlash(`/echo severity=error [总结脚本2] ${summaryType} 流程失败: ${error.message}`);
    // Errors should still lead to the finally block for cleanup
  } finally {
    // --- Post-Summary Actions (Inside Finally for robustness) ---
    console.log(`[总结脚本2] [Finally] Entered finally block.`);
    console.log(
      `[总结脚本2] [Finally] Values before final actions: promptMessageId=${promptMessageId}, targetMessageId=${targetMessageId}, saveConfirmed=${saveConfirmed}, keepCount=${keepCount}`,
