    }

    // B. Save Lorebook Entry (if content confirmed)
    if (saveConfirmed && summaryContent && chatLorebookName) {
      try {
        console.log(`[总结脚本2] [Finally] 将总结添加到聊天世界书 (${chatLorebookName})...`);
        const timestamp = new Date().toISOString();
        const entryComment = `${baseTitle} (${summaryType})`;
        const entryKey = `${entryComment} ${timestamp}`;
        const newEntryDefaults = {
          comment: entryComment,
          key: [entryKey],
          content: summaryContent,
          enabled: true,
          type: 'constant',
          position: 'before_prompt',
          order: 0,
        };
        const newEntryUid = await createLorebookEntry(chatLorebookName, newEntryDefaults);
        if (newEntryUid !== undefined && newEntryUid !== null) {
          console.log(`[总结脚本2] [Finally] 成功创建世界书条目 (UID: ${newEntryUid})`);
          triggerSlash(`/echo severity=success [总结脚本2] 成功将 ${summaryType} (${baseTitle}) 添加到聊天世界书。`);
        } else {
          throw new Error('创建条目未返回有效 UID');
        }
      } catch (loreError) {
        console.error(`[总结脚本2] [Finally] 处理聊天世界书时出错:`, loreError);
        triggerSlash(
          `/echo severity=error [总结脚本2] ${summaryType} 添加总结到聊天世界书时出错: ${loreError.message}`,
        );
      }
    }

    // C. Perform Final Hide (Only if content confirmed, AFTER unhiding temp)
    if (saveConfirmed) {
      if (promptMessageId >= 0 && keepCount >= 0) {
        const finalHideEndIndex = promptMessageId - keepCount;
        if (finalHideEndIndex >= 0) {
          console.log(
            `[总结脚本2] [Finally] 执行最终隐藏: 0-${finalHideEndIndex} (保留 ${keepCount} 层，基于提示ID ${promptMessageId})`,
          );
          triggerSlash(`/echo severity=info [总结脚本2] 正在执行最终隐藏 (保留 ${keepCount} 层)...`);
          try {
            await triggerSlash(`/hide 0-${finalHideEndIndex}`);
            console.log(`[总结脚本2] [Finally] 成功执行最终隐藏。`);
          } catch (hideError) {
            console.error(`[总结脚本2] [Finally] 执行最终隐藏时出错:`, hideError);
            triggerSlash(`/echo severity=error [总结脚本2] ${summaryType} 执行最终隐藏时出错: ${hideError.message}`);
          }
        } else {
          console.log(`[总结脚本2] [Finally] 无需执行最终隐藏 (计算出的结束索引 ${finalHideEndIndex} < 0)。`);
        }
      } else {
        console.warn(
          `[总结脚本2] [Finally] 无法执行最终隐藏，promptMessageId (${promptMessageId}) 或 keepCount (${keepCount}) 无效。`,
        );
      }
    } else {
      // Log why final hide was skipped (only depends on saveConfirmed now)
      if (!saveConfirmed) {
        console.log(`[总结脚本2] [Finally] 未确认保存总结结果，跳过最终隐藏步骤。`);
      } else {
        console.log(`[总结脚本2] [Finally] 未知原因导致跳过最终隐藏。`); // Should not happen if saveConfirmed is true
      }
    }

    // D. Delete Prompt and Response messages (with confirmation) - This part's condition is independent of saving
    let canAttemptDelete = false;
    let deleteConfirmText = '';

    if (promptMessageId >= 0 && targetMessageId >= 0 && targetMessageId >= promptMessageId) {
      console.log(`[总结脚本2] [Finally] Condition MET: Both IDs seem valid.`);
      deleteConfirmText = `确认删除最后两条消息 (提示: ${promptMessageId}, 回复: ${targetMessageId})?`;
      canAttemptDelete = true;
    } else if (promptMessageId >= 0 && targetMessageId < promptMessageId) {
      console.log(`[总结脚本2] [Finally] Condition MET: Only prompt ID seems valid.`);
      deleteConfirmText = `AI 回复无效或未生成。确认删除提示消息 (${promptMessageId})?`;
      canAttemptDelete = true;
    } else {
      console.warn(
        `[总结脚本2] [Finally] Condition NOT MET for deletion. IDs (Prompt: ${promptMessageId}, AI: ${targetMessageId})`,
      );
    }
    console.log(`[总结脚本2] [Finally] Result of deletion check: canAttemptDelete = ${canAttemptDelete}`);

    if (canAttemptDelete) {
      console.log(`[总结脚本2] [Finally] Proceeding to show deletion confirmation popup...`);
      let deleteConfirmed = false;
      try {
        triggerSlash(`/echo severity=warning [总结脚本2] 请求确认删除临时消息...`);
        const userChoiceDelete = await triggerSlashWithResult(
          `/popup title="确认删除消息" okButton="确定删除" cancelButton="取消" result=true "${deleteConfirmText}"`,
        );
        console.log('[Finally] Delete confirmation popup choice:', userChoiceDelete);
        if (userChoiceDelete === '1') {
          deleteConfirmed = true;
          console.log('[总结脚本2] [Finally] 用户确认删除消息。');
        } else {
          console.log('[总结脚本2] [Finally] 用户取消删除消息。');
        }
      } catch (popupError) {
        console.error('[总结脚本2] [Finally] 显示删除确认弹窗时出错:', popupError);
        triggerSlash(`/echo severity=error [总结脚本2] 显示删除确认弹窗失败: ${popupError.message}`);
      }

      if (deleteConfirmed) {
        try {
          let deleteRange = '';
          if (targetMessageId >= promptMessageId && promptMessageId >= 0) {
            // Delete range if both valid
            deleteRange = `${promptMessageId}-${targetMessageId}`;
            console.log(`[总结脚本2] [Finally] 执行删除消息范围: ${deleteRange}`);
            await triggerSlash(`/cut ${deleteRange}`);
            console.log(`[总结脚本2] [Finally] ${summaryType} 成功删除提示和回复消息。`);
          } else if (promptMessageId >= 0) {
            // Delete only prompt
            deleteRange = `${promptMessageId}`;
            console.log(`[总结脚本2] [Finally] 执行删除单个提示消息: ${deleteRange}`);
            await triggerSlash(`/cut ${deleteRange}`);
            console.log(`[总结脚本2] [Finally] ${summaryType} 成功删除提示消息。`);
          } else {
            console.error(`[总结脚本2] [Finally] 确认删除后发现无效的消息 ID，无法删除。`);
          }
        } catch (deleteError) {
          console.error(`[总结脚本2] [Finally] ${summaryType} 删除消息 (${deleteRange}) 时出错:`, deleteError);
          triggerSlash(
            `/echo severity=error [总结脚本2] ${summaryType} 删除消息 (${deleteRange}) 时出错: ${deleteError.message}`,
          );
        }
      }
    } // end if(canAttemptDelete)

    // E. Restore original preset
    if (originalPreset) {
      console.log(`[总结脚本2] ${summaryType} 完成，切换回原预设: ${originalPreset}`);
      triggerSlash(`/echo severity=info [总结脚本2] 正在切换回原预设: ${originalPreset}...`);
      try {
        await triggerSlash(`/preset select \"${originalPreset}\"`);
        console.log('[总结脚本2] 成功切换回原预设。');
        triggerSlash(`/echo severity=info [总结脚本2] 已切换回原预设: ${originalPreset}。`);
      } catch (switchBackError) {
