    } else {
      console.warn('[总结脚本2] 未记录原始预设，无法自动切换。');
      triggerSlash(`/echo severity=warning [总结脚本2] 未记录原始预设，请手动切换。`);
    }

    console.log(`[总结脚本2] ${summaryType} 流程结束。`);
    triggerSlash(`/echo severity=info [总结脚本2] ${summaryType} 流程结束。`);
  }
});

