'use strict';

// Constants
const SMALL_SUMMARY_PRESET = 'keminiSummary【小总结】';
const LARGE_SUMMARY_PRESET = 'keminiSummary【大总结】'; // Added from 总结.js
const SUMMARY_LOREBOOK_COMMENT_PREFIX = '范围总结';
const OVERALL_SUMMARY_LOREBOOK_COMMENT = '整体总结'; // Comment for full summary

// Helper for error handling
var detail;
(function (detail) {
  function errorCatched(fn) {
    const onError = error => {
      console.error('[高楼层总结脚本] 错误:', error);
      triggerSlash(
        `/echo severity=error (高楼层总结脚本)${error.stack ? error.stack : error.name + ': ' + error.message}`,
      );
    };
    return async (...args) => {
      try {
        const result = await fn(...args);
        return result;
      } catch (error) {
        onError(error);
        return undefined;
      }
    };
  }
  detail.errorCatched = errorCatched;
})(detail || (detail = {}));

// Function to parse range input "start-end"
function parseRange(rangeString) {
  if (!rangeString || typeof rangeString !== 'string') return null;
  const parts = rangeString.split('-');
  if (parts.length !== 2) return null;
  const start = parseInt(parts[0].trim());
  const end = parseInt(parts[1].trim());
  if (
    isNaN(start) ||
    isNaN(end) ||
    !Number.isInteger(start) ||
    !Number.isInteger(end) ||
    start < 0 ||
    end < 0 ||
    start > end
  ) {
    return null;
  }
  return { start, end };
}

// New Function: Manually Hide Layers based on input
const runManualHide = detail.errorCatched(async () => {
  triggerSlash(`/echo severity=info [高楼层总结脚本] 请求手动隐藏/显示楼层...`);

  try {
    const userInput = await triggerSlashWithResult(
      `/input title="手动隐藏/显示楼层" default="0-10" "请输入要隐藏的范围 (如 0-15) 或要保留的最近层数 (如 3)："`,
    );

    if (userInput === null || userInput === undefined || userInput === 'false') {
      triggerSlash(`/echo severity=info [高楼层总结脚本] 用户取消操作。`);
      return;
    }

    const trimmedInput = userInput.trim();
    const lastMessageId = await getLastMessageId();

    if (lastMessageId < 0) {
      triggerSlash(`/echo severity=info [高楼层总结脚本] 当前没有消息，无需操作。`);
      return;
    }

    let rangeToHide = null;
    let rangesToUnhide = [];

    if (trimmedInput.includes('-')) {
      // --- Range Hide Logic --- (e.g., "0-15")
      const parsedRange = parseRange(trimmedInput);
      if (!parsedRange) {
        triggerSlash(`/echo severity=error [高楼层总结脚本] 无效的隐藏范围格式: "${trimmedInput}"。`);
        return;
      }
      // Clamp the range to valid message IDs
      rangeToHide = {
        start: Math.max(0, parsedRange.start),
        end: Math.min(lastMessageId, parsedRange.end),
      };

      if (rangeToHide.end < rangeToHide.start) {
        // If clamping makes the range invalid (e.g., input 100-200 when lastId is 50), treat as hiding nothing
        console.log(`[高楼层总结脚本] 指定隐藏范围 ${trimmedInput} 无效或超出当前消息范围 (${lastMessageId})。`);
        rangeToHide = null;
      } else {
        console.log(`[高楼层总结脚本] 准备隐藏指定范围: ${rangeToHide.start}-${rangeToHide.end}`);
      }

      // Determine ranges to unhide
      if (rangeToHide) {
        // Unhide before the hidden range
        if (rangeToHide.start > 0) {
          rangesToUnhide.push({ start: 0, end: rangeToHide.start - 1 });
        }
        // Unhide after the hidden range
        if (rangeToHide.end < lastMessageId) {
          rangesToUnhide.push({ start: rangeToHide.end + 1, end: lastMessageId });
        }
      } else {
        // If rangeToHide is null (invalid input or fully outside), unhide everything
        rangesToUnhide.push({ start: 0, end: lastMessageId });
      }
    } else {
      // --- Keep Recent Logic --- (e.g., "3")
      const keepCount = parseInt(trimmedInput);
      if (isNaN(keepCount) || !Number.isInteger(keepCount) || keepCount < 0) {
        triggerSlash(`/echo severity=error [高楼层总结脚本] 无效的保留层数: "${trimmedInput}"。请输入非负整数。`);
        return;
      }
      console.log(`[高楼层总结脚本] 准备保留最近 ${keepCount} 层消息。`);

      const hideEndIndex = lastMessageId - keepCount;

      if (hideEndIndex >= 0) {
        // Range to hide
        rangeToHide = { start: 0, end: hideEndIndex };
        console.log(`[高楼层总结脚本] 准备隐藏范围: ${rangeToHide.start}-${rangeToHide.end}`);
        // Range to unhide (the ones we keep)
        const unhideStart = hideEndIndex + 1;
        if (unhideStart <= lastMessageId) {
          rangesToUnhide.push({ start: unhideStart, end: lastMessageId });
        }
      } else {
        // Keep count >= total messages, hide nothing, unhide everything
        console.log(
          `[高楼层总结脚本] 需要保留的层数 (${keepCount}) 大于或等于总消息数 (${lastMessageId + 1})，无需隐藏。`,
        );
        rangeToHide = null;
        rangesToUnhide.push({ start: 0, end: lastMessageId });
      }
    }

    // --- Execute Unhide Commands ---
    if (rangesToUnhide.length > 0) {
      triggerSlash(`/echo severity=info [高楼层总结脚本] 正在确保其他楼层可见...`);
      console.log(`[高楼层总结脚本] 准备显式取消隐藏范围:`, rangesToUnhide);
      try {
        for (const range of rangesToUnhide) {
          if (range.end >= range.start) {
            // Ensure range is valid
            await triggerSlash(`/unhide ${range.start}-${range.end}`);
          }
        }
        console.log(`[高楼层总结脚本] 已确保非目标楼层可见。`);
      } catch (unhideError) {
        console.error(`[高楼层总结脚本] 取消隐藏时出错:`, unhideError);
        triggerSlash(`/echo severity=error [高楼层总结脚本] 取消隐藏时出错: ${unhideError.message}`);
        // Continue to hide step anyway
      }
    } else {
      console.log(`[高楼层总结脚本] 没有需要显式取消隐藏的范围。`);
    }

    // --- Execute Hide Command ---
    if (rangeToHide && rangeToHide.end >= rangeToHide.start) {
      triggerSlash(`/echo severity=info [高楼层总结脚本] 正在隐藏目标楼层 ${rangeToHide.start}-${rangeToHide.end}...`);
      console.log(`[高楼层总结脚本] 准备执行隐藏: ${rangeToHide.start}-${rangeToHide.end}`);
      try {
        await triggerSlash(`/hide ${rangeToHide.start}-${rangeToHide.end}`);
        triggerSlash(
          `/echo severity=success [高楼层总结脚本] 已隐藏目标楼层 ${rangeToHide.start}-${rangeToHide.end}，其余楼层已显示。`,
        );
      } catch (hideError) {
        console.error(`[高楼层总结脚本] 隐藏目标楼层时出错:`, hideError);
        triggerSlash(`/echo severity=error [高楼层总结脚本] 隐藏目标楼层时出错: ${hideError.message}`);
      }
    } else {
      console.log(`[高楼层总结脚本] 没有需要隐藏的目标楼层。`);
      triggerSlash(`/echo severity=success [高楼层总结脚本] 操作完成，所有楼层均已显示。`);
    }
  } catch (error) {
    console.error(`[高楼层总结脚本] 手动隐藏/显示楼层时出错:`, error);
    triggerSlash(`/echo severity=error [高楼层总结脚本] 手动隐藏/显示楼层时出错: ${error.message}`);
  }
});

// Main summarization logic - accepts preset and hide mode
const runSummarizationProcess = detail.errorCatched(async (presetName, hideMode) => {
  let originalPreset = null;
  let chatLorebookName = null;
  let hiddenRanges = [];
  let initialLastMessageId = -1;
  let targetMessageId = -1;
  let rangeToKeep = null; // Store the keep range for lore comment
  const summaryTypeName = presetName === SMALL_SUMMARY_PRESET ? '小总结' : '大总结'; // For logs

  triggerSlash(`/echo severity=info [高楼层总结脚本] 开始执行 ${summaryTypeName}...`);
  console.log(`[高楼层总结脚本] 开始执行 ${summaryTypeName} (模式: ${hideMode})...`);

  try {
    // --- Get Initial State ---
    originalPreset = await triggerSlashWithResult('/preset');
    if (!originalPreset) throw new Error('无法获取当前预设。');
    console.log(`[高楼层总结脚本] 当前预设: ${originalPreset}`);

    initialLastMessageId = await getLastMessageId();
    console.log(`[高楼层总结脚本] 当前最后消息 ID: ${initialLastMessageId}`);

    chatLorebookName = await getOrCreateChatLorebook();
    console.log(`[高楼层总结脚本] 聊天世界书名称: ${chatLorebookName}`);

    // --- Hide Messages ---
    hiddenRanges = [];
    if (hideMode === 'range') {
      const rangeInput = await triggerSlashWithResult(
        `/input title="指定保留范围" default="0-${initialLastMessageId}" "请输入需要保留的消息范围 (格式: 开始-结束)："`,
      );
      if (rangeInput === null || rangeInput === undefined || rangeInput === 'false') {
        triggerSlash(`/echo severity=info [高楼层总结脚本] 用户取消操作。`);
        return;
      }
      rangeToKeep = parseRange(rangeInput);
      if (!rangeToKeep) {
        triggerSlash(`/echo severity=error [高楼层总结脚本] 无效的范围格式: "${rangeInput}"。`);
        return;
      }
      console.log(`[高楼层总结脚本] 用户指定保留范围: ${rangeToKeep.start}-${rangeToKeep.end}`);

      // Hide before start
      if (rangeToKeep.start > 0) {
        const rangeToHide = { start: 0, end: rangeToKeep.start - 1 };
        if (rangeToHide.end >= rangeToHide.start) {
          await triggerSlash(`/hide ${rangeToHide.start}-${rangeToHide.end}`);
          hiddenRanges.push(rangeToHide);
        }
      }
      // Hide after end
      if (rangeToKeep.end < initialLastMessageId) {
        const rangeToHide = { start: rangeToKeep.end + 1, end: initialLastMessageId };
        if (rangeToHide.end >= rangeToHide.start) {
          await triggerSlash(`/hide ${rangeToHide.start}-${rangeToHide.end}`);
          hiddenRanges.push(rangeToHide);
        }
      }
      if (hiddenRanges.length > 0) triggerSlash(`/echo severity=info [高楼层总结脚本] 已隐藏范围外的消息。`);
    } else if (hideMode === 'all') {
      console.log(`[高楼层总结脚本] 准备隐藏所有消息 (0-${initialLastMessageId})...`);
      if (initialLastMessageId >= 0) {
        const rangeToHide = { start: 0, end: initialLastMessageId };
        await triggerSlash(`/hide ${rangeToHide.start}-${rangeToHide.end}`);
        hiddenRanges.push(rangeToHide);
        triggerSlash(`/echo severity=info [高楼层总结脚本] 已隐藏所有之前的消息。`);
      } else {
        console.log(`[高楼层总结脚本] 没有消息需要隐藏。`);
      }
    }

    // --- Switch Preset ---
    console.log(`[高楼层总结脚本] 切换到预设: ${presetName}`);
    await triggerSlash(`/preset select "${presetName}"`);
    await new Promise(resolve => setTimeout(resolve, 1500));

    // --- Send Prompt (Conditional based on preset) ---
    let promptText;
    if (presetName === LARGE_SUMMARY_PRESET) {
      promptText = '根据之前的小总结，输出完整的大总结';
    } else {
      // Handles SMALL_SUMMARY_PRESET etc.
      promptText = '开始总结'; // Keep original for others
    }
    console.log(`[高楼层总结脚本] 发送总结提示: \"${promptText}\"`);
    await triggerSlash(`/send ${promptText}`);
    const promptMessageId = initialLastMessageId + 1;

    // --- Trigger AI & Wait ---
    console.log(`[高楼层总结脚本] 触发 AI 回复生成...`);
    let triggerSucceeded = false;
    try {
      const timeoutPromise = new Promise((_, reject) => setTimeout(() => reject(new Error('AI 回复生成超时')), 300000));
      const generationSignalPromise = eventWaitOnce(tavern_events.MESSAGE_RECEIVED).then(() => {
        console.log(`[高楼层总结脚本] 监听到 MESSAGE_RECEIVED 事件信号。`);
        return true;
      });
      await triggerSlash(`/trigger`);
      triggerSucceeded = await Promise.race([generationSignalPromise, timeoutPromise]);
    } catch (genError) {
      console.error(`[高楼层总结脚本] 等待 AI 回复信号时出错或超时:`, genError);
      triggerSucceeded = false;
    }

    // --- Get AI Response ID ---
    const finalLastMessageId = await getLastMessageId();
    console.log(`[高楼层总结脚本] 获取到生成后的最后消息 ID: ${finalLastMessageId}`);
    if (finalLastMessageId > promptMessageId) {
      targetMessageId = finalLastMessageId;
      console.log(`[高楼层总结脚本] 确认 AI 回复消息 ID: ${targetMessageId}`);
    } else {
      if (triggerSucceeded && finalLastMessageId === promptMessageId) {
        console.warn(`[高楼层总结脚本] 最后消息 ID 未增加，可能是 AI 回复为空?`);
        targetMessageId = finalLastMessageId;
      } else {
        throw new Error(
          `未能确认 AI 回复消息 ID (生成后最后 ID: ${finalLastMessageId}, 提示 ID: ${promptMessageId}, 事件触发: ${triggerSucceeded})。`,
        );
      }
    }

    // --- Get Response Content ---
    console.log(`[高楼层总结脚本] 获取生成的回复内容 (ID: ${targetMessageId})...`);
    const messages = await getChatMessages(targetMessageId);
    if (!messages || messages.length === 0) throw new Error(`无法获取消息 ID ${targetMessageId} 的内容。`);
    const responseText = messages[0].message;
    console.log(`[高楼层总结脚本] 成功获取回复内容。`);

    // --- Parse and Confirm --- (Popup title adjusted)
    console.log(`[高楼层总结脚本] 解析回复中的 <summary> 标签...`);
    const summaryMatch = responseText.match(/<summary>([\s\S]*?)<\/summary>/);
    const summaryContent = summaryMatch ? summaryMatch[1].trim() : null;
    let saveConfirmed = false;

    if (summaryContent) {
      console.log(`[高楼层总结脚本] 提取到总结内容，等待用户确认...`);
      const popupContent = summaryContent.replace(/\n/g, '<br>');
      const popupTitle =
        hideMode === 'range' ? `确认范围 ${rangeToKeep.start}-${rangeToKeep.end} 总结结果` : `确认整体总结结果`;
      const userChoice = await triggerSlashWithResult(
        `/popup title="${popupTitle}" okButton="确定" cancelButton="取消" result=true "${popupContent}"`,
      );
      console.log('Popup userChoice raw value:', userChoice);
      if (userChoice === '1') {
        // Check for string '1'
        saveConfirmed = true;
        console.log(`[高楼层总结脚本] 用户确认保存总结。`);
      } else {
        console.log(`[高楼层总结脚本] 用户取消保存总结。`);
      }
    } else {
      console.warn(`[高楼层总结脚本] 回复中未找到有效的 <summary> 标签内容。`);
      triggerSlash(`/echo severity=warning [高楼层总结脚本] 回复中未找到有效的 <summary> 标签内容。`);
    }

    // --- Save Lorebook Entry (if confirmed) --- (Key/Comment adjusted)
    if (saveConfirmed && chatLorebookName) {
      console.log(`[高楼层总结脚本] 正在将总结添加到聊天世界书 (${chatLorebookName})...`);

      // Extract title from summaryContent
      let baseTitle = '总结'; // Default title
      const titleRegex = /^\s*Title:\s*(.+)/im; // Case-insensitive, multiline
      const titleMatch = summaryContent.match(titleRegex);
      if (titleMatch && titleMatch[1]) {
        baseTitle = titleMatch[1].trim();
        console.log(`[高楼层总结脚本] 从总结内容中提取到标题: "${baseTitle}"`);
      } else {
        console.log(`[高楼层总结脚本] 未在总结内容中找到 "Title:" 行，使用默认标题: "${baseTitle}"`);
      }

      // Construct entry key and comment based on hideMode and extracted title
      let entryKey, entryComment;
      const timestamp = new Date().toISOString();
      if (hideMode === 'range' && rangeToKeep) {
        // Ensure rangeToKeep is valid
        entryKey = `${baseTitle} (${rangeToKeep.start}-${rangeToKeep.end}) ${timestamp}`;
        entryComment = `${baseTitle} (${rangeToKeep.start}-${rangeToKeep.end})`;
      } else {
        // Handles 'all' mode or cases where range might be invalid
        entryKey = `${baseTitle} ${timestamp}`;
        entryComment = baseTitle; // For overall summary, just use the title
      }

      const newEntryDefaults = {
        comment: entryComment,
        key: [entryKey],
        content: summaryContent,
        enabled: true,
        type: 'constant',
        position: 'before_prompt',
        order: 0,
      };
      try {
        const newEntryUid = await createLorebookEntry(chatLorebookName, newEntryDefaults);
        if (newEntryUid !== undefined && newEntryUid !== null) {
          console.log(`[高楼层总结脚本] 成功创建世界书条目 (UID: ${newEntryUid})`);
          triggerSlash(`/echo severity=success [高楼层总结脚本] 成功将 ${entryComment} 添加到聊天世界书。`);
        } else {
          throw new Error('创建世界书条目失败，未返回有效 UID。');
        }
      } catch (loreError) {
        console.error(`[高楼层总结脚本] 添加总结到世界书时出错:`, loreError);
        triggerSlash(`/echo severity=error [高楼层总结脚本] 添加总结到世界书时出错: ${loreError.message}`);
      }
    }
  } catch (error) {
    console.error(`[高楼层总结脚本] 执行过程中发生错误:`, error);
    triggerSlash(`/echo severity=error [高楼层总结脚本] 执行失败: ${error.message}`);
  } finally {
    // --- Unhide Messages ---
    if (hiddenRanges.length > 0) {
      console.log(`[高楼层总结脚本] 准备恢复隐藏的消息...`);
      triggerSlash(`/echo severity=info [高楼层总结脚本] 正在恢复隐藏的消息...`);
      try {
        for (const range of hiddenRanges) {
          await triggerSlash(`/unhide ${range.start}-${range.end}`);
        }
        triggerSlash(`/echo severity=info [高楼层总结脚本] 已恢复隐藏的消息。`);
      } catch (unhideError) {
        console.error(`[高楼层总结脚本] 恢复隐藏消息时出错:`, unhideError);
        triggerSlash(`/echo severity=error [高楼层总结脚本] 恢复隐藏消息时出错: ${unhideError.message}`);
      }
    }

    // --- Delete Prompt and Response ---
    const promptMessageIdToDelete = initialLastMessageId + 1;
    const aiMessageIdToDelete = targetMessageId;
    if (promptMessageIdToDelete >= 0 && aiMessageIdToDelete > promptMessageIdToDelete) {
      console.log(`[高楼层总结脚本] 准备删除提示 (${promptMessageIdToDelete}) 和回复 (${aiMessageIdToDelete}) 消息...`);
      triggerSlash(`/echo severity=info [高楼层总结脚本] 正在删除临时消息...`);
      try {
        await triggerSlash(`/cut ${promptMessageIdToDelete}-${aiMessageIdToDelete}`);
        triggerSlash(`/echo severity=info [高楼层总结脚本] 已删除临时消息。`);
      } catch (deleteError) {
        console.error(`[高楼层总结脚本] 删除消息时出错:`, deleteError);
        triggerSlash(`/echo severity=error [高楼层总结脚本] 删除消息时出错: ${deleteError.message}`);
      }
    } else if (promptMessageIdToDelete >= 0 && targetMessageId >= 0) {
      // Check if targetMessageId is valid even if not > prompt ID
      console.warn(
        `[高楼层总结脚本] AI 回复 ID (${aiMessageIdToDelete}) 无效或等于提示ID，尝试仅删除提示消息 (${promptMessageIdToDelete})`,
      );
      triggerSlash(`/echo severity=warning [高楼层总结脚本] 尝试仅删除提示消息...`);
      try {
        await triggerSlash(`/cut ${promptMessageIdToDelete}`);
      } catch (deleteError) {
        console.error(`[高楼层总结脚本] 删除提示消息时出错:`, deleteError);
      }
    } else {
      console.warn(`[高楼层总结脚本] 无法确定要删除的有效消息范围，跳过删除。`);
    }

    // --- Restore Original Preset ---
    if (originalPreset) {
      console.log(`[高楼层总结脚本] 切换回原预设: ${originalPreset}`);
      try {
        await triggerSlash(`/preset select "${originalPreset}"`);
        triggerSlash(`/echo severity=info [高楼层总结脚本] 已切换回原预设: ${originalPreset}。`);
      } catch (switchBackError) {
        console.error('[高楼层总结脚本] 切换回原预设失败:', switchBackError);
        triggerSlash(
          `/echo severity=error [高楼层总结脚本] 切换回原预设 ${originalPreset} 失败: ${switchBackError.message}`,
        );
      }
    } else {
      console.warn('[高楼层总结脚本] 未记录原始预设，无法自动切换。');
    }

    console.log(`[高楼层总结脚本] ${summaryTypeName} 流程结束。`);
    triggerSlash(`/echo severity=info [高楼层总结脚本] ${summaryTypeName} 流程结束。`);
  }
});

