"use strict";
const lorebook_name = '可视化地图导航';
const map_tag = '<MapGraph>';
const map_regex = /<MapGraph>([\s\S]+?)<\/MapGraph>/s;

// 基础配置项
var option;
(function (option_1) {
    const default_option = {
        should_send_directly: true,
        init_delay: 0,
        travel_mode: true, // 始终为写实模式
    };

    option_1.option = {...default_option}; // 初始化为默认值

    async function parse_option() {
        try {
            // 创建一个基于默认选项的新对象
            const result = {...default_option};

            // 获取所有设置条目
            let entries;
            try {
                entries = await getLorebookEntries(lorebook_name);

                // 筛选出带有设置-前缀的条目
                entries = entries.filter(entry => entry.comment && entry.comment.startsWith('设置-'));
            } catch (err) {
                console.error('获取世界书条目失败:', err);
                return result;
            }

            // 处理各个设置项
            let foundSendSetting = false;

            // 处理"开启则直接发送，关闭则填在输入框"
            const sendSettingEntry = entries.find(entry => 
                entry.comment === '设置-开启则直接发送，关闭则填在输入框'
            );

            if (sendSettingEntry) {
                foundSendSetting = true;
                const shouldSendDirectly = sendSettingEntry.enabled === true;
                result.should_send_directly = shouldSendDirectly;
            }

            // 处理"初始化延迟"
            const delayEntry = entries.find(entry => entry.comment === '设置-初始化延迟');
            if (delayEntry) {
                const delay = delayEntry.content && !isNaN(Number(delayEntry.content)) 
                    ? Number(delayEntry.content) 
                    : 0;
                result.init_delay = delay;
            }

            // 移除"移动模式"设置，固定为写实模式
            result.travel_mode = true;

            // 如果没找到直接发送设置，明确记录
            if (!foundSendSetting) {
                console.warn('未找到"开启则直接发送，关闭则填在输入框"设置，使用默认值:', result.should_send_directly);
            }

            return result;
        } catch (error) {
            console.error("解析设置出错:", error);
            return {...default_option};
        }
    }

    async function update() {
        try {
            const old_option = option_1.option;
            option_1.option = await parse_option();
            return !_.isEqual(option_1.option, old_option);
        } catch (error) {
            console.error("更新设置出错:", error);
            if (!option_1.option) {
                option_1.option = {...default_option};
            }
            return false;
        }
    }

    option_1.update = update;
})(option || (option = {}));

// 渲染管理模块
var render;
(function (render_1) {
    let style = null;
    let selectedLocation = null;

    // 更新样式
    async function extract_style() {
        const entries = await getLorebookEntries(lorebook_name, { filter: { comment: '样式-' } }).then(entries => entries.filter(entry => entry.enabled));
        if (entries.length === 0) {
            return default_style;
        }
        return entries[0].content;
    }

    async function update() {
        const old_style = style;
        style = await extract_style();
        return old_style !== style;
    }
    render_1.update = update;

    // 地点点击处理
    function locationClick($container, mapData, nodeId) {
        if ($container.parents('.last_mes').length > 0) {
            const targetNode = mapData.nodes.find(node => node.id === nodeId);
            if (!targetNode) return;

            const currentLocationId = mapData.currentLocation;
            if (!currentLocationId) {
                const now = Date.now();
                if (now - lastCurrentLocationWarningTime > warningThrottle) {
                    lastCurrentLocationWarningTime = now;
                }
                $container.find('.selected_location_display').html('<i class="fa-solid fa-triangle-exclamation"></i> 无法确定当前位置');
                return;
            }

            let isReachable = false;

            if (mapData.free_movement === "N") {
                $container.find('.selected_location_display').html('<i class="fa-solid fa-ban"></i> 当前无法移动');
                return;
            }

            if (nodeId === currentLocationId) {
                isReachable = true;
            } 
            else if (mapData.free_movement === "F") {
                isReachable = true;
            } 
            else {
                const connectingEdges = mapData.edges.filter(edge => 
                    ((edge.from === currentLocationId && edge.to === nodeId) ||
                     (edge.to === currentLocationId && edge.from === nodeId))
                );

                isReachable = connectingEdges.length > 0;
            }

            if (!isReachable) {
                if (mapData.free_movement === "F") {
                    $container.find('.selected_location_display').html('<i class="fa-solid fa-ban"></i> 无法前往该地点');
                } else {
                    $container.find('.selected_location_display').html('<i class="fa-solid fa-ban"></i> 只能前往相邻地点');
                }
                return;
            }

            const connectingEdges = mapData.edges.filter(edge => 
                ((edge.from === currentLocationId && edge.to === nodeId) ||
                    (edge.to === currentLocationId && edge.from === nodeId))
            );

            const allPathsLocked = isReachable && mapData.free_movement !== "F" && 
                connectingEdges.length > 0 && 
                connectingEdges.every(edge => isLockedEdgeType(edge.type));

            selectedLocation = nodeId;

            let hasLockedPath = false;
            let hasOpenPath = false;

            if (mapData.currentLocation) {
                hasLockedPath = connectingEdges.some(edge => isLockedEdgeType(edge.type));
                hasOpenPath = connectingEdges.some(edge => !isLockedEdgeType(edge.type));
            }

            let displayText = `<i class="fa-solid fa-map-pin"></i> 已选择: ${targetNode.label}`;

            if (allPathsLocked) {
                let lockType = 'l';

                for (const edge of connectingEdges) {
                    if (isLockedEdgeType(edge.type)) {
                        lockType = edge.type;
                        break;
                    }
                }

                switch(lockType) {
                    case 'ld':
                        displayText += ` <i class="fa-solid fa-lock"></i> (门已锁住)`;
                        break;
                    case 'lg':
                        displayText += ` <i class="fa-solid fa-user-shield"></i> (有人守卫)`;
                        break;
                    case 'lb':
                        displayText += ` <i class="fa-solid fa-ban"></i> (通道被阻挡)`;
                        break;
                    case 'lh':
                        displayText += ` <i class="fa-solid fa-eye-slash"></i> (入口隐藏)`;
                        break;
                    default:
                        displayText += ` <i class="fa-solid fa-lock"></i> (无法到达)`;
                }
            } else if (hasLockedPath && hasOpenPath) {
                displayText += ` <i class="fa-solid fa-info-circle"></i> (部分路径无法通行)`;
            }

            $container.find('.selected_location_display').html(displayText);
            $container.find('.go_button').removeClass('disabled');
        }
    }

    // 前往点击处理
    function goToLocation($button, mapData) {
        if (!selectedLocation) return;

        // 查找选中的地点
        const selectedNode = mapData.nodes.find(node => node.id === selectedLocation);
        if (!selectedNode) return;

        // 查找当前位置
        const currentNode = mapData.nodes.find(node => node.id === mapData.currentLocation);
        if (!currentNode) {
            // 控制警告消息频率，防止刷屏
            const now = Date.now();
            if (now - lastCurrentLocationWarningTime > warningThrottle) {
                console.warn('无法确定当前位置');
                lastCurrentLocationWarningTime = now;
            }
            return;
        }

        // 获取连接当前位置和选定位置的边
        const connectingEdges = mapData.edges.filter(edge => 
            ((edge.from === mapData.currentLocation && edge.to === selectedNode.id) ||
            (edge.to === mapData.currentLocation && edge.from === selectedNode.id))
        );

        // 构建移动指令
        let moveCommand = "";

        // 检查从当前位置到选定位置的路径状态
        let pathStatus = "open"; // 默认为开放路径
        let lockType = null; // 锁定类型

        if (mapData.currentLocation && connectingEdges.length > 0) {
            const hasLockedPath = connectingEdges.some(edge => isLockedEdgeType(edge.type));
            const hasOpenPath = connectingEdges.some(edge => !isLockedEdgeType(edge.type));

            if (hasLockedPath && !hasOpenPath) {
                pathStatus = "locked";
                // 找出第一个锁定的边类型
                for (const edge of connectingEdges) {
                    if (isLockedEdgeType(edge.type)) {
                        lockType = edge.type;
                        break;
                    }
                }
            } else if (hasLockedPath && hasOpenPath) {
                pathStatus = "partially_locked";
            }
        }

        // 检查是否为出口节点
        if (selectedNode.type === 'e' && pathStatus !== "locked") {
            // 创建出口选择弹窗
