                $modal.remove();
            });

            // 第一步选择处理
            $modal.find('.map-exit-option').on('click', function() {
                const action = $(this).data('action');

                if (action === 'stay') {
                    // 停留，与普通移动指令一样
                    if (mapData.free_movement === "F") {
                        // 自由移动模式下生成路径
                        const path = findShortestPath(mapData, mapData.currentLocation, selectedNode.id);

                        if (path && path.length > 1) {
                            const middleNodesText = path.map(nodeId => getNodeLabel(mapData, nodeId)).join('、');
                            moveCommand = `<request:{{user}} 从 ${currentNode.label} 经过${middleNodesText}到达 ${selectedNode.label}`;
                        } else if (path && path.length === 1) {
                            moveCommand = `<request:{{user}} 从 ${currentNode.label} 到达 ${selectedNode.label}`;
                        } else {
                            moveCommand = `<request:{{user}} 从 ${currentNode.label} 找路前往 ${selectedNode.label}`;
                        }
                    } else {
                        moveCommand = `<request:{{user}} 从 ${currentNode.label} 到达 ${selectedNode.label}`;
                    }

                    if (selectedNode.characters && selectedNode.characters.length > 0) {
                        moveCommand += `,${selectedNode.label} has:${selectedNode.characters.join(',')}`;
                    }

                    moveCommand += '>';

                    // 发送指令
                    const shouldSendDirectly = option.option?.should_send_directly !== false;
                    if (shouldSendDirectly) {
                        triggerSlash(`/send ${moveCommand} || /trigger`);
                    } else {
                        triggerSlash(`/setinput ${moveCommand}`);
                    }

                    $modal.remove();
                } else if (action === 'leave') {
                    // 显示目的地输入
                    $modal.find('.map-exit-modal-options').hide();
                    $modal.find('.map-exit-destination-input').show();
                    $modal.find('.map-exit-destination').focus();
                }
            });

            // 目的地输入处理
            $modal.find('.map-exit-input-option').on('click', function() {
                const action = $(this).data('action');

                if (action === 'go') {
                    // 前往指定目的地
                    const destination = $modal.find('.map-exit-destination').val().trim();
                    if (destination) {
                        moveCommand = `<request:{{user}}到达${selectedNode.label}并离开此地,到达${destination}展开新剧情>`;
                    } else {
                        // 如果没有输入目的地，默认为直接离开
                        moveCommand = `<request:{{user}}到达${selectedNode.label}并离开此地,到达下个全新场景展开新剧情,接下来剧情跟地点根据上下文进行合理推测>`;
                    }
                } else if (action === 'just-leave') {
                    // 直接离开
                    moveCommand = `<request:{{user}}到达${selectedNode.label}并离开此地,到达下个场景展开新剧情,接下来剧情跟地点根据上下文进行合理推测>`;
                }

                // 发送指令
                const shouldSendDirectly = option.option?.should_send_directly !== false;
                if (shouldSendDirectly) {
                    triggerSlash(`/send ${moveCommand} || /trigger`);
                } else {
                    triggerSlash(`/setinput ${moveCommand}`);
                }

                $modal.remove();
            });

            // 回车键直接确认输入
            $modal.find('.map-exit-destination').on('keypress', function(e) {
                if (e.which === 13) {
                    $modal.find('.map-exit-input-option[data-action="go"]').click();
                }
            });

            return; // 不执行后续的普通移动逻辑
        }

        // 根据路径状态生成移动指令
        if (pathStatus === "locked" && lockType) {
            // 根据锁定类型生成特定的移动指令
            switch (lockType) {
                case 'ld': // 门锁
                    moveCommand = `<request:{{user}} 从 ${currentNode.label} 尝试前往${selectedNode.label}，但门被锁住了>`;
                    break;
                case 'lg': // 守卫
                    moveCommand = `<request:{{user}} 从 ${currentNode.label} 想要进入${selectedNode.label}，但被拦住了>`;
                    break;
                case 'lb': // 障碍物
                    moveCommand = `<request:{{user}} 从 ${currentNode.label} 尝试前往${selectedNode.label}，但通道被障碍物堵住了>`;
                    break;
                case 'lh': // 隐藏入口
                    moveCommand = `<request:{{user}} 从 ${currentNode.label} 在${selectedNode.label}附近寻找隐藏的入口>`;
                    break;
                case 'l': // 基础锁定
                default:
                    moveCommand = `<request:{{user}} 从 ${currentNode.label} 尝试进入${selectedNode.label}，但通道被封锁了>`;
            }
        } else {
            // 自由移动模式
            if (mapData.free_movement === "F") {
                // 计算最短路径
                const path = findShortestPath(mapData, mapData.currentLocation, selectedNode.id);

                // 如果存在路径，且路径长度大于1（需要经过中间节点）
                if (path && path.length > 1) {
                    // 获取中间节点的标签
                    const middleNodesText = path.map(nodeId => getNodeLabel(mapData, nodeId)).join('、');
                    moveCommand = `<request:{{user}} 从 ${currentNode.label} 经过${middleNodesText}到达 ${selectedNode.label}`;
                } else if (path && path.length === 1) {
                    // 直接相邻的节点
                    moveCommand = `<request:{{user}} 从 ${currentNode.label} 到达 ${selectedNode.label}`;
                } else {
                    // 没有找到路径（一般不会发生，因为我们在F模式下）
                    moveCommand = `<request:{{user}} 从 ${currentNode.label} 找路前往 ${selectedNode.label}`;
                }
            } else {
                // 默认导航指令（临近移动模式）
                moveCommand = `<request:{{user}} 从 ${currentNode.label} 到达 ${selectedNode.label},假设目的地是建筑物,则进去并展开新剧情`;
            }

            // 如果有角色，添加角色信息
            if (selectedNode.characters && selectedNode.characters.length > 0) {
                moveCommand += `,${selectedNode.label} has:${selectedNode.characters.join(',')}`;
            }

            moveCommand += '>';
        }

        // 确保从option.option获取should_send_directly值，如果undefined则使用默认值true
        const shouldSendDirectly = option.option?.should_send_directly !== false;

        // 根据设置决定发送方式
        if (shouldSendDirectly) {
            triggerSlash(`/send ${moveCommand} || /trigger`);
        } else {
            triggerSlash(`/setinput ${moveCommand}`);
        }
    }

    // 创建地图元素
