// 在parseMapData函数后，添加最短路径算法
function findShortestPath(mapData, startNodeId, endNodeId) {
    if (startNodeId === endNodeId) {
        return [];
    }

    // 创建节点图
    const graph = {};
    mapData.nodes.forEach(node => {
        graph[node.id] = [];
    });

    // 添加所有非锁定边
    mapData.edges.forEach(edge => {
        if (!isLockedEdgeType(edge.type)) {
            graph[edge.from].push(edge.to);
            graph[edge.to].push(edge.from);
        }
    });

    // 广度优先搜索
    const queue = [startNodeId];
    const visited = { [startNodeId]: true };
    const parent = {};
    let found = false;

    while (queue.length > 0 && !found) {
        const current = queue.shift();

        if (current === endNodeId) {
            found = true;
            break;
        }

        for (const neighbor of graph[current]) {
            if (!visited[neighbor]) {
                visited[neighbor] = true;
                parent[neighbor] = current;
                queue.push(neighbor);
            }
        }
    }

    if (!found) {
        return null;
    }

    // 构建路径
    const path = [];
    let current = endNodeId;

    while (current !== startNodeId) {
        path.unshift(current);
        current = parent[current];
    }

    return path;
}

// 获取节点标签
function getNodeLabel(mapData, nodeId) {
    const node = mapData.nodes.find(n => n.id === nodeId);
    return node ? node.label : nodeId;
}

// 全局工具函数：判断节点是否为锁定类型
function isLockedNodeType(type) {
    return type === 'l' || (type?.startsWith('l') && type !== 'e');
}

// 全局工具函数：获取节点锁定类型的友好名称
function getNodeLockStatusText(type) {
    if (!type || !isLockedNodeType(type)) return '';

    switch (type) {
        case 'ld': return '门已锁住';
        case 'lg': return '有人守卫';
        case 'lb': return '通道被阻挡';
        case 'lh': return '入口隐藏';
        default: return '已锁定';
    }
}

// 全局工具函数：获取节点锁定类型的图标代码
function getNodeLockIconCode(type) {
    if (!type || !isLockedNodeType(type)) return '';

    switch (type) {
        case 'ld': return '\uf023'; // 锁
        case 'lg': return '\uf3ed'; // 用户盾牌
        case 'lb': return '\uf05e'; // 禁止图标
        case 'lh': return '\uf070'; // 隐藏图标
        default: return '\uf023'; // 默认锁图标
    }
}

// 判断路径是否为锁定类型
function isLockedEdgeType(type) {
    return type === 'l' || type?.startsWith('l');
}

// 获取路径锁定类型的友好名称
function getEdgeLockStatusText(type) {
    if (!type || !isLockedEdgeType(type)) return '';

    switch (type) {
        case 'ld': return '门已锁住';
        case 'lg': return '有人守卫';
        case 'lb': return '通道被阻挡';
        case 'lh': return '入口隐藏';
        default: return '已锁定';
    }
}

// 获取路径锁定类型的图标代码
function getEdgeLockIconCode(type) {
    if (!type || !isLockedEdgeType(type)) return '';

    switch (type) {
        case 'ld': return '\uf023'; // 锁
        case 'lg': return '\uf3ed'; // 用户盾牌
        case 'lb': return '\uf05e'; // 禁止图标
        case 'lh': return '\uf070'; // 隐藏图标
        default: return '\uf023'; // 默认锁图标
    }
}

// 添加防止控制台刷屏的变量
let lastCurrentLocationWarningTime = 0;
const warningThrottle = 5000;

// 简化日志控制变量
let debugModeEnabled = false;
let lastInteractionLogTime = 0;
const interactionLogThrottle = 1000;

// 封装日志函数以便控制输出
function debugLog(...args) {
    if (debugModeEnabled) {
        console.log(...args);
    }
}

// 检查节点是否为出口类型
function isExitNodeType(type) {
    return type === 'e';
}

// 获取出口节点的图标代码
function getNodeExitIconCode(type) {
    if (!type || !isExitNodeType(type)) return '';
    return '\uf52b'; // 出口图标 (门口箭头)
}

function get_styles() {
    return getTavernRegexes()
        .filter(regex => regex.enabled && regex.script_name.includes('样式-'))
        .map(regex => ({
        title: regex.script_name.replace('样式-', '').replaceAll(/【.+?】/gs, ''),
        code: regex.replace_string,
    }));
}
function extract_style_node(style) {
    return $('<style>').attr('id', `script_custom_style-${style.title}`).text(style.code);
}
function reappend_styles(nodes) {
    const head = $('head', window.parent.document);
    head.find('#script_custom_style').remove();
    head.append(nodes);
}
$(async () => {
    const styles = await get_styles();
    const style_nodes = styles.map(extract_style_node);
    reappend_styles($('<div>').attr('id', 'script_custom_style').append(style_nodes));
});
export {};

