    // Token分布饼图 - 使用HTML/CSS代替Chart.js
    function renderTokenPieChart(stats) {
        // 计算总的世界书Token和百分比 (基于启用的条目)
        const lorebookTotal = stats.totalConstantTokens + stats.totalSelectiveTokens + stats.totalVectorizedTokens;
        if (lorebookTotal === 0) return '<div style="text-align: center; padding: 40px; color: var(--text-secondary);">(无启用的世界书Token数据)</div>';

        const constPercent = ((stats.totalConstantTokens / lorebookTotal) * 100).toFixed(1);
        const selectPercent = ((stats.totalSelectiveTokens / lorebookTotal) * 100).toFixed(1);
        const vectPercent = ((stats.totalVectorizedTokens / lorebookTotal) * 100).toFixed(1);

        // 构建CSS conic-gradient
        let gradientString = 'conic-gradient(';
        let currentPercent = 0;

        if (parseFloat(constPercent) > 0) {
            gradientString += `${COLOR_CONSTANT} 0% ${constPercent}%`;
            currentPercent = parseFloat(constPercent);
        }

        if (parseFloat(selectPercent) > 0) {
            if (currentPercent > 0) gradientString += ', ';
            gradientString += `${COLOR_SELECTIVE} ${currentPercent}% ${currentPercent + parseFloat(selectPercent)}%`;
            currentPercent += parseFloat(selectPercent);
        }

        if (parseFloat(vectPercent) > 0) {
            if (currentPercent > 0) gradientString += ', ';
            gradientString += `${COLOR_VECTORIZED} ${currentPercent}% 100%`;
        }

        gradientString += ')';

        return `
        <div style="display: flex; align-items: center; justify-content: center; height: 100%;">
            <div id="token-pie-chart" class="token-pie-chart" style="width: 130px; height: 130px; border-radius: 50%; background: ${gradientString}; border: 1px solid var(--border-color); position: relative; cursor: pointer;"
                data-constant="${stats.totalConstantTokens}"
                data-selective="${stats.totalSelectiveTokens}"
                data-vectorized="${stats.totalVectorizedTokens}"
                data-const-percent="${constPercent}"
                data-select-percent="${selectPercent}"
                data-vect-percent="${vectPercent}">
                <!-- 悬停tooltip -->
                <div class="pie-tooltip" style="position: absolute; top: -40px; left: 50%; transform: translateX(-50%); background: rgba(0,0,0,0.8); color: white; padding: 5px 10px; border-radius: 4px; font-size: 0.85rem; opacity: 0; transition: opacity 0.3s; pointer-events: none; white-space: nowrap; z-index: 10;">
                    点击查看详细分布
                </div>
            </div>
            <div style="margin-left: 20px;">
                <div style="display: flex; align-items: center; margin-bottom: 8px;">
                    <div style="width: 12px; height: 12px; background-color: ${COLOR_CONSTANT}; margin-right: 8px; border-radius: 2px;"></div>
                    <span>蓝灯: ${constPercent}%</span>
                </div>
                <div style="display: flex; align-items: center; margin-bottom: 8px;">
                    <div style="width: 12px; height: 12px; background-color: ${COLOR_SELECTIVE}; margin-right: 8px; border-radius: 2px;"></div>
                    <span>绿灯: ${selectPercent}%</span>
                </div>
                <div style="display: flex; align-items: center;">
                    <div style="width: 12px; height: 12px; background-color: ${COLOR_VECTORIZED}; margin-right: 8px; border-radius: 2px;"></div>
                    <span>向量: ${vectPercent}%</span>
                </div>
            </div>
        </div>
        `;
    }

    // 来源分布条形图 - 使用HTML/CSS代替Chart.js
    function renderSourceBarChart(stats) {
        const sourceData = [
            { name: '主书', key: 'primary', color: STYLE_SOURCE_PRIMARY },
            { name: '全局', key: 'global', color: STYLE_SOURCE_GLOBAL },
            { name: '聊天', key: 'chat', color: STYLE_SOURCE_CHAT },
            { name: '附加', key: 'additional', color: STYLE_SOURCE_ADDITIONAL }
        ];

        // 找出最大值以便缩放 (基于启用的条目)
        let maxValue = 0;
        sourceData.forEach(source => {
            const data = stats.bySource[source.key];
            const total = data.c + data.s + data.v;
            if (total > maxValue) maxValue = total;
        });

        if (maxValue === 0) return '<div style="text-align: center; padding: 40px; color: var(--text-secondary);">(无来源数据)</div>';

        let barsHtml = '';

        sourceData.forEach(source => {
            const data = stats.bySource[source.key];
            const totalSourceTokens = data.c + data.s + data.v;
            // 计算高度，确保有值时至少有最小高度
            const minHeightPercent = 5; // 最小高度百分比
            const constHeight = data.c > 0 ? Math.max(minHeightPercent, (data.c / maxValue) * 100) : 0;
            const selectHeight = data.s > 0 ? Math.max(minHeightPercent, (data.s / maxValue) * 100) : 0;
            const vectHeight = data.v > 0 ? Math.max(minHeightPercent, (data.v / maxValue) * 100) : 0;

            // 防止堆叠高度超过100% (虽然理论上不会，但保险起见)
            const totalHeight = constHeight + selectHeight + vectHeight;
            const scaleFactor = totalHeight > 100 ? 100 / totalHeight : 1;

            const scaledConstHeight = constHeight * scaleFactor;
            const scaledSelectHeight = selectHeight * scaleFactor;
            const scaledVectHeight = vectHeight * scaleFactor;


            barsHtml += `
            <div class="source-bar-group" style="display: flex; flex-direction: column; align-items: center; flex: 1; cursor: default;" title="${source.name}: ${totalSourceTokens.toLocaleString()} Tokens (蓝:${data.c}, 绿:${data.s}, 黄:${data.v})">
                <div style="height: 160px; width: 50px; position: relative; margin-bottom: 10px; background-color: rgba(255,255,255,0.05); border-radius: 3px; overflow: hidden;">
                    <!-- 使用绝对定位，从底部向上堆叠 -->
                    ${data.c > 0 ? `<div class="source-bar" style="position: absolute; bottom: 0; left: 0; height: ${scaledConstHeight}%; width: 100%; background-color: ${COLOR_CONSTANT}; animation: growUp 1s forwards cubic-bezier(0.34, 1.56, 0.64, 1);" title="蓝灯: ${data.c.toLocaleString()}"></div>` : ''}
                    ${data.s > 0 ? `<div class="source-bar" style="position: absolute; bottom: ${scaledConstHeight}%; left: 0; height: ${scaledSelectHeight}%; width: 100%; background-color: ${COLOR_SELECTIVE}; animation: growUp 1s forwards cubic-bezier(0.34, 1.56, 0.64, 1);" title="绿灯: ${data.s.toLocaleString()}"></div>` : ''}
                    ${data.v > 0 ? `<div class="source-bar" style="position: absolute; bottom: ${scaledConstHeight + scaledSelectHeight}%; left: 0; height: ${scaledVectHeight}%; width: 100%; background-color: ${COLOR_VECTORIZED}; animation: growUp 1s forwards cubic-bezier(0.34, 1.56, 0.64, 1);" title="向量: ${data.v.toLocaleString()}"></div>` : ''}
                </div>
                <div class="source-bar-label" style="font-size: 0.9rem; ${source.color} font-weight: bold;">${source.name}</div>
                <div class="source-bar-value" style="font-size: 0.8rem; color: #aaa;">${totalSourceTokens.toLocaleString()}</div>
            </div>
            `;
        });

        return `
        <div style="display: flex; justify-content: space-around; align-items: flex-end; height: 100%;">
            ${barsHtml}
        </div>
        <div style="display: flex; justify-content: center; margin-top: 10px; gap: 15px;">
            <div style="display: flex; align-items: center;">
                <div style="width: 12px; height: 12px; background-color: ${COLOR_CONSTANT}; margin-right: 5px; border-radius: 2px;"></div>
                <span style="font-size: 0.8rem;">蓝灯</span>
            </div>
            <div style="display: flex; align-items: center;">
                <div style="width: 12px; height: 12px; background-color: ${COLOR_SELECTIVE}; margin-right: 5px; border-radius: 2px;"></div>
                <span style="font-size: 0.8rem;">绿灯</span>
            </div>
            <div style="display: flex; align-items: center;">
                <div style="width: 12px; height: 12px; background-color: ${COLOR_VECTORIZED}; margin-right: 5px; border-radius: 2px;"></div>
                <span style="font-size: 0.8rem;">向量</span>
            </div>
        </div>
        `;
    }

    // 渲染来源项目
    function renderSourceItems(stats) {
        const sources = [
            { key: 'primary', name: '主书', colorClass: 'primary' },
            { key: 'additional', name: '附加', colorClass: 'additional' },
            { key: 'global', name: '全局', colorClass: 'global' },
            { key: 'chat', name: '聊天', colorClass: 'chat' }
        ];

        let hasData = false;
        let html = '';

        sources.forEach(source => {
            const data = stats.bySource[source.key];
            const sourceTotal = data.c + data.s + data.v; // 基于统计

            if (sourceTotal > 0 || data.books.length > 0) { // 只要有关联的书或有token就显示
                hasData = true;
                const booksDisplay = data.books.length > 0 ?
                    data.books.join(', ') : '(无)'; // 显示关联的书名
                const truncatedDisplay = booksDisplay.length > 60 ?
                    booksDisplay.substring(0, 57) + '...' : booksDisplay;

                html += `
                <div class="source-item ${source.colorClass}">
                    <div class="source-header">
                        <span class="source-name">${source.name}</span>
                        <span class="source-total">总计: ${sourceTotal.toLocaleString()}</span>
                    </div>
                    <span class="source-books" title="${escapeHtml(booksDisplay)}">包含世界书: ${escapeHtml(truncatedDisplay)}</span>
                    <div class="source-breakdown">
                        <div class="source-breakdown-item" style="background-color: rgba(75, 192, 192, 0.1); color: ${COLOR_CONSTANT};">
                            <div>蓝灯</div>
                            <div style="font-weight: bold;">${data.c.toLocaleString()}</div>
                        </div>
                        <div class="source-breakdown-item" style="background-color: rgba(144, 238, 144, 0.1); color: ${COLOR_SELECTIVE};">
                            <div>绿灯</div>
                            <div style="font-weight: bold;">${data.s.toLocaleString()}</div>
                        </div>
                        <div class="source-breakdown-item" style="background-color: rgba(255, 206, 86, 0.1); color: ${COLOR_VECTORIZED};">
                            <div>向量</div>
                            <div style="font-weight: bold;">${data.v.toLocaleString()}</div>
                        </div>
                    </div>
                </div>
                `;
            }
        });

        if (!hasData) {
            return '<p style="text-align: center; color: var(--text-secondary);">无激活的世界书来源</p>';
        }

        return html;
    }

    // 渲染世界书表格行 - 移除详情按钮列
    function renderLorebooksRows(stats) {
        const sourceTextMap = {
            primary: '主',
            additional: '附',
            global: '全',
            chat: '聊'
        };

        const sourceColorMap = {
            primary: STYLE_SOURCE_PRIMARY,
            additional: STYLE_SOURCE_ADDITIONAL,
            global: STYLE_SOURCE_GLOBAL,
            chat: STYLE_SOURCE_CHAT
        };

        if (!stats.byLorebook || Object.keys(stats.byLorebook).length === 0) {
            return '<tr><td colspan="6" style="text-align: center; color: var(--text-secondary);">无激活的世界书</td></tr>';
        }

        // 按总启用Token数排序
        const lorebooks = Object.entries(stats.byLorebook).sort(([,a], [,b]) => b.t - a.t);
        let html = '';

        lorebooks.forEach(([name, data]) => {
            const sourceText = sourceTextMap[data.src] || data.src;
            const sourceStyle = sourceColorMap[data.src] || ''; // 获取颜色样式

            const displayName = name.length > 30 ? name.substring(0, 27) + '...' : name;

            html += `
            <tr class="lorebook-row" data-lorebook="${escapeHtml(name)}" style="cursor: pointer;" title="双击查看 '${escapeHtml(name)}' 的详细条目">
                <td title="${escapeHtml(name)}">${escapeHtml(displayName)}</td>
                <td style="${sourceStyle} font-weight: bold;" title="${sourceTextMap[data.src] || data.src}">${sourceText}</td>
                <td style="${STYLE_CONSTANT_TEXT}">${data.c.toLocaleString()}</td>
                <td style="${STYLE_SELECTIVE_TEXT}">${data.s.toLocaleString()}</td>
                <td style="${STYLE_VECTORIZED_TEXT}">${data.v.toLocaleString()}</td>
                <td style="font-weight: bold;">${data.t.toLocaleString()}</td>
                <!-- <td>
                    <button class="btn btn-outline view-entries-button" data-lorebook="${escapeHtml(name)}" style="padding: 2px 6px; font-size: 0.8em;">详情</button>
                </td> --> <!-- 移除详情按钮列 -->
            </tr>
            `;
        });

        return html;
    }

    // [修改] 条目详情HTML表格生成函数 (实现所有要求)
    function renderEntryDetailsHtml(allEntries, lorebookName) {
        logDebug(`renderEntryDetailsHtml: 开始为 '${lorebookName}' 渲染 ${allEntries.length} 个条目详情`);
        if (!allEntries || allEntries.length === 0) {
            return '<div style="text-align: center; padding: 20px; color: var(--text-secondary);">(该世界书没有条目)</div>';
        }

        // 1. 分离和排序条目 (要求 2)
        const enabledEntries = allEntries.filter(entry => entry.enabled);
        const disabledEntries = allEntries.filter(entry => !entry.enabled);

        // 按 Tokens 降序排序
        const sortByTokensDesc = (a, b) => getEntryTokens(b) - getEntryTokens(a);
        enabledEntries.sort(sortByTokensDesc);
        disabledEntries.sort(sortByTokensDesc);

        const sortedEntries = [...enabledEntries, ...disabledEntries];

        // 2. 计算页脚统计数据 (要求 1)
        let totalCount = allEntries.length;
        let enabledCount = enabledEntries.length;
        let disabledCount = disabledEntries.length;
        let totalEnabledTokens = 0;
        let enabledConstantCount = 0;
        let enabledSelectiveCount = 0;
        let enabledVectorizedCount = 0;

        enabledEntries.forEach(entry => {
            const entryTokens = getEntryTokens(entry); // 使用辅助函数
            totalEnabledTokens += entryTokens;
            if (entry.type === 'constant') enabledConstantCount++;
            else if (entry.type === 'selective') enabledSelectiveCount++;
            else if (entry.type === 'vectorized') enabledVectorizedCount++;
        });

        // 3. 构建表格 HTML (要求 3)
        let tableHtml = `
        <div class="entry-list-container">
            <table class="entry-list-table">
                <thead>
                    <tr>
                        <th>条目名称</th>
                        <th>启用</th>
                        <th>类型</th>
                        <th>插入位置</th>
                        <th>顺序</th>
                        <th>扫描深度</th>
                        <th>Tokens</th>
                    </tr>
                </thead>
                <tbody>
        `;

        sortedEntries.forEach(entry => {
            let typeText, typeStyle;
            if (entry.type === 'constant') {
                typeText = '蓝灯'; typeStyle = STYLE_CONSTANT_TEXT;
            } else if (entry.type === 'selective') {
                typeText = '绿灯'; typeStyle = STYLE_SELECTIVE_TEXT;
            } else if (entry.type === 'vectorized') {
                typeText = '向量'; typeStyle = STYLE_VECTORIZED_TEXT;
            } else {
                typeText = '未知'; typeStyle = '';
            }

            const entryTokens = getEntryTokens(entry);
            const displayName = entry.comment || `(UID: ${entry.uid})`;
            const truncatedName = displayName.length > 35 ? displayName.substring(0, 32) + '...' : displayName;
            const isEnabled = entry.enabled;
            const rowStyle = isEnabled ? '' : 'opacity: 0.6; font-style: italic;'; // 给禁用行添加样式

            tableHtml += `
                <tr style="${rowStyle}">
                    <td class="entry-comment" title="${escapeHtml(displayName)} (UID: ${entry.uid})">${escapeHtml(truncatedName)}</td>
                    <td style="text-align: center;">${isEnabled ? '<span style="color: #90EE90;">是</span>' : '<span style="color: #FF4500;">否</span>'}</td>
                    <td style="${typeStyle}">${typeText}</td>
                    <td title="${escapeHtml(entry.position)}">${escapeHtml(formatPosition(entry.position, entry.depth))}</td>
                    <td style="text-align: right;">${entry.order ?? '-'}</td>
                    <td style="text-align: right;">${formatScanDepth(entry.scan_depth)}</td>
                    <td style="text-align: right;">${entryTokens.toLocaleString()}</td>
                </tr>
            `;
        });

        tableHtml += `
                </tbody>
            </table>
        </div>
        `;

        // 4. 构建页脚 HTML (要求 1)
        const footerHtml = `
        <div style="text-align: center; margin-top: 15px; padding-top: 10px; border-top: 1px solid var(--border-color); display: flex; justify-content: space-around; flex-wrap: wrap; gap: 5px 15px; font-size: 0.85em; color: var(--text-secondary);">
