    // 打开AI编辑界面
    async function openAiEditInterface() {
        try {
            logDebug(`openAiEditInterface: 尝试打开编辑界面，当前世界书: ${currentViewingLorebook}`);

            if (!currentViewingLorebook) {
                logDebug("错误: currentViewingLorebook未定义");
                parentToastr?.error('无法识别当前世界书');
                return;
            }

            // 获取最新的完整条目数据用于编辑界面显示
            let currentEntriesForEdit;
            try {
                currentEntriesForEdit = await TavernHelper.getLorebookEntries(currentViewingLorebook);
            } catch (fetchError) {
                 logDebug(`获取 '${currentViewingLorebook}' 条目失败:`, fetchError);
                 parentToastr?.error(`获取世界书 "${currentViewingLorebook}" 的条目数据失败`);
                 return;
            }

            const lorebookName = currentViewingLorebook;
            logDebug(`加载世界书数据: ${lorebookName}, 条目数: ${currentEntriesForEdit.length}`);

            // 创建编辑界面HTML
            const editInterfaceHTML = `
            <div class="ai-edit-container" style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px;">
                <div class="edit-input-area">
                    <h3>编辑指令</h3>
                    <textarea id="ai-edit-input" rows="4" placeholder="例如：'将所有蓝灯条目改为绿灯并添加关键词'或'优化ID为3的条目内容使其更简洁'"
                        class="form-control" style="width: 100%; margin-bottom: 15px;"></textarea>
                    <button id="generate-edits-button" class="btn btn-primary">生成编辑建议</button>

                    <div class="current-entries" style="margin-top: 20px; max-height: 400px; overflow-y: auto;">
                        <h4>当前条目 (${currentEntriesForEdit.length})</h4>
                        <div class="edit-entries-list">
                            ${currentEntriesForEdit.map(entry => {
                                const entryTokens = getEntryTokens(entry); // 使用辅助函数
                                return `
                                <div class="entry-item" style="margin-bottom: 5px; padding: 5px; border-bottom: 1px solid var(--border-color-subtle);">
                                    <div><strong>${escapeHtml(entry.comment || `UID: ${entry.uid}`)}</strong> (UID: ${entry.uid})</div>
                                    <div style="color: var(--text-secondary); font-size: 0.85em;">
                                        启用: ${entry.enabled ? '<span style="color:#90EE90">是</span>' : '<span style="color:#FF4500">否</span>'} |
                                        类型: <span style="color: ${entry.type === 'constant' ? COLOR_CONSTANT : (entry.type === 'selective' ? COLOR_SELECTIVE : COLOR_VECTORIZED)}">
                                            ${entry.type === 'constant' ? '蓝灯' : (entry.type === 'selective' ? '绿灯' : '向量')}
                                        </span> |
                                        Tokens: ${entryTokens}
                                    </div>
                                </div>
                            `}).join('')}
                        </div>
                    </div>
                </div>
                <div class="edit-preview-area">
                    <h3>编辑预览</h3>
                    <div id="ai-edit-preview" style="background: var(--bg-dark); padding: 15px; border-radius: 8px; height: 400px; overflow-y: auto;">
                        <p class="text-muted">AI生成的编辑建议将显示在这里...</p>
                    </div>
                    <div style="text-align: right; margin-top: 15px;">
                        <button id="cancel-edits-button" class="btn btn-outline" style="margin-right: 8px;">取消</button>
                        <button id="apply-edits-button" class="btn btn-primary" disabled>应用修改</button>
                    </div>
                </div>
            </div>
            `;

            // 显示编辑界面
            callGenericPopup(editInterfaceHTML, POPUP_TYPE.DISPLAY, `AI编辑 - ${lorebookName}`, { wide: true, large: true });

            // 绑定事件处理
            parent$('#generate-edits-button').off('click').on('click', async function() {
                const editInstruction = parent$('#ai-edit-input').val();
                if (!editInstruction) {
                    parentToastr?.warning('请输入编辑指令');
                    return;
                }

                parent$(this).prop('disabled', true).html('<i class="fas fa-spinner fa-spin"></i> 生成中...');
                parent$('#ai-edit-preview').html('<p class="text-muted"><i class="fas fa-spinner fa-spin"></i> AI正在生成编辑建议...</p>');

                // 获取最新的条目数据用于生成编辑建议
                let entriesForGeneration;
                 try {
                     entriesForGeneration = await TavernHelper.getLorebookEntries(lorebookName);
                 } catch (fetchError) {
                     parent$('#ai-edit-preview').html(`<p style="color: red;">获取最新条目数据失败: ${fetchError.message}</p>`);
                     parent$('#generate-edits-button').prop('disabled', false).html('生成编辑建议');
                     return;
                 }

                try {
                    // [修改] 调用修改后的 generateAiEdits
                    const editPreview = await generateAiEdits(entriesForGeneration, editInstruction, lorebookName);
                    parent$('#ai-edit-preview').html(editPreview.html);
                    // 如果返回了手动输入区域，需要绑定手动解析按钮事件
                    if (parent$('#parse-manual-json').length > 0) {
                         parent$('#parse-manual-json').off('click').on('click', function() {
                             const manualJson = parent$('#manual-json-input').val();
                             try {
                                 const parsedEdits = JSON.parse(manualJson);
                                 if (Array.isArray(parsedEdits) && parsedEdits.every(e => e && typeof e === 'object' && e.uid !== undefined)) {
                                     // 验证并构建预览
                                     const mergedEditsData = validateAndMergeEdits(parsedEdits, entriesForGeneration);
                                     const previewHTML = buildEditPreviewHTML(mergedEditsData, entriesForGeneration, "手动解析JSON");
                                     parent$('#ai-edit-preview').html(previewHTML);
                                     parent$('#apply-edits-button').prop('disabled', mergedEditsData.length === 0).data('edits', mergedEditsData);
                                     parentToastr?.success("手动JSON解析成功！");
                                 } else {
                                     throw new Error("JSON格式不正确或缺少uid");
                                 }
                             } catch (parseError) {
                                 parentToastr?.error(`手动JSON解析失败: ${parseError.message}`);
                             }
                         });
                    } else {
                         parent$('#apply-edits-button').prop('disabled', !editPreview.edits || editPreview.edits.length === 0).data('edits', editPreview.edits);
                    }
                } catch (error) {
                    parent$('#ai-edit-preview').html(`<p style="color: red;">生成编辑时出错: ${error.message}</p>`);
                     parent$('#apply-edits-button').prop('disabled', true).data('edits', []); // 清空数据
                } finally {
                    parent$('#generate-edits-button').prop('disabled', false).html('生成编辑建议');
                }
            });

            parent$('#apply-edits-button').off('click').on('click', async function() {
                const editsToApply = parent$(this).data('edits');
                if (!editsToApply || !Array.isArray(editsToApply) || !editsToApply.length) {
                    parentToastr?.warning('没有可应用的修改。');
                    return;
                }

                parent$(this).prop('disabled', true).html('<i class="fas fa-spinner fa-spin"></i> 应用中...');
                parent$('#cancel-edits-button').prop('disabled', true); // 禁用取消按钮

                try {
                    await TavernHelper.setLorebookEntries(lorebookName, editsToApply);
                    parentToastr?.success(`成功应用了 ${editsToApply.length} 个条目的修改`);

                    // 关闭编辑弹窗
                    const editDialog = parent$(this).closest('dialog');
                    if (editDialog.length > 0 && typeof editDialog[0].close === 'function') {
                         editDialog[0].close('applied');
                    }

                    // 延迟一下再刷新主弹窗的条目列表
                    setTimeout(() => refreshEntryList(lorebookName), 300);

                } catch (error) {
                    parentToastr?.error(`应用修改时出错: ${error.message}`);
                    parent$('#apply-edits-button').prop('disabled', false).html('应用修改');
                     parent$('#cancel-edits-button').prop('disabled', false);
                }
            });

            parent$('#cancel-edits-button').off('click').on('click', function() {
                const editDialog = parent$(this).closest('dialog');
                 if (editDialog.length > 0 && typeof editDialog[0].close === 'function') {
                     editDialog[0].close('cancelled');
                 }
            });
        } catch (error) {
            logDebug(`openAiEditInterface错误: ${error.message}`);
            parentToastr?.error(`打开AI编辑界面时出错: ${error.message}`);
        }
    }

