    // --- eventMakeLast ---
    function eventMakeLast(eventSource, eventName, callback) { // 添加 eventName 参数
        if (eventSource && typeof eventSource.off === 'function' && typeof eventSource.on === 'function') {
            logDebug(`eventMakeLast: Rebinding event '${eventName}'`);
            eventSource.off(eventName, callback); // 确保移除特定事件的回调
            eventSource.on(eventName, callback);
        } else {
             console.warn(`eventMakeLast: Invalid eventSource or missing methods for event '${eventName}'`);
        }
    }

    // --- Initialization ---
    async function initializeScript() {
        logDebug('initializeScript: 初始化 Token 查看器脚本 (v2.5.5 美化版)...');
        if (typeof parent$ === 'function') {
            setupTokenButton(); // 初始设置按钮
            // 监听聊天变化事件，重新设置按钮（以防UI重新渲染导致按钮丢失）
            if (typeof tavern_events !== 'undefined' && typeof tavern_events.on === 'function' && typeof tavern_events.off === 'function') {
                 const chatChangeCallback = () => {
                     logDebug("Event: CHAT_CHANGED or GROUP_UPDATED or CHAR_UPDATED 触发，重新运行 setupTokenButton。");
                     // 稍微延迟以确保UI更新完成
                     setTimeout(setupTokenButton, 200);
                 };
                 // 监听多个可能导致UI更新的事件
                 eventMakeLast(tavern_events, 'CHAT_CHANGED', chatChangeCallback);
                 eventMakeLast(tavern_events, 'GROUP_UPDATED', chatChangeCallback);
                 eventMakeLast(tavern_events, 'CHAR_UPDATED', chatChangeCallback);
                 logDebug('initializeScript: 已绑定 CHAT_CHANGED, GROUP_UPDATED, CHAR_UPDATED 事件监听。');
            } else {
                console.warn("[TokenCheckerV2H] tavern_events 或其 on/off 方法未找到。按钮可能在UI更新后消失。");
            }
        } else {
            console.error('[TokenCheckerV2H] 无法初始化 UI，缺少父窗口 jQuery。');
        }
        logDebug('initializeScript: 初始化完成 (v2.5.5 美化版).');
    }

    // --- Entry Point ---
    try {
        // 延迟初始化以确保SillyTavern和TavernHelper完全加载
        setTimeout(initializeScript, 1000); // 增加延迟时间
    } catch(e) {
        console.error('[TokenCheckerV2H] 初始化顶层错误:', e);
        parentToastr?.error(`Token 查看器脚本(美化版)初始化失败: ${e.message}`);
    }

})();

$(function() {
    const dropdownSelector = '#completion_prompt_manager_footer_append_prompt';
    const listSelector = '#completion_prompt_manager_list';
    const listItemSelector = 'li[data-pm-identifier]';

    function updateDropdownOptions() {
        const $list = $(listSelector);
        const $dropdown = $(dropdownSelector);

        if (!$list.length || !$dropdown.length) return;

        const existingIdentifiers = new Set();
        $list.find(listItemSelector).each(function() {
            const identifier = $(this).data('pm-identifier');
            if (identifier) existingIdentifiers.add(identifier);
        });

        let selectedOptionExists = false;
        const selectedValue = $dropdown.val();
        let firstAvailableOption = null;

        $dropdown.find('option').each(function() {
            const $option = $(this);
            const optionValue = $option.val();

            if (optionValue && existingIdentifiers.has(optionValue)) {
                $option.hide();
                // 检查当前选中项是否已存在于列表中
                if (optionValue === selectedValue) {
                    selectedOptionExists = true;
                }
            } else {
                $option.show();
                // 记录第一个可用选项(有值且未被插入的选项)
                if (optionValue && !firstAvailableOption) {
                    firstAvailableOption = optionValue;
                }
            }
        });

        // 如果当前选中的选项已经在列表中存在
        if (selectedOptionExists) {
            if (firstAvailableOption) {
                // 选择第一个可用选项
                $dropdown.val(firstAvailableOption);
            } else {
                // 如果没有可用选项，则清空选择
                $dropdown.val('');
            }
            // 触发change事件更新UI
            $dropdown.trigger('change');
        }
    }

    // 使用事件委托绑定下拉框事件
    $(document).on('change', dropdownSelector, function() {
        // 下拉框变化时的处理逻辑
    });

    $(document).on('focus', dropdownSelector, function() {
        updateDropdownOptions();
    });

    // 初始更新选项
    updateDropdownOptions();

    // 监听列表变化
    const targetNode = document.querySelector(listSelector);
    if (targetNode) {
        const observer = new MutationObserver(() => updateDropdownOptions());
        observer.observe(targetNode, { childList: true, subtree: true });
    }

    // 定期检查，防止漏掉更新
    setInterval(function() {
        if ($(dropdownSelector).length) updateDropdownOptions();
    }, 1000);
});

"use strict";

/* --- API Config Manager Configuration --- */
const apiConfigManagerName = "api-config-manager";
const apiConfigManagerContainerId = 'api-config-manager-container';
const apiConfigManagerStylesId = 'api-config-manager-styles';
const apiConfigManagerSettingsKey = `st_${apiConfigManagerName}_settings`;
const apiConfigManagerDBName = 'api-config-manager-db';
const apiConfigManagerDBVersion = 1;
const apiConfigManagerStoreName = 'settings';
const apiConfigManagerEncryptionKey = `st_${apiConfigManagerName}_encryption_key`;

// Default settings with empty profiles
const apiConfigManagerDefaultSettings = {
    profiles: []
};

let apiConfigManagerSettings = {};
let apiConfigManagerDB = null;
let encryptionKey = null;

/* --- Helper Functions --- */
function apiConfigErrorCatched(fn, context = 'API Config Manager') {
    const onError = (error) => {
        let iframeName = 'UnknownIframe';
        try {
            iframeName = (typeof getIframeName === 'function') ? getIframeName() : (window.frameElement ? window.frameElement.id : 'UnknownIframe');
        } catch (e) {
            console.warn(`(${context}) Could not get iframe name:`, e);
        }
        const errorMessage = `Error in ${iframeName} (${context}) ${error.stack || error.message || error.name}`;
        console.error(errorMessage);
        if (typeof toastr !== 'undefined' && toastr) { // Check if toastr exists
            toastr.error(`Error in ${context}. Check console.`);
        } else {
             console.error(`Toastr not available to display error message in ${context}.`);
        }
    };
    return (...args) => {
        try {
            const result = fn(...args);
            if (result instanceof Promise) {
                return result.catch(error => {
                    onError(error);
                    throw error; // Re-throw after logging
                });
            }
            return result;
        } catch (error) {
            onError(error);
            throw error; // Re-throw after logging
        }
    };
}

const log = apiConfigErrorCatched((message) => {
    console.log(`[${apiConfigManagerName}] ${message}`);
}, 'log');

const fallbackCopy = apiConfigErrorCatched((text, doc) => {
