        return; // Exit if no active tab or unit found
    }

    const activeIndex = parseInt($activeTab.data('index'));

    // Validate index
    if (isNaN(activeIndex) || activeIndex < 0 || !apiConfigManagerSettings.profiles || activeIndex >= apiConfigManagerSettings.profiles.length) {
        log(`找不到有效的激活配置来保存数据，无效索引: ${activeIndex}`);
        return; // Exit if index is invalid
    }

    // Gather data from the visible config unit
    const name = $configUnit.find('.config-name').val()?.trim() || ''; // Use optional chaining and provide default
    const endpoint = $configUnit.find('.config-endpoint').val()?.trim() || '';
    const keys = [];
    $configUnit.find('.config-key').each(function() {
        const keyValue = $(this).val()?.trim(); // Trim whitespace
        // Optionally decide if empty keys should be saved
        // if (keyValue) { // Uncomment this line to only save non-empty keys
            keys.push(keyValue || ''); // Save empty string if input is empty but row exists
        // }
    });

    // Ensure at least one key input exists, even if empty, if the array becomes empty
    if (keys.length === 0) {
         keys.push('');
         log("配置中没有有效的秘钥，添加一个空秘钥占位符。");
    }


    // Update the profile in the settings object
    apiConfigManagerSettings.profiles[activeIndex] = {
        name: name || `配置 ${activeIndex + 1}`, // Provide default name if empty
        keys: keys,
        endpoint: endpoint
    };

    // Update the tab display name immediately
    const tabName = name || `配置 ${activeIndex + 1}`;
