                        border-color: #3a7bd5 !important; /* Use important if needed to override inline */
                        box-shadow: 0 0 0 3px rgba(58,123,213,0.2) !important;
                    }
                    .config-key { font-family: monospace; letter-spacing: 0.5px; }
                    button { display: flex; align-items: center; justify-content: center; transition: all 0.2s ease !important; } /* Reduced button transition */
                    button:active { transform: scale(0.95) !important; }

                    /* Scrollbar Styles */
                    .drawer-body::-webkit-scrollbar, .profile-tabs-container::-webkit-scrollbar { width: 6px; height: 6px; }
                    .drawer-body::-webkit-scrollbar-track, .profile-tabs-container::-webkit-scrollbar-track { background: rgba(58,123,213,0.05); border-radius: 6px; }
                    .drawer-body::-webkit-scrollbar-thumb, .profile-tabs-container::-webkit-scrollbar-thumb { background: rgba(58,123,213,0.3); border-radius: 6px; }
                    .drawer-body::-webkit-scrollbar-thumb:hover, .profile-tabs-container::-webkit-scrollbar-thumb:hover { background: rgba(58,123,213,0.5); }
                    .drawer-body, .profile-tabs-container { scrollbar-width: thin; scrollbar-color: rgba(58,123,213,0.3) rgba(58,123,213,0.05); }


                    /* Tab Styles */
                    .profile-tabs-container { /* Styles already in createProfileModal */ }
                    .profile-tab { /* Styles already in renderProfileTabs */ animation: slideIn 0.2s ease-out forwards; }
                    .profile-tab:hover { background: rgba(58, 123, 213, 0.1) !important; transform: translateY(-2px); }
                    .profile-tab.active:hover { background: rgba(58, 123, 213, 0.25) !important; }
                    .profile-tab.over { border: 1px dashed #3a7bd5 !important; background: rgba(58, 123, 213, 0.15) !important; }
                    .profile-tab.dragging { opacity: 0.5; border-style: dashed; }

                     /* Simple slide-in animation */
                     @keyframes slideIn { from { transform: translateY(5px); opacity: 0; } to { transform: translateY(0); opacity: 1; } }
                     .config-unit, .key-row { animation: slideIn 0.25s ease-out forwards; }


                    /* Mobile Responsiveness */
                    @media (max-width: 768px) {
                        /* Modal itself uses max-width: 100% now, relies on backdrop padding */
                         #api-config-manager-modal {
                             /* width: 95% !important; /* Not needed */
                             max-height: 95vh !important;
                             padding: 15px !important;
                             width: 95%; /* Or keep auto width based on content */
                         }
                        .drawer-body { max-height: calc(95vh - 150px) !important; /* Adjust if header/footer height changes */ }
                        .key-row { flex-wrap: wrap; } /* Allow wrapping on small screens */
                        .config-key { width: calc(100% - 120px) !important; min-width: 100px !important; font-size: 13px !important; } /* Adjust key input width */
                        .key-actions { width: 110px; justify-content: flex-end; } /* Keep actions together */
                        .key-actions button { width: 30px !important; height: 30px !important; font-size: 12px !important; }
                        .drawer-header h3 { font-size: 14px !important; }
                        .drawer-header .encryption-status { display: none; } /* Hide status on small screens */
                        .profile-tab { max-width: 100px; } /* Reduce max tab width */
                         #api-config-manager-save { font-size: 13px; padding: 8px; }
                    }
                     @media (max-width: 400px) {
                         .config-key { width: calc(100% - 40px) !important; } /* Make key almost full width */
                         .key-actions { width: auto; margin-top: 5px; justify-content: flex-start; } /* Stack actions below */
                     }
                </style>
            `;
            $('head', parentDoc).append(styles);
             log("CSS 样式已注入");
        }

        // 5. Inject the Control Button into the page (e.g., next to a title)
        // Adjust selector as needed for SillyTavern's structure
        const $apiConfigTitle = $('h3:contains("API连接配置")', parentDoc); // Find anchor point
        if ($apiConfigTitle.length && $(`#api-config-manager-button`, parentDoc).length === 0) {
            log(`找到目标标题 "${$apiConfigTitle.text()}"，准备添加按钮`);
            const buttonTitle = `API配置管理器 ${encryptionKey ? '(已加密)' : '(未加密)'}`;
            // Using FontAwesome icon if available
            const $button = $(`<a id="api-config-manager-button" href="#" title="${buttonTitle}"><i class="fa-solid fa-key" style="font-size: inherit;"></i></a>`); // Inherit font size
            $apiConfigTitle.append($button);
             log(`按钮已添加到标题`);

            // 6. Add Click Handler for the Button
            $button.on('click', function(e) {
                e.preventDefault();
                log(`API 配置按钮被点击`);

                // Ensure only one modal/backdrop exists
                $('#api-config-manager-modal', parentDoc).remove();
                $('#api-config-manager-backdrop', parentDoc).remove();

                // Create backdrop first
                const $backdrop = $(`<div id="api-config-manager-backdrop"></div>`);
                $('body', parentDoc).append($backdrop);

                // Create modal (it's initially hidden with opacity 0)
                const $modal = createProfileModal(); // Creates the modal but doesn't show it yet

                // Load latest settings right before showing
                 loadApiConfigSettingsFromStorage().then(() => {
                     // Update encryption status display based on latest key check
                      $('.encryption-status', $modal).html(encryptionKey ? '🔒 已加密' : '⚠️ 未加密');
                      // Render UI components
                     renderProfileTabs(); // Renders tabs, ensures one is active, inits D&D
                     renderConfigUnits(); // Renders the unit for the active tab
                     bindUIEvents(); // Binds all necessary event listeners

                     // --- Show Modal and Backdrop ---
                     // Use requestAnimationFrame to ensure styles are applied before transition starts
                     requestAnimationFrame(() => {
                         $backdrop.addClass('visible');
                         $modal.addClass('visible').css('display', 'block'); // Need display: block for visibility
                         log(`模态框和遮罩已显示`);
                     });

                     // Add backdrop click listener to close
                     $backdrop.off('click').on('click', function(event) {
                          // Only close if click is directly on backdrop, not modal content
                          if (event.target === this) {
                             log("Backdrop click detected, closing modal.");
                              $modal.removeClass('visible');
                              $backdrop.removeClass('visible');
                              // Remove from DOM after transition
                              setTimeout(() => {
                                  $modal.remove();
                                  $backdrop.remove();
                                  log("模态框和遮罩已移除 (通过Backdrop点击)");
                              }, 300); // Match backdrop transition duration
                          }
                     });

                 }).catch(err => {
                     console.error("Error during modal setup:", err);
                      if (typeof toastr !== 'undefined' && toastr) toastr.error("打开配置管理器时出错，请检查控制台。");
                       // Clean up backdrop if modal setup fails
                       $backdrop.remove();
                 });
            });

        } else if ($(`#api-config-manager-button`, parentDoc).length > 0) {
             log("API 配置按钮已存在。");
        } else {
            console.error(`[${apiConfigManagerName}] 未找到目标标题元素 "h3:contains('API连接配置')" 来附加按钮。`);
             if (typeof toastr !== 'undefined' && toastr) toastr.error("无法将API配置按钮添加到页面。");
        }

        log(`API Config Manager 初始化完成。`);

    } catch (error) {
        console.error(`[${apiConfigManagerName}] 初始化过程中发生严重错误:`, error);
         if (typeof toastr !== 'undefined' && toastr) toastr.error("API配置管理器初始化失败，请检查控制台。");
         // Reset init flag if failed?
         window.apiConfigManagerInitialized = false;
    }
});

