            function updateProgressBar() {
                let favorabilityValue = favorabilityElement.textContent.trim();
                // å°è¯•æå–æ•°å­—
                let match = favorabilityValue.match(/\d+/);
                let value = match ? parseInt(match[0]) : 0;

