    $msgContent[0].scrollTop = $msgContent[0].scrollHeight;
    $(element).val("");
    if (QQ_Groups.includes(name)) {
        QQ_CacheSendMsg += `\nåœ¨ç¾¤èŠ${name}ä¸­å‘é€:${content}`;
    }
    else {
        QQ_CacheSendMsg += `\nç»™${name}å‘æ¶ˆæ¯:${content}`;
    }
}
