            input.val("");
        }
        else {
            name = $container.attr("data-name") || "æœªçŸ¥ç”¨æˆ·";
            console.warn("å‘é€å†…å®¹ä¸ºç©º");
        }
        console.log(`ç¼“å­˜æ¶ˆæ¯$:${QQ_CacheSendMsg}`);
        if (QQ_Groups.includes(name)) {
            if (QQ_CacheSendMsg) {
                value = `${QQ_CacheSendMsg}`;
                if (content) {
                    value += `\nåœ¨ç¾¤èŠ${name}ä¸­å‘é€:${content}`;
                }
                value += `\n${Request}`;
            }
            else {
                value = `åœ¨ç¾¤èŠ${name}ä¸­å‘é€:${content}`;
            }
        }
        else {
            if (QQ_CacheSendMsg) {
                value = `${QQ_CacheSendMsg}`;
                if (content) {
                    value += `\nç»™${name}å‘æ¶ˆæ¯:${content}`;
                }
                value += `\n${Request}`;
            }
            else {
                value = `ç»™${name}å‘æ¶ˆæ¯:${content}`;
            }
        }
        if (!value && !QQ_CacheSendMsg) {
            QQ_Error("å‘é€æ¶ˆæ¯ä¸èƒ½ä¸ºç©º");
            return;
        }
        QQ_CacheSendMsg = "";
        if (value) {
            const namevalue = QQ_GetValueName(value);
            if (namevalue) {
