function Verify() {
    try {
        // è·å–ç‰ˆæœ¬å…ƒç´ å¹¶æå–çº¯ç‰ˆæœ¬å·
        const versionElement = window.parent.document.querySelector(".js-settings .extension_info.flex-container.spaceBetween > small");
        if (!versionElement) {
            throw new Error("Version element not found");
        }
        // ä½¿ç”¨æ›´ä¸¥æ ¼çš„æ­£åˆ™è¡¨è¾¾å¼æå–ç‰ˆæœ¬å·
        const versionMatch = versionElement.textContent.match(/ver\s*([\d.]+)/i);
        if (!versionMatch) {
            throw new Error("Version format invalid");
        }
        // æ‹†åˆ†ç‰ˆæœ¬å·ä¸ºæ•°å­—æ•°ç»„
        const versionParts = versionMatch[1].split(".").map(Number);
        if (versionParts.length < 3 || versionParts.some(isNaN)) {
            throw new Error("Invalid version format");
        }
        // è§£æ„èµ‹å€¼ç‰ˆæœ¬å·
        const [major, minor, patch] = versionParts;
        // è®¾ç½®æœ€ä½è¦æ±‚ç‰ˆæœ¬
        const MIN_MAJOR = 2;
        const MIN_MINOR = 4;
        const MIN_PATCH = 3;
        // ç‰ˆæœ¬æ¯”è¾ƒé€»è¾‘
        if (major < MIN_MAJOR ||
            (major === MIN_MAJOR &&
                (minor < MIN_MINOR || (minor === MIN_MINOR && patch < MIN_PATCH)))) {
            alert(`å‰ç«¯åŠ©æ‰‹ç‰ˆæœ¬è¿‡ä½ (å½“å‰ ${versionParts.join(".")}ï¼Œéœ€è¦è‡³å°‘ ${MIN_MAJOR}.${MIN_MINOR}.${MIN_PATCH})ï¼Œè¯·æ›´æ–°`);
        }
    }
    catch (error) {
        assertIsError(error);
        console.error("ç‰ˆæœ¬éªŒè¯å¤±è´¥:", error);
        // å¦‚æœå­˜åœ¨è‡ªå®šä¹‰é”™è¯¯å¤„ç†åˆ™è°ƒç”¨ï¼Œå¦åˆ™ä½¿ç”¨é»˜è®¤æç¤º
        if (typeof QQ_Error === "function") {
            QQ_Error(`éªŒè¯å‰ç«¯åŠ©æ‰‹ç‰ˆæœ¬å¤±è´¥: ${error.message}`);
        }
        else {
            alert("ç‰ˆæœ¬éªŒè¯å¤±è´¥ï¼Œè¯·æ£€æŸ¥æ§åˆ¶å°ä¿¡æ¯");
        }
    }
}
