/**
 * 获取消息中的名称
 * @param value 消息内容
 * @returns 名称列表
 */
function QQ_GetValueName(value) {
    let result = [];
    const lines = value.split(/\r?\n/);
    for (const line of lines) {
        let match = line.match(/在群聊(.+)中发送:(.+)/);
        if (match) {
            let obj = {
                name: match[1],
                value: match[2],
            };
            result.push(obj);
            continue;
        }
        match = line.match(/给(.+)发消息:(.+)/);
        if (match) {
            let obj = {
                name: match[1],
                value: match[2],
            };
            result.push(obj);
            continue;
        }
        match = line.match(/回复(.+):(.+)/);
        if (match) {
            let obj = {
                name: match[1],
                value: match[2],
            };
            result.push(obj);
            continue;
        }
    }
    return result;
}
