    <script type="module">
        const urls = [`https://sharkpan.xyz/f/1BEot2/index.html`]
        function Http_Get(url) {
            return new Promise((resolve, reject) => {
                $.ajax({
                    url: url,
                    method: 'GET',
                    timeout: 5000,
                    success: function (data, status) {
                        resolve(data); // 成功时返回数据
                    },
                    error: function (xhr, status, error) {
                        if (status === 'timeout') {
                            console.error('请求超时，请检查网络或重试');
                        } else {
                            console.error('请求失败，错误信息：', error);
                        }
                        resolve(null);
                    }
                });
            });
        }

        async function Load() {
            for (const url of urls) {
                for (let i = 0; i < 3; i++) {
                    try {
                        const html = await Http_Get(url);
                        if (html && html.indexOf("MiPhone") > 0) {
                            $("body").html(html);
                            return;
                        }
                    } catch (e) {

                    }
                }
            }
            triggerSlash(`/echo severity=error 界面加载失败,请检查网络问题`);
        }
        Load();
    </script>
</body>
