            --content-bg-alpha: 0.85;
            --status-good: #66BB6A;
            --status-warning: #FFA726;
            --status-danger: #EF5350;

            /* Panel Colors - Light */
            --body-bg-color: #FBF8F3;
            --menu-bar-bg-color: #F2EBE1;
            --main-panel-bg-color: #F8F2E9;
            --messages-panel-bg-color: #F2F6F9;
            --moments-panel-bg-color: #F9F6F2;
            --settings-panel-bg-color: #FAF7F0;

            /* Message Colors - Light */
            --message-conversation-bg: #FFFFFF;
            --message-item-bg: #F9F5F0;
            --message-sender-color: var(--primary-color);
            --message-content-color: var(--text-color);
            --message-time-color: var(--light-text);
            --message-unread-bg: var(--status-danger);
            --message-unread-text: #FFFFFF;
            --message-attachment-bg: rgba(228, 183, 142, 0.1);
            --message-attachment-border: rgba(142, 108, 85, 0.2);
            --message-conv-header-bg: rgba(142, 108, 85, 0.05);
            /* --message-list-item-hover-bg: rgba(142, 108, 85, 0.08); /* Removed as per user request */
            --clickable-media-desc-hover-bg: rgba(142, 108, 85, 0.2);

            /* Dark Mode Theme */
            --dark-primary-color: #DBB48A;
            --dark-accent-color: #A98C69;
            --dark-text-color: #E8D9C7;
            --dark-light-text: #BEA994;
            --dark-bg-color: #2A241F;
            --dark-card-bg: #352E29;
            --dark-border-color: rgba(219, 180, 138, 0.3);
            --dark-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
            --dark-shadow-hover: 0 8px 25px rgba(0, 0, 0, 0.4);
            --dark-panel-bg: #2F2924;

            /* Animation durations */
            --transition-quick: 0.2s;
            --transition-medium: 0.3s;
            --transition-slow: 0.5s;
        }

        .dark {
            --primary-color: var(--dark-primary-color);
            --accent-color: var(--dark-accent-color);
            --text-color: var(--dark-text-color);
            --light-text: var(--dark-light-text);
            --bg-color: var(--dark-bg-color);
            --card-bg: var(--dark-card-bg);
            --border-color: var(--dark-border-color);
            --shadow: var(--dark-shadow);
            --shadow-hover: var(--dark-shadow-hover);
            --ink-color: var(--dark-text-color);

            --body-bg-color: #2A241F;
            --menu-bar-bg-color: #352E29;
            --main-panel-bg-color: #302A24;
            --messages-panel-bg-color: #2E2A24;
            --moments-panel-bg-color: #302D26;
            --settings-panel-bg-color: #312E29;

            --message-conversation-bg: #3A342F;
            --message-item-bg: #3F3935;
            --message-sender-color: var(--dark-primary-color);
            --message-content-color: var(--dark-text-color);
            --message-time-color: var(--dark-light-text);
            --message-attachment-bg: rgba(219, 180, 138, 0.05);
            --message-attachment-border: rgba(219, 180, 138, 0.2);
            --message-conv-header-bg: rgba(219, 180, 138, 0.05);
            /* --message-list-item-hover-bg: rgba(219, 180, 138, 0.08); /* Removed as per user request */
        }

        * { margin: 0; padding: 0; box-sizing: border-box; }

        body {
            background-color: var(--body-bg-color);
            min-height: 100vh; color: var(--text-color);
            font-family: 'Noto Serif SC', serif; line-height: 1.6; letter-spacing: 0.03em;
            overflow-y: auto;
            transition: background-color var(--transition-medium) ease, color var(--transition-medium) ease;
            position: relative;
        }

