        const DataExtractor = {
            extractMainContent(textBlock) { const mainContentMatch = textBlock.match(/<maincontent>([\s\S]*?)<\/maincontent>/i); if (!mainContentMatch) return null; const content = mainContentMatch[1].trim(); const data = { time: '', location: '', weather: '', story: '' }; let storyLines = []; let collectingStory = false; content.split('\n').forEach(line => { line = line.trim(); if (!line) return; if (/^(当前时间|当前地点|天气)\s*\|/.test(line)) { collectingStory = false; } if (collectingStory) { storyLines.push(line); return; } if (line.startsWith('当前时间|')) data.time = line.substring('当前时间|'.length).trim(); else if (line.startsWith('当前地点|')) data.location = line.substring('当前地点|'.length).trim(); else if (line.startsWith('天气|')) data.weather = line.substring('天气|'.length).trim(); else if (line.startsWith('正文|')) { storyLines.push(line.substring('正文|'.length).trim()); collectingStory = true; } }); data.story = storyLines.join('\n'); return data; },
