            extractChatData(textBlock) {
                const chatLogMatch = textBlock.match(/<chatlog>([\s\S]*?)<\/chatlog>/i);
                if (!chatLogMatch) return [];
                const chatLogContent = chatLogMatch[1].trim();
                const rawMessages = chatLogContent.split('---CHAT_MESSAGE_SEPARATOR---');
                const messages = [];
