                        if (collectingText) { currentTextLines.push(line); continue; }
                        const parts = line.split('|'); const key = parts[0].trim(); const value = parts.length > 1 ? parts.slice(1).join('|').trim() : '';
                        if (key === '发帖id') post.author = value;
                        else if (key === '头像') post.avatar = value;
                        else if (key === '发帖时间') post.timestamp = value;
