                        else if (key.startsWith('图片') || key.startsWith('视频')) { const type = key.startsWith('图片') ? 'image' : 'video'; const indexMatch = key.match(/\d+$/); const index = indexMatch ? parseInt(indexMatch[0]) : post.mediaText.length + 1; post.mediaText.push({ type: type, index: index, text: value }); }
                        else if (key.startsWith('标签')) { post.tags.push(value); }
