        };

        const UIUpdater = {
            updateMainContent(data) { if (!data) return; const statusBar = $('.player-status-bar'); if (statusBar) { const timeEl = statusBar.querySelector('[data-type="time"] span:last-child'); const locEl = statusBar.querySelector('[data-type="location"] span:last-child'); const weatherEl = statusBar.querySelector('[data-type="weather"] span:last-child'); const weatherIconEl = statusBar.querySelector('[data-type="weather"] i'); if (timeEl) timeEl.textContent = data.time || '（未知时辰）'; if (locEl) locEl.textContent = data.location || '（未知地点）'; if (weatherEl) weatherEl.textContent = data.weather || '（未知天气）'; if (weatherIconEl) { if (data.weather.includes('雨')) weatherIconEl.className = 'fas fa-cloud-showers-heavy'; else if (data.weather.includes('雪')) weatherIconEl.className = 'fas fa-snowflake'; else if (data.weather.includes('云') || data.weather.includes('阴')) weatherIconEl.className = 'fas fa-cloud'; else if (data.weather.includes('晴')) weatherIconEl.className = 'fas fa-sun'; else weatherIconEl.className = 'fas fa-question-circle'; } } const storyP = $('.story-content p'); if (storyP) storyP.innerHTML = data.story ? data.story.replace(/\n/g, '<br>') : '（暂无剧情）'; },
            updateOptions(options) { const choicesContainer = $('.player-choices'); if (!choicesContainer) return; choicesContainer.querySelectorAll('.choice-option').forEach(opt => opt.remove()); if (options && options.length > 0) { options.forEach((optText, index) => { if (!optText) return; const optDiv = document.createElement('div'); optDiv.className = 'choice-option'; optDiv.innerHTML = `<div class="choice-text">${optText}</div>`; optDiv.dataset.choiceIndex = index; choicesContainer.appendChild(optDiv); }); } else { const noOptDiv = document.createElement('div'); noOptDiv.className = 'choice-option disabled'; noOptDiv.innerHTML = `<div class="choice-text">（暂无选项）</div>`; choicesContainer.appendChild(noOptDiv); } },
            updateMessagesPanel(messagesData) {
                allConversationsData = {};
                if (messagesData && messagesData.length > 0) {
                    messagesData.forEach(msg => {
                        if (!allConversationsData[msg.conversationId]) {
                            allConversationsData[msg.conversationId] = {
                                representativeSender: msg.sender,
                                lastMessagePreview: msg.lastMessagePreview, 
                                unreadCount: msg.unreadCount,
                                latestTimestamp: msg.timestamp, 
                                messages: [],
                                uniqueSenders: new Set()
                            };
                        }
                        allConversationsData[msg.conversationId].messages.push(msg);
                        allConversationsData[msg.conversationId].uniqueSenders.add(msg.sender);
                        if (msg.timestamp > allConversationsData[msg.conversationId].latestTimestamp) {
                           allConversationsData[msg.conversationId].latestTimestamp = msg.timestamp;
                        }
                    });
                }
                renderConversationList();
                switchToListView();
            },
            updateMomentsData(posts) {
                const momentsContainer = $('.moments-container');
                const prevButton = $('#prev-moment');
                const nextButton = $('#next-moment');
                const momentCounter = $('#moment-counter');

                if (!momentsContainer) return;

                momentsContainer.innerHTML = '';
                currentMomentIndex = 0;
                totalMoments = posts ? posts.length : 0;

                // Update counter display
                momentCounter.textContent = totalMoments > 0 ? `1 / ${totalMoments}` : '0 / 0';

                // Update navigation buttons
                prevButton.disabled = true; // First post, can't go previous
                nextButton.disabled = totalMoments <= 1; // Disable next if 0 or 1 post

                if (posts && posts.length > 0) {
                    posts.forEach((post, postIndex) => {
                        const postDiv = document.createElement('div');
                        postDiv.className = 'moment-post';
                        postDiv.dataset.postId = `post-${postIndex}`;

                        // Make only the first post active initially
                        if (postIndex === 0) {
                            postDiv.classList.add('active');
                        }

                        let avatarHTML;
                        const authorName = post.author || '匿名';

                        if (post.avatar && (post.avatar.startsWith('http') || post.avatar.startsWith('data:'))) {
                            avatarHTML = `<img src="${post.avatar}" alt="${authorName}头像" class="moment-avatar" onerror="this.src='${defaultAvatar}'; this.onerror=null;">`;
                        } else {
                            const initial = authorName.trim().substring(0, 1) || '?';
                            avatarHTML = `<div class="moment-avatar" title="${authorName}头像">${initial}</div>`;
                        }

                        let tagsHTML = post.tags && post.tags.length > 0 ? post.tags.map(tag => `<div class="moment-tag"><i class="fas fa-tag"></i> ${tag}</div>`).join('') : '';

                        let mediaTextGridHTML = '';
                        if (post.mediaText && post.mediaText.length > 0) {
                            mediaTextGridHTML = '<div class="moment-media-text-grid">';
                            post.mediaText.forEach((media, idx) => {
                                const iconClass = media.type === 'image' ? 'fa-image' : 'fa-video';
                                const label = `${media.type === 'image' ? '图片' : '视频'} ${idx + 1}`;
                                mediaTextGridHTML += `<div class="moment-media-text-item" 
                                                            data-media-type="${media.type}" 
                                                            data-media-title="${label}" 
                                                            data-media-text="${encodeURIComponent(media.text || '无详细描述')}">
                                                        <i class="fas ${iconClass}"></i> ${label}
