                        }

                        let commentsHTML = post.comments && post.comments.length > 0 ? post.comments.map(comment => ` <div class="moment-comment"> <span class="comment-author-name">${comment.author}:</span> <span class="comment-text">${comment.text}</span> </div>`).join('') : '';

                        postDiv.innerHTML = `
                            <div class="moment-post-header"> ${avatarHTML} <div class="moment-post-info"> <div class="moment-author">${authorName}</div> <div class="moment-timestamp">${post.timestamp || '（未知时间）'}</div> </div> </div>
                            <div class="moment-post-body"> ${post.title ? `<h3 class="moment-post-title">${post.title}</h3>` : ''} <div class="moment-post-text">${post.text ? post.text.replace(/\n/g, '<br>') : '（无内容）'}</div> ${tagsHTML ? `<div class="moment-tags">${tagsHTML}</div>` : ''} ${mediaTextGridHTML ? `<div class="moment-media-content">${mediaTextGridHTML}</div>` : ''} </div>
                            <div class="moment-actions"> <div class="moment-action like-action" data-liked="false"><i class="far fa-heart"></i><span class="action-count">${post.likes || 0}</span></div> <div class="moment-action comment-action"><i class="far fa-comment-dots"></i><span class="action-count">${post.commentsCount || 0}</span></div> <div class="moment-action share-action"><i class="fas fa-share-square"></i><span class="action-count">${post.shares || 0}</span></div> </div>
                            ${commentsHTML ? `<div class="moment-comments-section">${commentsHTML}</div>` : ''}`;

                        momentsContainer.appendChild(postDiv);
                    });
                } else {
                    momentsContainer.innerHTML = '<p style="text-align:center; color: var(--light-text);">（朋友圈暂无动态）</p>';
                }
            },
            applySettings(settings) { document.body.style.fontFamily = settings.fontFamily; document.body.style.fontSize = `${settings.fontSize}px`; document.documentElement.style.setProperty('--bg-color', settings.bgColor); document.documentElement.style.setProperty('--primary-color', settings.primaryColor); document.documentElement.style.setProperty('--content-bg-alpha', parseFloat(settings.contentBrightness) / 100); const fontSizeValueEl = $('#font-size-value'); if (fontSizeValueEl) fontSizeValueEl.textContent = `${settings.fontSize}px`; const brightnessValueEl = $('#brightness-value'); if (brightnessValueEl) brightnessValueEl.textContent = `${settings.contentBrightness}%`; 
                if (settings.primaryColor) {
                    const hexToRgb = (hex) => {
                        const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
                        return result ? {
                            r: parseInt(result[1], 16),
                            g: parseInt(result[2], 16),
                            b: parseInt(result[3], 16)
                        } : null;
                    };
                    const rgb = hexToRgb(settings.primaryColor);
                    if (rgb) {
                        document.documentElement.style.setProperty('--primary-color-rgb', `${rgb.r}, ${rgb.g}, ${rgb.b}`);
                    }
                }
            },
            loadSettingsToPanel(settings) { const fontFamilySelect = $('#font-family-setting'); const fontSizeRange = $('#font-size-setting'); const bgColorPicker = $('#bg-color-setting'); const primaryColorPicker = $('#primary-color-setting'); const brightnessRange = $('#brightness-setting'); if (fontFamilySelect) fontFamilySelect.value = settings.fontFamily; if (fontSizeRange) fontSizeRange.value = settings.fontSize; if (bgColorPicker) bgColorPicker.value = settings.bgColor; if (primaryColorPicker) primaryColorPicker.value = settings.primaryColor; if (brightnessRange) brightnessRange.value = settings.contentBrightness; UIUpdater.applySettings(settings); }
        };

        function renderConversationList() {
            const listView = $('#conversation-list-view');
            const searchBar = listView.querySelector('.messages-search-bar');
            Array.from(listView.children).forEach(child => {
                if (!child.classList.contains('messages-search-bar')) {
                    child.remove();
                }
            });

            let totalUnreadForTabDot = 0;

            if (Object.keys(allConversationsData).length > 0) {
                Object.keys(allConversationsData).forEach(convId => {
                    const conv = allConversationsData[convId];
                    const listItem = document.createElement('div');
                    listItem.className = 'conversation-list-item';
                    listItem.dataset.convId = convId; 

                    const unreadCount = parseInt(conv.unreadCount);
                    if (unreadCount > 0) { totalUnreadForTabDot += unreadCount; }

                    let listTitle = convId; 
                    if (conv.uniqueSenders.size === 1) { 
                        listTitle = conv.representativeSender || convId; 
                    } else if (conv.uniqueSenders.size > 1) {
                        listTitle = `${convId} (群聊)`; 
                    }

                    const initial = listTitle.charAt(0).toUpperCase();
                    const isOnline = Math.random() > 0.5; 
                    const isVerified = Math.random() > 0.7; 

                    listItem.innerHTML = `
                        <div class="conversation-avatar">${initial}
                            <div class="conversation-online-status ${isOnline ? 'online' : ''}"></div>
                        </div>
                        <div class="conversation-list-details">
                            <div class="conversation-list-title">${listTitle} ${isVerified ? '<i class="fas fa-check-circle conversation-list-verified"></i>' : ''}</div>
                            <div class="conversation-list-preview">${conv.lastMessagePreview || '（无预览）'}</div>
                            <div class="conversation-list-meta">
                                <span class="conversation-list-time">${conv.latestTimestamp || '（未知时间）'}</span>
                                ${unreadCount > 0 ? `<span class="conversation-list-unread">${unreadCount}</span>` : ''}
                            </div>
                        </div>`;
                    listItem.addEventListener('click', () => showDetailedMessages(convId));

                    if (searchBar) {
                        listView.insertBefore(listItem, searchBar.nextSibling);
                    } else {
                        listView.appendChild(listItem);
                    }
                });
            } else {
                const emptyState = document.createElement('div');
                emptyState.innerHTML = '<p style="text-align:center; color: var(--light-text); padding: 20px;">（暂无会话）</p>';
                listView.appendChild(emptyState);
            }

            const messagesDot = $('.messages-dot');
            const messagesMenuItem = $('.menu-item[data-panel="messages"]');
            if (messagesDot && messagesMenuItem) {
                if (totalUnreadForTabDot > 0 && !messagesMenuItem.classList.contains('active')) {
                    messagesDot.style.display = 'inline-block';
                } else { messagesDot.style.display = 'none'; }
            }
        }

        function showDetailedMessages(convId) {
            const detailedTitle = $('#detailed-view-title');
            const detailedAvatar = $('.detailed-view-avatar');
            const messagesArea = $('.detailed-messages-area');
            const statusText = $('.online-status-text');
            const typingIndicator = $('.detailed-view-typing');
            const convData = allConversationsData[convId];

            if (!convData) return;

            let detailedHeaderTitle = convId; 
            if (convData.uniqueSenders.size === 1) {
                detailedHeaderTitle = convData.representativeSender || convId;
            } else if (convData.uniqueSenders.size > 1) {
                detailedHeaderTitle = `${convId} (群聊)`;
            }

            detailedTitle.textContent = detailedHeaderTitle;
            detailedAvatar.textContent = detailedHeaderTitle.charAt(0).toUpperCase();

            const isOnline = Math.random() > 0.3;
            if (isOnline) {
                statusText.textContent = '在线';
                statusText.style.color = 'var(--status-good)';
                if (Math.random() > 0.9) {
                    statusText.style.display = 'none';
                    typingIndicator.classList.add('active');
                } else {
                    statusText.style.display = 'inline';
                    typingIndicator.classList.remove('active');
                }
            } else {
                statusText.textContent = `最后在线：${Math.floor(Math.random() * 60) + 1}分钟前`;
                statusText.style.color = 'var(--light-text)';
                typingIndicator.classList.remove('active');
            }

            messagesArea.innerHTML = '';
            const dateGroups = groupMessagesByDate(convData.messages);

            Object.keys(dateGroups).forEach(date => {
                const dateSeparator = document.createElement('div');
                dateSeparator.className = 'message-date-separator';
                dateSeparator.innerHTML = `<span class="message-date-text">${date}</span>`;
                messagesArea.appendChild(dateSeparator);

                dateGroups[date].forEach((msg, msgIndex) => { // msgIndex is available
                    const entryDiv = document.createElement('div');

                    // const isSelfMessage = false; // All messages are treated as incoming
                    // const isSystemMessage = msgIndex === 0 && Math.random() > 0.7; // REMOVED: Logic for displaying encryption message

                    // if (isSystemMessage) { // REMOVED: Block for displaying encryption message
                    //     entryDiv.className = 'message-entry system-message';
                    //     entryDiv.innerHTML = `系统消息: 聊天已加密，对话内容仅参与者可见`;
                    //     messagesArea.appendChild(entryDiv);
                    //     return; 
                    // }

                    // All non-system messages will use 'sender-message' style
                    entryDiv.className = 'message-entry sender-message';

                    let attachmentsHTML = '';
                    if (msg.images.length > 0 || msg.videos.length > 0) {
                        attachmentsHTML += '<div class="message-attachments">';
                        msg.images.forEach((imgDesc, idx) => {
                            if(imgDesc) attachmentsHTML += `<div class="message-attachment-item" data-media-type="image" data-media-title="图片 ${idx + 1} " data-media-text="${encodeURIComponent(imgDesc || '无详细描述')}"><i class="fas fa-image"></i> <span>图片 ${idx + 1} (点击查看)</span></div>`;
                        });
                        msg.videos.forEach((vidDesc, idx) => {
                            if(vidDesc) attachmentsHTML += `<div class="message-attachment-item" data-media-type="video" data-media-title="视频 ${idx + 1} " data-media-text="${encodeURIComponent(vidDesc || '无详细描述')}"><i class="fas fa-video"></i> <span>视频 ${idx + 1} (点击查看)</span></div>`;
                        });
                        attachmentsHTML += '</div>';
                    }

                    const messageStatus = ''; // Status indicators are not used for incoming messages in this design

                    entryDiv.innerHTML = `
                        <div class="message-entry-header">
                            <div class="message-sender-wrapper">
                                <span class="message-sender">${msg.sender || '未知'}</span>
                            </div>
                            <span class="message-timestamp">${formatTime(msg.timestamp) || '（未知时间）'}</span>
                        </div>
                        <div class="message-content-body">${msg.content ? msg.content.replace(/\n/g, '<br>') : '（无内容）'}</div>
                        ${attachmentsHTML}
                        ${messageStatus}`; 

                    messagesArea.appendChild(entryDiv);
                });
            });

            messagesArea.scrollTop = messagesArea.scrollHeight;
            switchToDetailedView();
        }

        function formatTime(timestamp) {
            if (!timestamp) return '';
            return timestamp;
        }

        function groupMessagesByDate(messages) {
            const groups = {};
            messages.forEach(msg => {
                let dateString = '今天'; 
                if (msg.timestamp) {
                    const random = Math.random();
                    if (random < 0.4) dateString = '今天';
                    else if (random < 0.7) dateString = '昨天';
                    else if (random < 0.9) dateString = '上周';
                    else dateString = '更早';
                }
                if (!groups[dateString]) {
                    groups[dateString] = [];
                }
                groups[dateString].push(msg);
            });
            return groups;
        }

        function switchToListView() {
            $('#conversation-list-view').style.display = 'block';
            $('#detailed-message-view').style.display = 'none';
        }
        function switchToDetailedView() {
            $('#conversation-list-view').style.display = 'none';
            $('#detailed-message-view').style.display = 'flex';
        }

        function openMediaViewer(title, text) {
            const viewer = $('.media-viewer');
            const viewerTitleEl = $('#media-viewer-title');
            const viewerTextEl = $('#media-viewer-text');
            if (!viewer || !viewerTitleEl || !viewerTextEl) {
                console.error("Media viewer elements not found for opening.");
                return;
            }
            viewerTitleEl.textContent = title;
            viewerTextEl.innerHTML = text.replace(/\n/g, '<br>');
            viewer.classList.add('active');
        }

        function handleGlobalMediaClick(event) {
            const targetMediaItem = event.target.closest('.moment-media-text-item, .message-attachment-item');
            if (targetMediaItem) {
                const title = targetMediaItem.dataset.mediaTitle || '详情';
                let textContent = '（无详细描述）';
                const encodedText = targetMediaItem.dataset.mediaText;

                if (encodedText) {
                    try {
                        textContent = decodeURIComponent(encodedText);
                    } catch (e) {
                        console.error("Error decoding mediaText:", e, "Original encoded text:", encodedText);
                        textContent = "错误：描述文本无效或已损坏。";
                    }
                }
                openMediaViewer(title, textContent);
            }
        }

        function handleMomentNavigation() {
            const prevButton = $('#prev-moment');
            const nextButton = $('#next-moment');

            if (!prevButton || !nextButton) return;

            prevButton.addEventListener('click', function() {
                if (currentMomentIndex > 0) {
                    navigateToMoment(currentMomentIndex - 1);
                }
            });

            nextButton.addEventListener('click', function() {
                if (currentMomentIndex < totalMoments - 1) {
                    navigateToMoment(currentMomentIndex + 1);
                }
            });
        }

        function navigateToMoment(index) {
            const momentContainer = $('.moments-container');
            const prevButton = $('#prev-moment');
            const nextButton = $('#next-moment');
            const momentCounter = $('#moment-counter');

            if (!momentContainer || !prevButton || !nextButton || !momentCounter) return;

            // Hide current post
            const currentPost = momentContainer.querySelector('.moment-post.active');
            if (currentPost) {
                currentPost.classList.remove('active');
            }

            // Show new post
            const newPost = momentContainer.querySelector(`.moment-post[data-post-id="post-${index}"]`);
            if (newPost) {
                newPost.classList.add('active');
                currentMomentIndex = index;

                // Update counter
                momentCounter.textContent = `${index + 1} / ${totalMoments}`;

                // Update button states
                prevButton.disabled = index === 0;
                nextButton.disabled = index === totalMoments - 1;
            }
        }

        function handlePanelSwitching() {
            const menuItems = $$('.menu-item');
            const panels = $$('.panel');
            const momentsDot = $('.moments-dot');
            const messagesDot = $('.messages-dot');
            menuItems.forEach(item => {
                item.addEventListener('click', function() {
                    const targetPanelId = this.dataset.panel;
                    menuItems.forEach(i => i.classList.remove('active'));
                    this.classList.add('active');
                    panels.forEach(p => {
                        p.style.display = (p.id === targetPanelId) ? 'block' : 'none';
                        if (p.id === targetPanelId) p.classList.add('active');
                        else p.classList.remove('active');
                    });
                    if (targetPanelId === 'messages') {
                        switchToListView();
                        if (messagesDot) messagesDot.style.display = 'none';
                        localStorage.setItem('tianxiang_messages_last_active_total_unread', '0');
                    }
                    if (targetPanelId === 'moments' && momentsDot) {
                        momentsDot.style.display = 'none';
                        const currentCount = $('.moments-container') ? $$('.moment-post', $('.moments-container')).length : 0;
                        localStorage.setItem('tianxiang_moments_last_seen_count', currentCount.toString());
                    }
                });
            });
            const initialActivePanel = $('.panel.active');
            panels.forEach(p => { if (p !== initialActivePanel) p.style.display = 'none'; else p.style.display = 'block'; });
            if ($('#messages') && $('#messages').classList.contains('active')) { switchToListView(); }
        }
        function handlePlayerChoiceClick() { const choicesContainer = $('.player-choices'); if (!choicesContainer) return; choicesContainer.addEventListener('click', function(event) { const choiceOption = event.target.closest('.choice-option'); if (choiceOption && !choiceOption.classList.contains('disabled')) { const choiceText = choiceOption.querySelector('.choice-text').textContent.trim(); const messageToSend = `{{user}}决定 ${choiceText}`; try { const stInput = window.parent.document.querySelector('#send_textarea, #chat_input textarea, #user_input'); const stSendButton = window.parent.document.querySelector('#send_but, #send_button, #submit_button'); if (stInput && stSendButton) { stInput.value = messageToSend; stInput.dispatchEvent(new Event('input', { bubbles: true })); stSendButton.click(); } else { throw new Error('SillyTavern input or send button not found.'); } } catch (e) { navigator.clipboard.writeText(messageToSend).then(() => { console.warn(`无法直接发送指令，已将内容复制到剪贴板：\n${messageToSend}`); }).catch(err => { console.error(`无法发送指令或复制到剪贴板。\n请手动复制：${messageToSend}`); }); } } }); }
        function handleSettingsChanges() { const settingsPanel = $('#settings'); if (!settingsPanel) return; const inputs = { fontFamily: $('#font-family-setting'), fontSize: $('#font-size-setting'), bgColor: $('#bg-color-setting'), primaryColor: $('#primary-color-setting'), contentBrightness: $('#brightness-setting'), resetBtn: $('#reset-settings-btn') }; const currentSettings = loadSettings(); UIUpdater.loadSettingsToPanel(currentSettings); Object.keys(inputs).forEach(key => { if (key === 'resetBtn' || !inputs[key]) return; inputs[key].addEventListener('input', () => { currentSettings[key] = inputs[key].value; UIUpdater.applySettings(currentSettings); saveSettings(currentSettings); }); }); if(inputs.resetBtn) { inputs.resetBtn.addEventListener('click', () => { localStorage.removeItem('tianxiang_settings'); UIUpdater.loadSettingsToPanel(defaultSettings); saveSettings(defaultSettings); }); } }
        function saveSettings(settings) { localStorage.setItem('tianxiang_settings', JSON.stringify(settings)); }
        function loadSettings() { const saved = localStorage.getItem('tianxiang_settings'); if (saved) { try { const parsed = JSON.parse(saved); const allKeysPresent = Object.keys(defaultSettings).every(key => key in parsed); if (allKeysPresent) return parsed; else { return { ...defaultSettings }; } } catch (e) { return { ...defaultSettings }; } } return { ...defaultSettings }; }
        function handleMomentsInteractions() { const momentsContainer = $('.moments-container'); if (momentsContainer) { momentsContainer.addEventListener('click', function(event) { const likeButton = event.target.closest('.like-action'); if (likeButton) { const icon = likeButton.querySelector('i'); const countSpan = likeButton.querySelector('.action-count'); let count = parseInt(countSpan.textContent) || 0; const isLiked = likeButton.dataset.liked === 'true'; if (isLiked) { icon.classList.remove('fas'); icon.classList.add('far'); likeButton.classList.remove('liked'); count = Math.max(0, count - 1); likeButton.dataset.liked = 'false'; } else { icon.classList.remove('far'); icon.classList.add('fas'); likeButton.classList.add('liked'); count++; likeButton.dataset.liked = 'true'; } countSpan.textContent = count; } }); } }

        function processInjectedText(rawText) {
            if (typeof rawText !== 'string' || !rawText.trim()) { console.warn("Received empty or invalid data."); UIUpdater.updateMainContent(null); UIUpdater.updateOptions([]); UIUpdater.updateMessagesPanel([]); UIUpdater.updateMomentsData([]); return; }
            let gameContent = rawText;
            const gameTextMatch = rawText.match(/<gametext>([\s\S]*?)<\/gametext>/i);
            if (gameTextMatch && gameTextMatch[1]) { gameContent = gameTextMatch[1]; }
            else { const overallMatch = rawText.match(/<overall>([\s\S]*?)<\/overall>/i); if (overallMatch && overallMatch[1]) { gameContent = overallMatch[1]; } }

            const mainData = DataExtractor.extractMainContent(gameContent);
            const optionsData = DataExtractor.extractOptions(gameContent);
            const chatData = DataExtractor.extractChatData(gameContent);
            const momentsData = DataExtractor.extractMomentsData(gameContent);

            UIUpdater.updateMainContent(mainData);
            UIUpdater.updateOptions(optionsData);
            UIUpdater.updateMessagesPanel(chatData);
            UIUpdater.updateMomentsData(momentsData);

            const momentsDot = $('.moments-dot');
            const momentsMenuItem = $('.menu-item[data-panel="moments"]');
            if (momentsDot && momentsMenuItem && momentsData && momentsData.length > 0) {
                const currentMomentsCount = momentsData.length;
                const lastSeenCount = parseInt(localStorage.getItem('tianxiang_moments_last_seen_count') || '0');
                if (currentMomentsCount > lastSeenCount && !momentsMenuItem.classList.contains('active')) {
                    momentsDot.style.display = 'inline-block';
                } else {
                    momentsDot.style.display = 'none';
                    if (momentsMenuItem.classList.contains('active')) {
                        localStorage.setItem('tianxiang_moments_last_seen_count', currentMomentsCount.toString());
                    }
                }
            } else if (momentsDot) { momentsDot.style.display = 'none'; }
        }

        function handleDarkModeToggle() {
            const toggleBtn = $('#dark-mode-toggle');
            if (!toggleBtn) return;

            toggleBtn.addEventListener('click', function() {
                const isDark = document.documentElement.classList.toggle('dark');
                const icon = toggleBtn.querySelector('i');

                if (isDark) {
                    icon.className = 'fas fa-sun';
                } else {
                    icon.className = 'fas fa-moon';
                }
            });
        }

        function addRippleEffect() {
            document.addEventListener('click', function(e) {
                const target = e.target.closest('.menu-item, .choice-option, .reset-button, .moment-action, .moment-nav-button');
                if (!target) return;

                const rect = target.getBoundingClientRect();
                const x = e.clientX - rect.left;
                const y = e.clientY - rect.top;

                const ripple = document.createElement('span');
                ripple.className = 'ripple-effect';
                ripple.style.cssText = `
                    position: absolute;
                    top: ${y}px;
                    left: ${x}px;
                    transform: translate(-50%, -50%);
                    width: 0;
                    height: 0;
                    background: rgba(255, 255, 255, 0.4);
                    border-radius: 50%;
                    pointer-events: none;
                    z-index: 10;
                    animation: ripple 0.6s linear;
                `;

                target.style.position = target.style.position || 'relative';
                target.style.overflow = 'hidden';
                target.appendChild(ripple);

                setTimeout(() => {
                    ripple.remove();
                }, 600);
            });

            const style = document.createElement('style');
            style.textContent = `
                @keyframes ripple {
                    0% {
                        width: 0;
                        height: 0;
                        opacity: 0.5;
                    }
                    100% {
                        width: 300px;
                        height: 300px;
                        opacity: 0;
                    }
                }

                .dark .ripple-effect {
                    background: rgba(255, 255, 255, 0.2);
                }
            `;
            document.head.appendChild(style);
        }

        function init() {
            checkDarkMode();
            const savedSettings = loadSettings();
            UIUpdater.loadSettingsToPanel(savedSettings);
            handlePanelSwitching();
            handleSettingsChanges();
            handlePlayerChoiceClick();
            handleMomentsInteractions();
            handleDarkModeToggle();
            handleMomentNavigation();
            addRippleEffect();

            $('#back-to-list-btn').addEventListener('click', switchToListView);
            document.body.addEventListener('click', handleGlobalMediaClick);

            const viewer = $('.media-viewer');
            const closeBtn = viewer ? viewer.querySelector('.viewer-close') : null;
            if (closeBtn) closeBtn.addEventListener('click', () => viewer.classList.remove('active'));
            if (viewer) viewer.addEventListener('click', (e) => { if (e.target === viewer) viewer.classList.remove('active'); });
            document.addEventListener('keydown', (e) => { if (e.key === 'Escape' && viewer && viewer.classList.contains('active')) { viewer.classList.remove('active'); } });

            const dataContainer = $('#injected-data-container');
            if (dataContainer) {
                const injectedRawData = dataContainer.textContent || "";
                processInjectedText(injectedRawData);
            } else {
                UIUpdater.updateMainContent(null); UIUpdater.updateOptions([]); UIUpdater.updateMessagesPanel([]); UIUpdater.updateMomentsData([]);
            }
        }
        document.addEventListener('DOMContentLoaded', init);
    })();
    </script>
</body>
</html>
```

