"use strict";
$(async () => {
    // 自定义按钮配置 - 可以按需修改
    const customButtons = [
        {
            name: "回车",           // 按钮名称
            content: "\n",         // 插入的内容
            cursorPos: "end"       // 光标位置: "start", "middle", "end" 或数字
        },
        {
            name: "引号",
            content: "\"\"",
            cursorPos: "middle"
        },
        {
            name: "括号",
            content: "()",
            cursorPos: "middle"
        }
        // 您可以按照上面的格式添加更多按钮
    ];

    // 创建监听器，等待页面元素加载
    const bodyObserver = new MutationObserver((mutations) => {
        if (debounceTimer) {
            clearTimeout(debounceTimer);
        }

        debounceTimer = setTimeout(() => {
            checkAndAddButtons();
            debounceTimer = null;
        }, 300);
    });

    let debounceTimer = null;

    // 开始监听整个document
    bodyObserver.observe(document, {
        childList: true,
        subtree: true,
        attributes: false,
        characterData: false
    });

    // 初始检查
    checkAndAddButtons();

    function checkAndAddButtons() {
        const $qrBar = $("#qr--bar");

        if ($qrBar.length > 0) {
            let $customContainer = $qrBar.find("#custom_buttons_container");

            if ($customContainer.length === 0) {
                const $topLevelButtons = $qrBar.children(".qr--buttons");
                const isConsolidatedMode = $topLevelButtons.length === 1;

                if (isConsolidatedMode) {
                    // 合并模式：在现有按钮容器中添加
                    customButtons.forEach(button => {
                        addCustomButton($topLevelButtons, button);
                    });
                } else {
                    // 非合并模式：创建新的按钮容器
                    const $newContainer = $(`<div id="custom_buttons_container" class="qr--buttons qr--color" style="--qr--color: rgba(0, 0, 0, 0);"></div>`);
                    $qrBar.append($newContainer);

                    customButtons.forEach(button => {
                        addCustomButton($newContainer, button);
                    });
                }
            }
        }
    }

    function addCustomButton($container, button) {
        const buttonId = `input_${button.name.replace(/\s+/g, '_').toLowerCase()}_btn`;

        if ($(`#${buttonId}`).length > 0) {
            return;
        }

        const $button = $(`<div id="${buttonId}" class="qr--button menu_button interactable" title="${button.name}" tabindex="0">
            <div class="qr--button-icon fa-solid qr--hidden"></div>
            <div class="qr--button-label">${button.name}</div>
            <div class="qr--button-expander" title="Open context menu">⋮</div>
        </div>`);

        $container.append($button);

        $button.on("click", function() {
            if (button.name === "回车") {
                insertNewLine();
            } else {
                insertContent(button.content, button.cursorPos);
            }
        });
    }

    function getMessageInput() {
        return $("#send_textarea");
    }

    function insertNewLine() {
        const textarea = getMessageInput();
        const text = textarea.val();
        const cursorPos = textarea.prop("selectionStart");

        // 查找当前行的末尾位置
        let lineEnd = text.indexOf("\n", cursorPos);
        if (lineEnd === -1) {
            lineEnd = text.length;
        }

        // 在行末插入换行符
        const newText = text.substring(0, lineEnd) + "\n" + text.substring(lineEnd);
        textarea.val(newText);

        // 设置光标位置
        setTimeout(() => {
            textarea.prop("selectionStart", lineEnd + 1);
            textarea.prop("selectionEnd", lineEnd + 1);
            textarea.focus();
        }, 0);
    }

    function insertContent(content, cursorPos) {
        const textarea = getMessageInput();
        const startPos = textarea.prop("selectionStart");
        const endPos = textarea.prop("selectionEnd");
        const text = textarea.val();

        const beforeText = text.substring(0, startPos);
        const selectedText = text.substring(startPos, endPos);
        const afterText = text.substring(endPos);

        // 处理选中文本
        let newText;
        if (selectedText && (content === "\"\"" || content === "()")) {
            const openTag = content.charAt(0);
            const closeTag = content.charAt(1);
            newText = beforeText + openTag + selectedText + closeTag + afterText;
        } else {
            newText = beforeText + content + afterText;
        }

        textarea.val(newText);

        // 计算新的光标位置
        let newCursorPos = startPos;

        if (selectedText && (content === "\"\"" || content === "()")) {
            newCursorPos = startPos + selectedText.length + 2;
        } else {
            if (cursorPos === "start") {
                newCursorPos = startPos;
            } else if (cursorPos === "middle") {
                newCursorPos = startPos + Math.floor(content.length / 2);
            } else if (cursorPos === "end") {
                newCursorPos = startPos + content.length;
            } else {
                newCursorPos = startPos + parseInt(cursorPos);
            }
        }

        // 设置光标位置
        setTimeout(() => {
            textarea.prop("selectionStart", newCursorPos);
            textarea.prop("selectionEnd", newCursorPos);
            textarea.focus();
        }, 0);
    }

    // 定期检查
    setInterval(checkAndAddButtons, 5000);
});

