'use strict';

// Constants
const SMALL_SUMMARY_PRESET = 'keminiSummary【小总结】';
const KEEP_RECENT_COUNT_VAR_NAME = 'summaryKeepRecentCount'; // Chat variable name

// Helper for error handling
var detail;
(function (detail) {
  function errorCatched(fn) {
    const onError = error => {
      console.error('[总结脚本2] 错误:', error);
      triggerSlash(`/echo severity=error (总结脚本2)${error.stack ? error.stack : error.name + ': ' + error.message}`);
    };
    return async (...args) => {
      try {
        return await fn(...args);
      } catch (error) {
        onError(error);
        return undefined;
      }
    };
  }
  detail.errorCatched = errorCatched;
})(detail || (detail = {}));

// Function to handle setting the retention count via popup
const setRetentionCount = detail.errorCatched(async () => {
  triggerSlash(`/echo severity=info [总结脚本2] 请求设置保留层数...`);
  console.log(`[总结脚本2] 开始设置保留层数...`);
  let currentCount = 1; // Default value

  console.log(`[总结脚本2] 使用的变量名: ${KEEP_RECENT_COUNT_VAR_NAME}`);

  try {
    const chatVars = await getVariables({ type: 'chat' });
    console.log(`[总结脚本2] 获取到的全部聊天变量:`, chatVars);

    const configuredValue = chatVars[KEEP_RECENT_COUNT_VAR_NAME];
    console.log(`[总结脚本2] 读取到的当前配置值:`, configuredValue, `类型:`, typeof configuredValue);

    let parsedCurrentCount = NaN;
    if (configuredValue !== null && configuredValue !== undefined) {
      parsedCurrentCount = parseInt(configuredValue);
    }

    if (!isNaN(parsedCurrentCount) && Number.isInteger(parsedCurrentCount) && parsedCurrentCount >= 0) {
      currentCount = parsedCurrentCount;
      console.log(`[总结脚本2] 使用读取并解析到的有效值作为默认值: ${currentCount}`);
    } else {
      console.log(`[总结脚本2] 读取到的值 (${configuredValue}) 无效或无法解析，使用默认值: ${currentCount}`);
    }
  } catch (varError) {
    console.warn(`[总结脚本2] 获取当前保留层数时出错（用于默认值）：`, varError);
  }

  try {
    console.log(`[总结脚本2] 显示输入对话框，默认值: ${currentCount}`);
    const userInput = await triggerSlashWithResult(
      `/input title="设置保留层数" default="${currentCount}" "请输入隐藏总结前需要保留的最近消息层数（非负整数）："`,
    );

    console.log(`[总结脚本2] 用户输入原始值:`, userInput, `类型:`, typeof userInput);

    if (userInput === null || userInput === undefined || userInput === 'false') {
      console.log('[总结脚本2] 用户取消设置保留层数。');
      triggerSlash(`/echo severity=info [总结脚本2] 已取消设置保留层数。`);
      return;
    }

    const newCount = parseInt(userInput);
    console.log(`[总结脚本2] 解析后的保留层数值:`, newCount, `类型:`, typeof newCount);

    if (isNaN(newCount) || !Number.isInteger(newCount) || newCount < 0) {
      console.error(`[总结脚本2] 无效的输入: ${userInput}。请输入一个非负整数。`);
      triggerSlash(`/echo severity=error [总结脚本2] 无效的输入: \"${userInput}\"。请输入一个非负整数。`);
      return;
    }

    console.log(`[总结脚本2] 正在设置变量 ${KEEP_RECENT_COUNT_VAR_NAME} as=string 为:`, newCount);
    // Save as string for consistency, as reading seems to return string
    await triggerSlash(`/setvar key=${KEEP_RECENT_COUNT_VAR_NAME} as=string \"${newCount}\"`);

    console.log(`[总结脚本2] 设置变量后立即尝试读取验证...`);
    try {
      const updatedVars = await getVariables({ type: 'chat' });
      const updatedValue = updatedVars[KEEP_RECENT_COUNT_VAR_NAME];
      console.log(`[总结脚本2] 设置后读取到的变量值:`, updatedValue, `类型:`, typeof updatedValue);
      // Check if the string representation matches
      if (String(updatedValue) !== String(newCount)) {
        console.warn(`[总结脚本2] 警告：设置的值 (${newCount}) 与读取到的值 (${updatedValue}) 不匹配！`);
      }
    } catch (verifyError) {
      console.error(`[总结脚本2] 验证变量设置时出错:`, verifyError);
    }

    console.log(`[总结脚本2] 成功将保留层数设置为: ${newCount}`);
    triggerSlash(`/echo severity=success [总结脚本2] 成功将保留层数设置为: ${newCount} (请重新执行\"总结\"生效)`);
  } catch (inputError) {
    console.error(`[总结脚本2] 处理保留层数输入时出错:`, inputError);
    triggerSlash(`/echo severity=error [总结脚本2] 处理保留层数输入时出错: ${inputError.message}`);
  }
});

// Main summarization logic
const runSummarization = detail.errorCatched(async () => {
  // --- Declare variables in function scope ---
  let originalPreset = null;
  let chatLorebookName = null;
  let tempHiddenRange = null;
  let keepCount = 1; // Default
  let promptMessageId = -1;
  let targetMessageId = -1;
  let summaryContent = null;
  let saveConfirmed = false;
  let baseTitle = '总结'; // Default

  const summaryType = '小总结'; // Only small summary in this script
  const targetPresetName = SMALL_SUMMARY_PRESET;

  triggerSlash(`/echo severity=info [总结脚本2] 开始执行 ${summaryType}...`);
  console.log(`[总结脚本2] 开始执行 ${summaryType} (使用预设: ${targetPresetName})...`);

  try {
    // 1. Get Initial State (Preset, Lorebook Name)
    try {
      originalPreset = await triggerSlashWithResult('/preset');
      if (!originalPreset) throw new Error('无法通过 /preset 命令获取当前预设名称。');
      console.log(`[总结脚本2] 当前预设: ${originalPreset}`);

      chatLorebookName = await getOrCreateChatLorebook();
      console.log(`[总结脚本2] 获取到聊天世界书名称: ${chatLorebookName}`);
    } catch (initialError) {
      console.error('[总结脚本2] 获取初始状态时出错:', initialError);
      triggerSlash(`/echo severity=error [总结脚本2] 获取初始状态失败: ${initialError.message}`);
      return; // Cannot proceed
    }

    // --- Logic for 小总结 (Before Preset Switch) ---
    // a. Read keepCount setting
    try {
      const chatVars = await getVariables({ type: 'chat' });
      const configuredValue = chatVars[KEEP_RECENT_COUNT_VAR_NAME];
      console.log(`[总结脚本2] 读取到的原始配置值:`, configuredValue, `类型:`, typeof configuredValue);

      let parsedCount = NaN;
      if (configuredValue !== null && configuredValue !== undefined) {
        parsedCount = parseInt(configuredValue);
      }

      if (!isNaN(parsedCount) && Number.isInteger(parsedCount) && parsedCount >= 0) {
        keepCount = parsedCount;
        console.log(`[总结脚本2] 使用读取并解析到的有效保留层数: ${keepCount}`);
      } else {
        console.log(`[总结脚本2] 读取到的值 (${configuredValue}) 无效或无法解析为非负整数，使用默认值 ${keepCount}。`);
      }
      console.log(`[总结脚本2] [调试] 最终使用的保留层数 (keepCount): ${keepCount}`);
    } catch (varError) {
      console.warn(`[总结脚本2] 读取保留层数配置时发生错误，使用默认值 ${keepCount}:`, varError);
      triggerSlash(`/echo severity=warning [总结脚本2] 读取保留层数失败，使用默认值 ${keepCount}。`);
    }

    // b. Temporarily hide the last keepCount messages
    let initialLastMessageIdBeforeHide = -1;
    try {
      initialLastMessageIdBeforeHide = await getLastMessageId();
      console.log(`[总结脚本2] 临时隐藏前最后消息 ID: ${initialLastMessageIdBeforeHide}`);
    } catch (e) {
      console.error('[总结脚本2] 获取临时隐藏前最后消息 ID 时出错:', e);
      triggerSlash(`/echo severity=error [总结脚本2] 获取临时隐藏前 ID 失败: ${e.message}`);
      initialLastMessageIdBeforeHide = -1;
    }

    if (keepCount > 0 && initialLastMessageIdBeforeHide >= 0) {
      const tempHideStart = Math.max(0, initialLastMessageIdBeforeHide - keepCount + 1);
      const tempHideEnd = initialLastMessageIdBeforeHide;
      if (tempHideEnd >= tempHideStart) {
        tempHiddenRange = { start: tempHideStart, end: tempHideEnd };
        console.log(
          `[总结脚本2] 小总结：暂时隐藏范围 ${tempHiddenRange.start}-${tempHiddenRange.end} 以排除在总结外。`,
        );
        triggerSlash(`/echo severity=info [总结脚本2] 正在暂时隐藏最近 ${keepCount} 条消息...`);
        try {
          await triggerSlash(`/hide ${tempHiddenRange.start}-${tempHiddenRange.end}`);
        } catch (tempHideError) {
          console.error(`[总结脚本2] 小总结：暂时隐藏消息时出错:`, tempHideError);
          triggerSlash(`/echo severity=error [总结脚本2] 暂时隐藏最新消息失败: ${tempHideError.message}`);
          tempHiddenRange = null; // Mark as failed
        }
      } else {
        console.log(
          `[总结脚本2] 小总结：无需暂时隐藏，保留层数 (${keepCount}) >= 消息总数 (${
            initialLastMessageIdBeforeHide + 1
          })。`,
        );
      }
    } else {
      console.log(`[总结脚本2] 小总结：保留层数 (${keepCount}) <= 0 或无消息或获取ID失败，不执行暂时隐藏。`);
    }
    // --- End of 小总结 Specific Logic ---

    // 2. Switch to target preset
    console.log(`[总结脚本2] 切换到预设: ${targetPresetName}`);
    triggerSlash(`/echo severity=info [总结脚本2] 正在切换到预设: ${targetPresetName}...`);
    await triggerSlash(`/preset select \"${targetPresetName}\"`);
    await new Promise(resolve => setTimeout(resolve, 1500));

    // 3. Send prompt message and get its ID
    const promptText = '开始总结'; // Only small summary prompt needed
    console.log(`[总结脚本2] 发送实际提示文本: \"${promptText}\"`);
    try {
      await triggerSlash(`/send ${promptText}`);
      promptMessageId = await getLastMessageId();
      console.log(`[总结脚本2] [Try] 成功获取提示消息 ID: ${promptMessageId}`);
      if (promptMessageId < 0) {
        throw new Error(`获取到的提示消息 ID 无效: ${promptMessageId}`);
      }
    } catch (promptError) {
      console.error('[总结脚本2] 发送提示或获取其 ID 时出错:', promptError);
      triggerSlash(`/echo severity=error [总结脚本2] 发送提示或获取其 ID 失败: ${promptError.message}`);
      throw promptError; // Jump to finally
    }

    // 4. Trigger AI and wait for signal/get response ID
    console.log(`[总结脚本2] 触发 ${summaryType} AI 回复生成...`);
    let triggerSucceeded = false;
    let finalLastMessageId = -1;
    targetMessageId = -1; // Reset
    try {
      const timeoutPromise = new Promise((_, reject) => setTimeout(() => reject(new Error('AI 回复生成超时')), 300000));
      const generationSignalPromise = eventWaitOnce(tavern_events.MESSAGE_RECEIVED).then(() => true);
      await triggerSlash(`/trigger`);
      triggerSucceeded = await Promise.race([generationSignalPromise, timeoutPromise]);

      finalLastMessageId = await getLastMessageId();
      console.log(`[总结脚本2] 获取到生成后的最后消息 ID: ${finalLastMessageId}`);

      if (promptMessageId >= 0 && finalLastMessageId > promptMessageId) {
        targetMessageId = finalLastMessageId;
        console.log(`[总结脚本2] [Try] 确认 AI 回复消息 ID: ${targetMessageId}`);
      } else {
        let warningMsg = `AI 回复 ID (${finalLastMessageId}) 未增加或无效 (提示消息 ID: ${promptMessageId})。`;
        if (triggerSucceeded && finalLastMessageId === promptMessageId) warningMsg += ' 可能为空回复。';
        else if (!triggerSucceeded) warningMsg += ' 超时或未监听到事件。';
        console.warn(`[总结脚本2] ${warningMsg}`);
        triggerSlash(`/echo severity=warning [总结脚本2] ${warningMsg}`);
      }
    } catch (genError) {
