/* Update Regex List Display */
const updateRegexListDisplay = errorCatched(async ($listContainer, searchTerm = '') => {
    if (!$listContainer || $listContainer.length === 0) return;
    $listContainer.empty().append('<p>加载中...</p>');
    const lowerCaseSearchTerm = searchTerm.toLowerCase().trim();

    try {
        const allTavernRegexes = await getFullTavernRegexes();
        const starredRegexNames = getStarredRegexFromStorage();
        $listContainer.empty();

        if (!allTavernRegexes || allTavernRegexes.length === 0) {
            $listContainer.append('<p>未在全局设置中找到任何酒馆正则表达式。</p>');
            return;
        }

        const filteredRegexes = allTavernRegexes.filter(regex => {
            const name = regex.script_name || '';
            const pattern = regex.find_regex || '';
            return lowerCaseSearchTerm === '' ||
                   name.toLowerCase().includes(lowerCaseSearchTerm) ||
                   pattern.toLowerCase().includes(lowerCaseSearchTerm);
        });

        if (filteredRegexes.length === 0) {
             $listContainer.append(`<p>没有匹配 "${searchTerm}" 的正则表达式。</p>`);
             return;
        }

        const globalRegexes = filteredRegexes.filter(regex => regex.scope === 'global');
        const localRegexes = filteredRegexes.filter(regex => regex.scope === 'character');
        let hasDisplayedGlobal = false;

        // Global Regexes
        if (globalRegexes.length > 0) {
            const starredGlobalItems = [];
            const unstarredGlobalItems = [];
            globalRegexes.forEach(regex => {
                if (starredRegexNames.includes(regex.script_name)) starredGlobalItems.push(regex);
                else unstarredGlobalItems.push(regex);
            });
            starredGlobalItems.sort((a, b) => (a.script_name || '').localeCompare(b.script_name || ''));
            unstarredGlobalItems.sort((a, b) => (a.script_name || '').localeCompare(b.script_name || ''));

            if (starredGlobalItems.length > 0) {
                $listContainer.append('<div class="regex-section-title">⭐ 已标记全局正则</div>');
                starredGlobalItems.forEach(regex => addRegexItem(regex, $listContainer, true, false));
                hasDisplayedGlobal = true;
            }
            if (starredGlobalItems.length > 0 && unstarredGlobalItems.length > 0) {
                $listContainer.append('<div class="regex-divider"></div>');
            }
            if (unstarredGlobalItems.length > 0) {
                 if (!hasDisplayedGlobal) $listContainer.append('<div class="regex-section-title">全局正则</div>');
                unstarredGlobalItems.forEach(regex => addRegexItem(regex, $listContainer, false, false));
                hasDisplayedGlobal = true;
            }
        }

        // Local Regexes
        if (localRegexes.length > 0) {
            if (hasDisplayedGlobal) $listContainer.append('<div class="regex-divider local-divider"></div>');
            $listContainer.append('<div class="regex-section-title">局部正则 (当前卡片)</div>');
            localRegexes.sort((a, b) => (a.script_name || '').localeCompare(b.script_name || ''));
            localRegexes.forEach(regex => addRegexItem(regex, $listContainer, false, true));
        }

    } catch (error) {
        $listContainer.empty().append('<p>加载正则表达式时出错。</p>');
        console.error('Combined Selector (Regex): Error updating regex list display', error);
    }
}, 'updateRegexListDisplay');

/* Create single regex item HTML */
function addRegexItem(regex, $container, isStarred, isLocal) {
    const name = regex.script_name || 'Unnamed Regex';
    const enabled = regex.enabled;
    const scope = regex.scope;

    const $item = $('<div></div>')
        .addClass(REGEX_ITEM_CLASS)
        .attr('data-regex-name', name)
        .attr('data-enabled', enabled)
        .attr('data-scope', scope);

    if (!enabled) $item.addClass(REGEX_ITEM_DISABLED_CLASS);

    const $content = $('<div></div>').addClass('regex-item-content');
    const $name = $('<span></span>').addClass('regex-item-name').text(name);
    $content.append($name);

    const $controls = $('<div></div>').addClass('regex-item-controls');
    if (!isLocal) {
        const $star = $('<span></span>')
            .addClass(REGEX_STAR_ICON_CLASS)
            .html(isStarred ? '★' : '☆')
            .attr('title', isStarred ? '取消标记' : '标记为常用');
        $controls.append($star);
    }
    $item.append($content).append($controls);
    if (isStarred && !isLocal) $item.addClass(STARRED_REGEX_CLASS);
    $container.append($item);
}

/* Update Lorebook List Display */
const updateLorebookListDisplay = errorCatched(async ($listContainer, searchTerm = '') => {
    if (!$listContainer || $listContainer.length === 0) return;
    $listContainer.empty().append('<p>加载中...</p>');
    const lowerCaseSearchTerm = searchTerm.toLowerCase().trim();

    try {
        const [allLorebooks, currentSettings] = await Promise.all([
            getLorebooksSafe(),
            getLorebookSettingsSafe()
        ]);
        const starredLorebooks = getStarredLorebooksFromStorage();
        $listContainer.empty();

        if (!allLorebooks || allLorebooks.length === 0) {
            $listContainer.append('<p>未找到世界书。</p>');
            return;
        }

        const currentGlobal = currentSettings.selected_global_lorebooks || [];
        const filteredLorebooks = allLorebooks.filter(name =>
            lowerCaseSearchTerm === '' || name.toLowerCase().includes(lowerCaseSearchTerm)
        );

        if (filteredLorebooks.length === 0) {
             $listContainer.append(`<p>没有匹配 "${searchTerm}" 的世界书。</p>`);
             return;
        }

        const starredBooks = [];
        const unstarredBooks = [];
        filteredLorebooks.forEach(name => {
            if (starredLorebooks.includes(name)) starredBooks.push(name);
            else unstarredBooks.push(name);
        });
        starredBooks.sort((a, b) => a.localeCompare(b));
        unstarredBooks.sort((a, b) => a.localeCompare(b));

        if (starredBooks.length > 0) {
            $listContainer.append('<div class="lorebook-section-title">⭐ 已标记世界书</div>');
            starredBooks.forEach(name => addLorebookItem(name, $listContainer, currentGlobal, true));
        }
        if (starredBooks.length > 0 && unstarredBooks.length > 0) {
            $listContainer.append('<div class="lorebook-divider"></div>');
        }
        if (unstarredBooks.length > 0) {
             if (starredBooks.length === 0) {
                 // Optionally add title: $listContainer.append('<div class="lorebook-section-title">全部世界书</div>');
             }
            unstarredBooks.forEach(name => addLorebookItem(name, $listContainer, currentGlobal, false));
        }

    } catch (error) {
        $listContainer.empty().append('<p>加载世界书时出错。</p>');
        console.error('Combined Selector (Lorebook): Error updating lorebook list display', error);
        if (typeof triggerSlash === 'function') {
            triggerSlash(`echo severity=error Error loading lorebooks.`);
        }
    }
}, 'updateLorebookListDisplay');

/* Create single lorebook item HTML */
function addLorebookItem(name, $container, selectedLorebooks, isStarred) {
    const $item = $('<div></div>')
        .addClass(LOREBOOK_ITEM_CLASS)
        .attr('data-lorebook-name', name);

    const $content = $('<div></div>').addClass('lorebook-item-content').text(name);
    const $controls = $('<div></div>').addClass('lorebook-item-controls');
    const $star = $('<span></span>')
        .addClass(LOREBOOK_STAR_ICON_CLASS)
        .html(isStarred ? '★' : '☆')
        .attr('title', isStarred ? '取消标记' : '标记为常用');
    $controls.append($star);
    $item.append($content).append($controls);

    if (selectedLorebooks.includes(name)) $item.addClass(SELECTED_LOREBOOK_CLASS);
    if (isStarred) $item.addClass(STARRED_LOREBOOK_CLASS);
    $container.append($item);
}

/* Toggle Combined Panel Display */
const toggleCombinedPanel = errorCatched(async () => {
    const parentDoc = window.parent.document;
    const $panel = $(`#${COMBINED_PANEL_ID}`, parentDoc);
    const $button = $(`#${COMBINED_BUTTON_ID}`, parentDoc);

    if (!$panel.length) return;

    if ($panel.is(':visible')) {
        $panel.hide();
        if ($button.length) $button.removeClass('active');
    } else {
        // Determine active tab (or default to regex)
        const $activeTab = $panel.find(`.tab-button.${ACTIVE_TAB_CLASS}`);
        const activeTabId = $activeTab.length ? $activeTab.attr('id') : REGEX_TAB_ID;

        // Clear search and update list for the active tab
        const $activeContent = $panel.find(`#${activeTabId === REGEX_TAB_ID ? REGEX_CONTENT_ID : LOREBOOK_CONTENT_ID}`);
        const $searchInput = $activeContent.find(`.${activeTabId === REGEX_TAB_ID ? REGEX_SEARCH_INPUT_CLASS : LOREBOOK_SEARCH_INPUT_CLASS}`);
        const $list = $activeContent.find(`.${activeTabId === REGEX_TAB_ID ? REGEX_LIST_CLASS : LOREBOOK_LIST_CLASS}`);

        if ($searchInput.length) $searchInput.val('');
        if ($list.length) {
             try {
                 if (activeTabId === REGEX_TAB_ID) {
                     await updateRegexListDisplay($list);
                 } else {
                     await updateLorebookListDisplay($list);
                 }
             } catch (e) { console.error("Combined Selector: Failed to update list on panel toggle."); }
        }

        $panel.show();
        if ($button.length) $button.addClass('active');
        // Only focus search input on non-mobile devices
        if ($searchInput.length && !isMobile()) {
             $searchInput.focus();
        }
    }
}, 'toggleCombinedPanel');

/* Switch Tabs */
const switchTab = errorCatched(async (tabId) => {
    const parentDoc = window.parent.document;
    const $panel = $(`#${COMBINED_PANEL_ID}`, parentDoc);
