    const $contentToShow = $panel.find(`#${contentId}`);
    $contentToShow.addClass(ACTIVE_CONTENT_CLASS);

    // Refresh content for the new tab and focus search
    const $list = $contentToShow.find(`.${tabId === REGEX_TAB_ID ? REGEX_LIST_CLASS : LOREBOOK_LIST_CLASS}`);
    const $searchInput = $contentToShow.find(`.${tabId === REGEX_TAB_ID ? REGEX_SEARCH_INPUT_CLASS : LOREBOOK_SEARCH_INPUT_CLASS}`);

    if ($searchInput.length) $searchInput.val(''); // Clear search on tab switch
    if ($list.length) {
        try {
            if (tabId === REGEX_TAB_ID) {
                await updateRegexListDisplay($list);
