    // --- API & Context Checks ---
    logDebug("脚本开始加载 (HTML 版 v2.5.5 - 美化界面)，检查环境..."); // Version updated
    if (typeof SillyTavern !== 'object' || !SillyTavern.characters || !SillyTavern.groups || typeof SillyTavern.getTokenCount !== 'function') { console.error('[TokenCheckerV2H] ST Context无效'); return; }
    if (typeof SillyTavern.callGenericPopup !== 'function' || typeof SillyTavern.POPUP_TYPE === 'undefined') { console.error('[TokenCheckerV2H] ST Popup功能(callGenericPopup)在上下文中未找到！'); return; }
    // [修改] 检查 generateRaw 是否存在
    if (typeof TavernHelper === 'undefined' || typeof TavernHelper.getCharLorebooks === 'undefined' || typeof TavernHelper.getLorebookEntries === 'undefined' || typeof TavernHelper.getLorebookSettings === 'undefined' || typeof TavernHelper.getOrCreateChatLorebook === 'undefined' || typeof TavernHelper.generateRaw === 'undefined' || typeof TavernHelper.generate === 'undefined') { console.error('[TokenCheckerV2H] TavernHelper API缺失 (需要 getCharLorebooks, getLorebookEntries, getLorebookSettings, getOrCreateChatLorebook, generate, generateRaw)'); return; }
    logDebug("SillyTavern Context, Popup, TavernHelper API 有效.");

    const parentWindow = window.parent;
    const parent$ = parentWindow.$; // 获取父窗口的jQuery引用
    const parentToastr = parentWindow.toastr;
    const callGenericPopup = SillyTavern.callGenericPopup;
    const POPUP_TYPE = SillyTavern.POPUP_TYPE;
    if (typeof parent$ !== 'function') { console.error('[TokenCheckerV2H] Parent jQuery ($) not found.'); return; } // 确保jQuery可用
    if (typeof parentToastr !== 'object') { console.warn('[TokenCheckerV2H] Parent toastr not found.'); } else { logDebug("Parent toastr 有效."); }


    // --- Helper Functions ---
    function logDebug(...args) {
        if (DEBUG_MODE) console.log('[TokenCheckerV2H]', ...args);
    }

    const escapeHtml = (unsafe) => {
        if (!unsafe) return '';
        return String(unsafe)
            .replace(/&/g, "&amp;")
            .replace(/</g, "&lt;")
            .replace(/>/g, "&gt;")
            .replace(/"/g, "&quot;")
            .replace(/'/g, "&#39;");
    };
    function getTokenCount(text) { if (!text || typeof text !== 'string') return 0; try { return SillyTavern.getTokenCount(text); } catch (error) { console.error('[TokenCheckerV2H] getTokenCount error:', error); return 0; } }

    // [新增] 辅助函数：格式化条目插入位置
    function formatPosition(position, depth) {
        const posMap = {
            'before_character_definition': '角色定义前',
            'after_character_definition': '角色定义后',
            'before_example_messages': '示例消息前',
            'after_example_messages': '示例消息后',
            'before_author_note': '作者注释前',
            'after_author_note': '作者注释后',
            'at_depth_as_system': `系统 (@D${depth ?? '?'})`,
            'at_depth_as_assistant': `助手 (@D${depth ?? '?'})`,
            'at_depth_as_user': `用户 (@D${depth ?? '?'})`
        };
        return posMap[position] || position || '未知';
    }


    // 辅助函数：获取或计算Token，处理null/undefined内容
    function getEntryTokens(entry) {
    // 如果 entry.tk 存在且是数字，直接返回
    if (entry && typeof entry.tk === 'number') {
        return entry.tk;
    }
    // 否则，使用 getTokenCount 计算 content 的 token
    // 如果 entry 或 entry.content 不存在，则视为空字符串
    return getTokenCount(entry?.content ?? '');
    }

    // [新增] 辅助函数：格式化扫描深度
    function formatScanDepth(scanDepth) {
        if (scanDepth === 'same_as_global') {
            return '全局';
        } else if (typeof scanDepth === 'number') {
            return scanDepth.toString();
        }
        return '未知';
    }

    // --- Core Data Calculation (Unchanged from v2.5.4) ---
    async function calculateTokenStats() {
        logDebug("calculateTokenStats: 开始计算 Token 统计数据...");
        const startTime = Date.now();
        const context = SillyTavern;
        const stats = {
            characterCardTokens: 0, characterName: '未知',
            totalConstantTokens: 0, totalSelectiveTokens: 0, totalVectorizedTokens: 0,
            totalTokens: 0,
            bySource: {
                primary: { c: 0, s: 0, v: 0, books: [] }, additional: { c: 0, s: 0, v: 0, books: [] },
                global: { c: 0, s: 0, v: 0, books: [] }, chat: { c: 0, s: 0, v: 0, books: [] },
            },
            byLorebook: {}, allEntries: [], // allEntries 只包含启用的
            calculationTime: 0, error: null,
        };
        try {
            // 1. Character Card Tokens
            if (context.characterId !== undefined && context.characters[context.characterId]) {
                const character = context.characters[context.characterId]; stats.characterName = character.name; const fields = [character.description, character.personality, character.scenario, character.first_mes]; stats.characterCardTokens = getTokenCount(fields.filter(Boolean).join('\n'));
            } else if (context.groupId) { const group = context.groups.find(g => g.id === context.groupId); if (group) { stats.characterName = group.name || `Group ${context.groupId}`; stats.characterCardTokens = 0; for (const memberAvatar of group.members) { const memberData = context.characters.find(c => c.avatar === memberAvatar); if (memberData) { const fields = [memberData.description, memberData.personality, memberData.scenario, memberData.first_mes]; stats.characterCardTokens += getTokenCount(fields.filter(Boolean).join('\n')); } } } }
            else { stats.characterName = '(无角色/群组)'; }

            // 2. Gather Lorebooks
            const lorebooksToProcess = new Map(); const settings = await TavernHelper.getLorebookSettings(); let charLorebooks = { primary: null, additional: [] }; if (context.characterId !== undefined) { try { charLorebooks = await TavernHelper.getCharLorebooks(); } catch (e) { logDebug("获取角色世界书出错:", e); } } if (charLorebooks.primary) lorebooksToProcess.set(charLorebooks.primary, 'primary'); charLorebooks.additional.forEach(book => { if (!lorebooksToProcess.has(book)) lorebooksToProcess.set(book, 'additional'); }); settings.selected_global_lorebooks.forEach(book => { if (!lorebooksToProcess.has(book)) lorebooksToProcess.set(book, 'global'); }); try { const chatBook = await TavernHelper.getOrCreateChatLorebook(); if (chatBook && !lorebooksToProcess.has(chatBook)) { lorebooksToProcess.set(chatBook, 'chat'); } } catch (e) { logDebug("获取聊天世界书出错:", e); }

            // 3. Process Entries (只处理启用的条目用于概览统计)
            for (const [bookName, source] of lorebooksToProcess.entries()) {
                stats.byLorebook[bookName] = { c: 0, s: 0, v: 0, t: 0, src: source, entries: [] }; // entries 存储启用的详情
                try {
                    const entries = await TavernHelper.getLorebookEntries(bookName); // 获取所有条目，但只统计启用的
                    let processedCount = 0;
                    for (const entry of entries) {
                        if (!entry.enabled) continue; // 跳过禁用的条目进行统计

                        const entryTokens = getTokenCount(entry.content);
                        //if (entryTokens === 0 && !(entry.comment && entry.comment.trim())) continue; // 允许0 token条目存在于统计中，但可能不计入token总数

                        let entryType;
                        if (entry.type === 'constant') entryType = 'c';
                        else if (entry.type === 'selective') entryType = 's';
                        else if (entry.type === 'vectorized') entryType = 'v';
                        else { logDebug(`未知条目类型 '${entry.type}' for UID ${entry.uid} in book '${bookName}'. Treating as selective.`); entryType = 's'; }

                        if (entryType === 'c') { stats.totalConstantTokens += entryTokens; stats.bySource[source].c += entryTokens; stats.byLorebook[bookName].c += entryTokens; }
                        else if (entryType === 's') { stats.totalSelectiveTokens += entryTokens; stats.bySource[source].s += entryTokens; stats.byLorebook[bookName].s += entryTokens; }
                        else if (entryType === 'v') { stats.totalVectorizedTokens += entryTokens; stats.bySource[source].v += entryTokens; stats.byLorebook[bookName].v += entryTokens; }
                        stats.byLorebook[bookName].t += entryTokens;

                        // 将启用的条目详情存入 stats.byLorebook[bookName].entries (用于可能的快速预览，但详情页会重新获取)
                        const entryDetail = { name: entry.comment || `(UID: ${entry.uid})`, type: entryType, tk: entryTokens };
                        stats.byLorebook[bookName].entries.push(entryDetail);
                        // 将启用的条目详情存入 stats.allEntries (用于AI分析)
                        stats.allEntries.push({ book: bookName, name: entryDetail.name, type: entryType, tk: entryTokens });
                        processedCount++;
                    }
                    if (processedCount > 0 || stats.byLorebook[bookName].t > 0) {
                         logDebug(`calculateTokenStats:  - '${bookName}' 处理了 ${processedCount} 个有效条目。总 T: ${stats.byLorebook[bookName].t} (C: ${stats.byLorebook[bookName].c}, S: ${stats.byLorebook[bookName].s}, V: ${stats.byLorebook[bookName].v})`);
                         if (!stats.bySource[source].books.includes(bookName)) { stats.bySource[source].books.push(bookName); }
                    } else {
                         // 如果一个书没有任何启用的条目且总token为0，可以考虑从概览中移除
                         // delete stats.byLorebook[bookName];
                         // const bookIndex = stats.bySource[source].books.indexOf(bookName);
                         // if (bookIndex > -1) { stats.bySource[source].books.splice(bookIndex, 1); }
                         logDebug(`calculateTokenStats: - '${bookName}' 无启用的有效条目，但保留在列表中。`);
                         if (!stats.bySource[source].books.includes(bookName)) { stats.bySource[source].books.push(bookName); } // 仍然记录书名
                    }
                } catch (e) { console.error(`处理世界书 '${bookName}' 时出错:`, e); delete stats.byLorebook[bookName]; }
            }

            // 4. Final Calc
            stats.totalTokens = stats.characterCardTokens + stats.totalConstantTokens + stats.totalSelectiveTokens + stats.totalVectorizedTokens;
            logDebug(`calculateTokenStats: 最终计算: 总 Tokens = ${stats.characterCardTokens} (角色) + ${stats.totalConstantTokens} (蓝灯) + ${stats.totalSelectiveTokens} (绿灯) + ${stats.totalVectorizedTokens} (向量) = ${stats.totalTokens}`);
        } catch (error) { console.error('[TokenCheckerV2H] calculateTokenStats error:', error); stats.error = error.message || '未知计算错误'; }
        finally {
            stats.calculationTime = Date.now() - startTime;
            logDebug(`Token 统计计算完成，耗时: ${stats.calculationTime}ms`, stats);
        }

        // 更新全局变量stats
        return stats;
    }

