    // Token分布饼图 - 使用HTML/CSS代替Chart.js
    function renderTokenPieChart(stats) {
        // 计算总的世界书Token和百分比 (基于启用的条目)
        const lorebookTotal = stats.totalConstantTokens + stats.totalSelectiveTokens + stats.totalVectorizedTokens;
        if (lorebookTotal === 0) return '<div style="text-align: center; padding: 40px; color: var(--text-secondary);">(无启用的世界书Token数据)</div>';

        const constPercent = ((stats.totalConstantTokens / lorebookTotal) * 100).toFixed(1);
        const selectPercent = ((stats.totalSelectiveTokens / lorebookTotal) * 100).toFixed(1);
        const vectPercent = ((stats.totalVectorizedTokens / lorebookTotal) * 100).toFixed(1);

        // 构建CSS conic-gradient
        let gradientString = 'conic-gradient(';
        let currentPercent = 0;

        if (parseFloat(constPercent) > 0) {
            gradientString += `${COLOR_CONSTANT} 0% ${constPercent}%`;
            currentPercent = parseFloat(constPercent);
        }

        if (parseFloat(selectPercent) > 0) {
            if (currentPercent > 0) gradientString += ', ';
            gradientString += `${COLOR_SELECTIVE} ${currentPercent}% ${currentPercent + parseFloat(selectPercent)}%`;
            currentPercent += parseFloat(selectPercent);
        }

        if (parseFloat(vectPercent) > 0) {
            if (currentPercent > 0) gradientString += ', ';
            gradientString += `${COLOR_VECTORIZED} ${currentPercent}% 100%`;
        }

        gradientString += ')';

        return `
        <div style="display: flex; align-items: center; justify-content: center; height: 100%;">
            <div id="token-pie-chart" class="token-pie-chart" style="width: 130px; height: 130px; border-radius: 50%; background: ${gradientString}; border: 1px solid var(--border-color); position: relative; cursor: pointer;"
                data-constant="${stats.totalConstantTokens}"
                data-selective="${stats.totalSelectiveTokens}"
                data-vectorized="${stats.totalVectorizedTokens}"
                data-const-percent="${constPercent}"
                data-select-percent="${selectPercent}"
                data-vect-percent="${vectPercent}">
                <!-- 悬停tooltip -->
                <div class="pie-tooltip" style="position: absolute; top: -40px; left: 50%; transform: translateX(-50%); background: rgba(0,0,0,0.8); color: white; padding: 5px 10px; border-radius: 4px; font-size: 0.85rem; opacity: 0; transition: opacity 0.3s; pointer-events: none; white-space: nowrap; z-index: 10;">
                    点击查看详细分布
                </div>
            </div>
            <div style="margin-left: 20px;">
                <div style="display: flex; align-items: center; margin-bottom: 8px;">
                    <div style="width: 12px; height: 12px; background-color: ${COLOR_CONSTANT}; margin-right: 8px; border-radius: 2px;"></div>
                    <span>蓝灯: ${constPercent}%</span>
                </div>
                <div style="display: flex; align-items: center; margin-bottom: 8px;">
                    <div style="width: 12px; height: 12px; background-color: ${COLOR_SELECTIVE}; margin-right: 8px; border-radius: 2px;"></div>
                    <span>绿灯: ${selectPercent}%</span>
                </div>
                <div style="display: flex; align-items: center;">
                    <div style="width: 12px; height: 12px; background-color: ${COLOR_VECTORIZED}; margin-right: 8px; border-radius: 2px;"></div>
                    <span>向量: ${vectPercent}%</span>
                </div>
            </div>
        </div>
        `;
    }

    // 来源分布条形图 - 使用HTML/CSS代替Chart.js
    function renderSourceBarChart(stats) {
        const sourceData = [
            { name: '主书', key: 'primary', color: STYLE_SOURCE_PRIMARY },
            { name: '全局', key: 'global', color: STYLE_SOURCE_GLOBAL },
            { name: '聊天', key: 'chat', color: STYLE_SOURCE_CHAT },
            { name: '附加', key: 'additional', color: STYLE_SOURCE_ADDITIONAL }
        ];

        // 找出最大值以便缩放 (基于启用的条目)
        let maxValue = 0;
        sourceData.forEach(source => {
            const data = stats.bySource[source.key];
            const total = data.c + data.s + data.v;
            if (total > maxValue) maxValue = total;
        });

        if (maxValue === 0) return '<div style="text-align: center; padding: 40px; color: var(--text-secondary);">(无来源数据)</div>';

        let barsHtml = '';

        sourceData.forEach(source => {
            const data = stats.bySource[source.key];
            const totalSourceTokens = data.c + data.s + data.v;
            // 计算高度，确保有值时至少有最小高度
            const minHeightPercent = 5; // 最小高度百分比
            const constHeight = data.c > 0 ? Math.max(minHeightPercent, (data.c / maxValue) * 100) : 0;
            const selectHeight = data.s > 0 ? Math.max(minHeightPercent, (data.s / maxValue) * 100) : 0;
            const vectHeight = data.v > 0 ? Math.max(minHeightPercent, (data.v / maxValue) * 100) : 0;

            // 防止堆叠高度超过100% (虽然理论上不会，但保险起见)
            const totalHeight = constHeight + selectHeight + vectHeight;
            const scaleFactor = totalHeight > 100 ? 100 / totalHeight : 1;

            const scaledConstHeight = constHeight * scaleFactor;
            const scaledSelectHeight = selectHeight * scaleFactor;
            const scaledVectHeight = vectHeight * scaleFactor;


            barsHtml += `
            <div class="source-bar-group" style="display: flex; flex-direction: column; align-items: center; flex: 1; cursor: default;" title="${source.name}: ${totalSourceTokens.toLocaleString()} Tokens (蓝:${data.c}, 绿:${data.s}, 黄:${data.v})">
                <div style="height: 160px; width: 50px; position: relative; margin-bottom: 10px; background-color: rgba(255,255,255,0.05); border-radius: 3px; overflow: hidden;">
                    <!-- 使用绝对定位，从底部向上堆叠 -->
                    ${data.c > 0 ? `<div class="source-bar" style="position: absolute; bottom: 0; left: 0; height: ${scaledConstHeight}%; width: 100%; background-color: ${COLOR_CONSTANT}; animation: growUp 1s forwards cubic-bezier(0.34, 1.56, 0.64, 1);" title="蓝灯: ${data.c.toLocaleString()}"></div>` : ''}
                    ${data.s > 0 ? `<div class="source-bar" style="position: absolute; bottom: ${scaledConstHeight}%; left: 0; height: ${scaledSelectHeight}%; width: 100%; background-color: ${COLOR_SELECTIVE}; animation: growUp 1s forwards cubic-bezier(0.34, 1.56, 0.64, 1);" title="绿灯: ${data.s.toLocaleString()}"></div>` : ''}
                    ${data.v > 0 ? `<div class="source-bar" style="position: absolute; bottom: ${scaledConstHeight + scaledSelectHeight}%; left: 0; height: ${scaledVectHeight}%; width: 100%; background-color: ${COLOR_VECTORIZED}; animation: growUp 1s forwards cubic-bezier(0.34, 1.56, 0.64, 1);" title="向量: ${data.v.toLocaleString()}"></div>` : ''}
                </div>
                <div class="source-bar-label" style="font-size: 0.9rem; ${source.color} font-weight: bold;">${source.name}</div>
                <div class="source-bar-value" style="font-size: 0.8rem; color: #aaa;">${totalSourceTokens.toLocaleString()}</div>
            </div>
            `;
        });

        return `
        <div style="display: flex; justify-content: space-around; align-items: flex-end; height: 100%;">
            ${barsHtml}
        </div>
        <div style="display: flex; justify-content: center; margin-top: 10px; gap: 15px;">
            <div style="display: flex; align-items: center;">
                <div style="width: 12px; height: 12px; background-color: ${COLOR_CONSTANT}; margin-right: 5px; border-radius: 2px;"></div>
                <span style="font-size: 0.8rem;">蓝灯</span>
            </div>
            <div style="display: flex; align-items: center;">
                <div style="width: 12px; height: 12px; background-color: ${COLOR_SELECTIVE}; margin-right: 5px; border-radius: 2px;"></div>
                <span style="font-size: 0.8rem;">绿灯</span>
            </div>
            <div style="display: flex; align-items: center;">
                <div style="width: 12px; height: 12px; background-color: ${COLOR_VECTORIZED}; margin-right: 5px; border-radius: 2px;"></div>
                <span style="font-size: 0.8rem;">向量</span>
            </div>
        </div>
        `;
    }

    // 渲染来源项目
    function renderSourceItems(stats) {
        const sources = [
            { key: 'primary', name: '主书', colorClass: 'primary' },
            { key: 'additional', name: '附加', colorClass: 'additional' },
            { key: 'global', name: '全局', colorClass: 'global' },
            { key: 'chat', name: '聊天', colorClass: 'chat' }
        ];

        let hasData = false;
        let html = '';

        sources.forEach(source => {
            const data = stats.bySource[source.key];
            const sourceTotal = data.c + data.s + data.v; // 基于统计

            if (sourceTotal > 0 || data.books.length > 0) { // 只要有关联的书或有token就显示
                hasData = true;
                const booksDisplay = data.books.length > 0 ?
                    data.books.join(', ') : '(无)'; // 显示关联的书名
                const truncatedDisplay = booksDisplay.length > 60 ?
                    booksDisplay.substring(0, 57) + '...' : booksDisplay;

                html += `
                <div class="source-item ${source.colorClass}">
                    <div class="source-header">
                        <span class="source-name">${source.name}</span>
                        <span class="source-total">总计: ${sourceTotal.toLocaleString()}</span>
                    </div>
                    <span class="source-books" title="${escapeHtml(booksDisplay)}">包含世界书: ${escapeHtml(truncatedDisplay)}</span>
                    <div class="source-breakdown">
                        <div class="source-breakdown-item" style="background-color: rgba(75, 192, 192, 0.1); color: ${COLOR_CONSTANT};">
                            <div>蓝灯</div>
                            <div style="font-weight: bold;">${data.c.toLocaleString()}</div>
                        </div>
                        <div class="source-breakdown-item" style="background-color: rgba(144, 238, 144, 0.1); color: ${COLOR_SELECTIVE};">
                            <div>绿灯</div>
                            <div style="font-weight: bold;">${data.s.toLocaleString()}</div>
                        </div>
                        <div class="source-breakdown-item" style="background-color: rgba(255, 206, 86, 0.1); color: ${COLOR_VECTORIZED};">
                            <div>向量</div>
                            <div style="font-weight: bold;">${data.v.toLocaleString()}</div>
                        </div>
                    </div>
                </div>
                `;
            }
        });

        if (!hasData) {
            return '<p style="text-align: center; color: var(--text-secondary);">无激活的世界书来源</p>';
        }

        return html;
    }

    // 渲染世界书表格行 - 移除详情按钮列
    function renderLorebooksRows(stats) {
        const sourceTextMap = {
            primary: '主',
            additional: '附',
            global: '全',
            chat: '聊'
        };

        const sourceColorMap = {
            primary: STYLE_SOURCE_PRIMARY,
            additional: STYLE_SOURCE_ADDITIONAL,
            global: STYLE_SOURCE_GLOBAL,
            chat: STYLE_SOURCE_CHAT
        };

        if (!stats.byLorebook || Object.keys(stats.byLorebook).length === 0) {
            return '<tr><td colspan="6" style="text-align: center; color: var(--text-secondary);">无激活的世界书</td></tr>';
        }

        // 按总启用Token数排序
        const lorebooks = Object.entries(stats.byLorebook).sort(([,a], [,b]) => b.t - a.t);
        let html = '';

        lorebooks.forEach(([name, data]) => {
            const sourceText = sourceTextMap[data.src] || data.src;
            const sourceStyle = sourceColorMap[data.src] || ''; // 获取颜色样式

            const displayName = name.length > 30 ? name.substring(0, 27) + '...' : name;

            html += `
            <tr class="lorebook-row" data-lorebook="${escapeHtml(name)}" style="cursor: pointer;" title="双击查看 '${escapeHtml(name)}' 的详细条目">
                <td title="${escapeHtml(name)}">${escapeHtml(displayName)}</td>
                <td style="${sourceStyle} font-weight: bold;" title="${sourceTextMap[data.src] || data.src}">${sourceText}</td>
                <td style="${STYLE_CONSTANT_TEXT}">${data.c.toLocaleString()}</td>
                <td style="${STYLE_SELECTIVE_TEXT}">${data.s.toLocaleString()}</td>
                <td style="${STYLE_VECTORIZED_TEXT}">${data.v.toLocaleString()}</td>
                <td style="font-weight: bold;">${data.t.toLocaleString()}</td>
                <!-- <td>
                    <button class="btn btn-outline view-entries-button" data-lorebook="${escapeHtml(name)}" style="padding: 2px 6px; font-size: 0.8em;">详情</button>
                </td> --> <!-- 移除详情按钮列 -->
            </tr>
            `;
        });

        return html;
    }

    // [修改] 条目详情HTML表格生成函数 (实现所有要求)
    function renderEntryDetailsHtml(allEntries, lorebookName) {
        logDebug(`renderEntryDetailsHtml: 开始为 '${lorebookName}' 渲染 ${allEntries.length} 个条目详情`);
        if (!allEntries || allEntries.length === 0) {
            return '<div style="text-align: center; padding: 20px; color: var(--text-secondary);">(该世界书没有条目)</div>';
        }

        // 1. 分离和排序条目 (要求 2)
        const enabledEntries = allEntries.filter(entry => entry.enabled);
        const disabledEntries = allEntries.filter(entry => !entry.enabled);

        // 按 Tokens 降序排序
        const sortByTokensDesc = (a, b) => getEntryTokens(b) - getEntryTokens(a);
        enabledEntries.sort(sortByTokensDesc);
        disabledEntries.sort(sortByTokensDesc);

        const sortedEntries = [...enabledEntries, ...disabledEntries];

        // 2. 计算页脚统计数据 (要求 1)
        let totalCount = allEntries.length;
        let enabledCount = enabledEntries.length;
        let disabledCount = disabledEntries.length;
        let totalEnabledTokens = 0;
        let enabledConstantCount = 0;
        let enabledSelectiveCount = 0;
        let enabledVectorizedCount = 0;

        enabledEntries.forEach(entry => {
            const entryTokens = getEntryTokens(entry); // 使用辅助函数
            totalEnabledTokens += entryTokens;
            if (entry.type === 'constant') enabledConstantCount++;
            else if (entry.type === 'selective') enabledSelectiveCount++;
            else if (entry.type === 'vectorized') enabledVectorizedCount++;
        });

        // 3. 构建表格 HTML (要求 3)
        let tableHtml = `
        <div class="entry-list-container">
            <table class="entry-list-table">
                <thead>
                    <tr>
                        <th>条目名称</th>
                        <th>启用</th>
                        <th>类型</th>
                        <th>插入位置</th>
                        <th>顺序</th>
                        <th>扫描深度</th>
                        <th>Tokens</th>
                    </tr>
                </thead>
                <tbody>
        `;

        sortedEntries.forEach(entry => {
            let typeText, typeStyle;
            if (entry.type === 'constant') {
                typeText = '蓝灯'; typeStyle = STYLE_CONSTANT_TEXT;
            } else if (entry.type === 'selective') {
                typeText = '绿灯'; typeStyle = STYLE_SELECTIVE_TEXT;
            } else if (entry.type === 'vectorized') {
                typeText = '向量'; typeStyle = STYLE_VECTORIZED_TEXT;
            } else {
                typeText = '未知'; typeStyle = '';
            }

            const entryTokens = getEntryTokens(entry);
            const displayName = entry.comment || `(UID: ${entry.uid})`;
            const truncatedName = displayName.length > 35 ? displayName.substring(0, 32) + '...' : displayName;
            const isEnabled = entry.enabled;
            const rowStyle = isEnabled ? '' : 'opacity: 0.6; font-style: italic;'; // 给禁用行添加样式

            tableHtml += `
                <tr style="${rowStyle}">
                    <td class="entry-comment" title="${escapeHtml(displayName)} (UID: ${entry.uid})">${escapeHtml(truncatedName)}</td>
                    <td style="text-align: center;">${isEnabled ? '<span style="color: #90EE90;">是</span>' : '<span style="color: #FF4500;">否</span>'}</td>
                    <td style="${typeStyle}">${typeText}</td>
                    <td title="${escapeHtml(entry.position)}">${escapeHtml(formatPosition(entry.position, entry.depth))}</td>
                    <td style="text-align: right;">${entry.order ?? '-'}</td>
                    <td style="text-align: right;">${formatScanDepth(entry.scan_depth)}</td>
                    <td style="text-align: right;">${entryTokens.toLocaleString()}</td>
                </tr>
            `;
        });

        tableHtml += `
                </tbody>
            </table>
        </div>
        `;

        // 4. 构建页脚 HTML (要求 1)
        const footerHtml = `
        <div style="text-align: center; margin-top: 15px; padding-top: 10px; border-top: 1px solid var(--border-color); display: flex; justify-content: space-around; flex-wrap: wrap; gap: 5px 15px; font-size: 0.85em; color: var(--text-secondary);">
            <span>共 ${totalCount} 条目</span>
            <span>已启用 ${enabledCount}</span>
            <span>未启用 ${disabledCount}</span>
            <span>有效 Tokens ${totalEnabledTokens.toLocaleString()}</span>
            <span style="${STYLE_CONSTANT_TEXT}">蓝灯 ${enabledConstantCount}</span>
            <span style="${STYLE_SELECTIVE_TEXT}">绿灯 ${enabledSelectiveCount}</span>
            <span style="${STYLE_VECTORIZED_TEXT}">向量 ${enabledVectorizedCount}</span>
        </div>
        `;

        return tableHtml + footerHtml; // 组合表格和页脚
    }

    // 新增一个AI编辑按钮到条目详情页面
    function addAiEditButton(entryDetailsSection, lorebookName) {
        logDebug(`addAiEditButton: 为 ${lorebookName} 添加AI编辑按钮`);

        // 先移除可能存在的旧按钮容器
        entryDetailsSection.find('.card-header .buttons-container').remove();

        // 为卡片标题栏添加AI编辑按钮
        const aiEditButton = parent$(`<button class="btn btn-primary" id="ai-edit-entries-button" style="margin-right: 10px;">
            <i class="fas fa-magic" style="margin-right: 5px;"></i>AI 编辑条目
        </button>`);

        // 获取卡片标题栏和返回按钮
        const cardHeader = entryDetailsSection.find('.card-header');
        const backButton = cardHeader.find('#back-to-lorebook-list');

        // 把返回按钮移除（如果它还在那里）
        backButton.detach();

        // 创建一个包装器来包含两个按钮
        const buttonsContainer = parent$('<div class="buttons-container" style="display: flex; align-items: center;"></div>'); // 添加 class
        buttonsContainer.append(aiEditButton);
        buttonsContainer.append(backButton); // 重新添加返回按钮

        // 放回到卡片标题栏
        cardHeader.append(buttonsContainer);

        // 保存当前世界书名称到全局变量
        currentViewingLorebook = lorebookName;
        logDebug(`已设置currentViewingLorebook为: ${currentViewingLorebook}`);

        // 绑定点击事件
        aiEditButton.off('click').on('click', function(e) { // 使用 .off().on() 避免重复绑定
            e.preventDefault();
            e.stopPropagation();
            logDebug("AI编辑按钮被点击");
            openAiEditInterface();
        });
    }

    // 打开AI编辑界面
    async function openAiEditInterface() {
        try {
            logDebug(`openAiEditInterface: 尝试打开编辑界面，当前世界书: ${currentViewingLorebook}`);

            if (!currentViewingLorebook) {
                logDebug("错误: currentViewingLorebook未定义");
                parentToastr?.error('无法识别当前世界书');
                return;
            }

            // 获取最新的完整条目数据用于编辑界面显示
            let currentEntriesForEdit;
            try {
                currentEntriesForEdit = await TavernHelper.getLorebookEntries(currentViewingLorebook);
            } catch (fetchError) {
                 logDebug(`获取 '${currentViewingLorebook}' 条目失败:`, fetchError);
                 parentToastr?.error(`获取世界书 "${currentViewingLorebook}" 的条目数据失败`);
                 return;
            }

            const lorebookName = currentViewingLorebook;
            logDebug(`加载世界书数据: ${lorebookName}, 条目数: ${currentEntriesForEdit.length}`);

            // 创建编辑界面HTML
            const editInterfaceHTML = `
            <div class="ai-edit-container" style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px;">
                <div class="edit-input-area">
                    <h3>编辑指令</h3>
                    <textarea id="ai-edit-input" rows="4" placeholder="例如：'将所有蓝灯条目改为绿灯并添加关键词'或'优化ID为3的条目内容使其更简洁'"
                        class="form-control" style="width: 100%; margin-bottom: 15px;"></textarea>
                    <button id="generate-edits-button" class="btn btn-primary">生成编辑建议</button>

                    <div class="current-entries" style="margin-top: 20px; max-height: 400px; overflow-y: auto;">
                        <h4>当前条目 (${currentEntriesForEdit.length})</h4>
                        <div class="edit-entries-list">
                            ${currentEntriesForEdit.map(entry => {
                                const entryTokens = getEntryTokens(entry); // 使用辅助函数
                                return `
                                <div class="entry-item" style="margin-bottom: 5px; padding: 5px; border-bottom: 1px solid var(--border-color-subtle);">
                                    <div><strong>${escapeHtml(entry.comment || `UID: ${entry.uid}`)}</strong> (UID: ${entry.uid})</div>
                                    <div style="color: var(--text-secondary); font-size: 0.85em;">
                                        启用: ${entry.enabled ? '<span style="color:#90EE90">是</span>' : '<span style="color:#FF4500">否</span>'} |
                                        类型: <span style="color: ${entry.type === 'constant' ? COLOR_CONSTANT : (entry.type === 'selective' ? COLOR_SELECTIVE : COLOR_VECTORIZED)}">
                                            ${entry.type === 'constant' ? '蓝灯' : (entry.type === 'selective' ? '绿灯' : '向量')}
                                        </span> |
                                        Tokens: ${entryTokens}
                                    </div>
                                </div>
                            `}).join('')}
                        </div>
                    </div>
                </div>
                <div class="edit-preview-area">
                    <h3>编辑预览</h3>
                    <div id="ai-edit-preview" style="background: var(--bg-dark); padding: 15px; border-radius: 8px; height: 400px; overflow-y: auto;">
                        <p class="text-muted">AI生成的编辑建议将显示在这里...</p>
                    </div>
                    <div style="text-align: right; margin-top: 15px;">
                        <button id="cancel-edits-button" class="btn btn-outline" style="margin-right: 8px;">取消</button>
                        <button id="apply-edits-button" class="btn btn-primary" disabled>应用修改</button>
                    </div>
                </div>
            </div>
            `;

            // 显示编辑界面
            callGenericPopup(editInterfaceHTML, POPUP_TYPE.DISPLAY, `AI编辑 - ${lorebookName}`, { wide: true, large: true });

            // 绑定事件处理
            parent$('#generate-edits-button').off('click').on('click', async function() {
                const editInstruction = parent$('#ai-edit-input').val();
                if (!editInstruction) {
                    parentToastr?.warning('请输入编辑指令');
                    return;
                }

                parent$(this).prop('disabled', true).html('<i class="fas fa-spinner fa-spin"></i> 生成中...');
                parent$('#ai-edit-preview').html('<p class="text-muted"><i class="fas fa-spinner fa-spin"></i> AI正在生成编辑建议...</p>');

                // 获取最新的条目数据用于生成编辑建议
                let entriesForGeneration;
                 try {
                     entriesForGeneration = await TavernHelper.getLorebookEntries(lorebookName);
                 } catch (fetchError) {
                     parent$('#ai-edit-preview').html(`<p style="color: red;">获取最新条目数据失败: ${fetchError.message}</p>`);
                     parent$('#generate-edits-button').prop('disabled', false).html('生成编辑建议');
                     return;
                 }

                try {
                    // [修改] 调用修改后的 generateAiEdits
                    const editPreview = await generateAiEdits(entriesForGeneration, editInstruction, lorebookName);
                    parent$('#ai-edit-preview').html(editPreview.html);
                    // 如果返回了手动输入区域，需要绑定手动解析按钮事件
                    if (parent$('#parse-manual-json').length > 0) {
                         parent$('#parse-manual-json').off('click').on('click', function() {
                             const manualJson = parent$('#manual-json-input').val();
                             try {
                                 const parsedEdits = JSON.parse(manualJson);
                                 if (Array.isArray(parsedEdits) && parsedEdits.every(e => e && typeof e === 'object' && e.uid !== undefined)) {
                                     // 验证并构建预览
                                     const mergedEditsData = validateAndMergeEdits(parsedEdits, entriesForGeneration);
                                     const previewHTML = buildEditPreviewHTML(mergedEditsData, entriesForGeneration, "手动解析JSON");
                                     parent$('#ai-edit-preview').html(previewHTML);
                                     parent$('#apply-edits-button').prop('disabled', mergedEditsData.length === 0).data('edits', mergedEditsData);
                                     parentToastr?.success("手动JSON解析成功！");
                                 } else {
                                     throw new Error("JSON格式不正确或缺少uid");
                                 }
                             } catch (parseError) {
                                 parentToastr?.error(`手动JSON解析失败: ${parseError.message}`);
                             }
                         });
                    } else {
                         parent$('#apply-edits-button').prop('disabled', !editPreview.edits || editPreview.edits.length === 0).data('edits', editPreview.edits);
                    }
                } catch (error) {
                    parent$('#ai-edit-preview').html(`<p style="color: red;">生成编辑时出错: ${error.message}</p>`);
                     parent$('#apply-edits-button').prop('disabled', true).data('edits', []); // 清空数据
                } finally {
                    parent$('#generate-edits-button').prop('disabled', false).html('生成编辑建议');
                }
            });

            parent$('#apply-edits-button').off('click').on('click', async function() {
                const editsToApply = parent$(this).data('edits');
                if (!editsToApply || !Array.isArray(editsToApply) || !editsToApply.length) {
                    parentToastr?.warning('没有可应用的修改。');
                    return;
                }

                parent$(this).prop('disabled', true).html('<i class="fas fa-spinner fa-spin"></i> 应用中...');
                parent$('#cancel-edits-button').prop('disabled', true); // 禁用取消按钮

                try {
                    await TavernHelper.setLorebookEntries(lorebookName, editsToApply);
                    parentToastr?.success(`成功应用了 ${editsToApply.length} 个条目的修改`);

                    // 关闭编辑弹窗
                    const editDialog = parent$(this).closest('dialog');
                    if (editDialog.length > 0 && typeof editDialog[0].close === 'function') {
                         editDialog[0].close('applied');
                    }

                    // 延迟一下再刷新主弹窗的条目列表
                    setTimeout(() => refreshEntryList(lorebookName), 300);

                } catch (error) {
                    parentToastr?.error(`应用修改时出错: ${error.message}`);
                    parent$('#apply-edits-button').prop('disabled', false).html('应用修改');
                     parent$('#cancel-edits-button').prop('disabled', false);
                }
            });

            parent$('#cancel-edits-button').off('click').on('click', function() {
                const editDialog = parent$(this).closest('dialog');
                 if (editDialog.length > 0 && typeof editDialog[0].close === 'function') {
                     editDialog[0].close('cancelled');
                 }
            });
        } catch (error) {
            logDebug(`openAiEditInterface错误: ${error.message}`);
            parentToastr?.error(`打开AI编辑界面时出错: ${error.message}`);
        }
    }

    // 刷新条目列表 (在主弹窗中)
    async function refreshEntryList(lorebookName) {
        logDebug(`refreshEntryList: 刷新主弹窗中 ${lorebookName} 的条目列表`);

        // 找到主弹窗的内容容器
        const mainPopupContent = parent$(`dialog[open]:not([closing]) #${POPUP_CONTENT_ID}`);
        if (mainPopupContent.length === 0) {
            logDebug("refreshEntryList: 未找到主弹窗，无法刷新。");
            return;
        }

        const entryDetailsSection = mainPopupContent.find('#entry-details-section');
        if (entryDetailsSection.is(':visible') && currentViewingLorebook === lorebookName) {
            logDebug(`refreshEntryList: 详情页可见，正在刷新 '${lorebookName}'`);
            const entryDetailsTitle = entryDetailsSection.find('#entry-details-title');
            const entryListContentArea = entryDetailsSection.find('#entry-list-content-area');

            // 显示加载状态
            entryListContentArea.html('<div style="text-align:center; padding: 20px;"><i class="fa-solid fa-spinner fa-spin"></i> 刷新条目数据中...</div>');
            entryDetailsTitle.text(`'${escapeHtml(lorebookName)}' 条目详情 (刷新中...)`);

            try {
                const allEntries = await TavernHelper.getLorebookEntries(lorebookName);
                entryListContentArea.html(renderEntryDetailsHtml(allEntries, lorebookName));
                entryDetailsTitle.text(`'${escapeHtml(lorebookName)}' 条目详情`);
                // 重新添加编辑按钮
                addAiEditButton(entryDetailsSection, lorebookName);
                logDebug(`refreshEntryList: '${lorebookName}' 刷新完成`);
            } catch (error) {
                 console.error(`刷新世界书 '${lorebookName}' 条目时出错:`, error);
                 parentToastr?.error(`刷新 '${lorebookName}' 条目失败: ${error.message}`);
                 entryListContentArea.html(`<div style="color: red; padding: 15px;">刷新条目数据失败: ${escapeHtml(error.message)}</div>`);
                 entryDetailsTitle.text(`'${escapeHtml(lorebookName)}' 条目详情 (刷新错误)`);
            }
        } else {
             logDebug(`refreshEntryList: 详情页不可见或世界书不匹配，跳过刷新。 (当前查看: ${currentViewingLorebook}, 请求刷新: ${lorebookName})`);
        }

        // 可选：同时刷新概览数据（如果应用修改可能影响概览）
        // stats = await calculateTokenStats();
        // mainPopupContent.html(createBeautifiedHtml(stats)); // 这会完全重绘，可能导致闪烁和丢失当前视图
        // 更优的方式是只更新受影响的概览卡片和列表，但这比较复杂
    }


    // 生成AI编辑建议 (Modified to send full metadata)
    async function generateAiEdits(entries, instruction, lorebookName) {
        logDebug(`generateAiEdits: 为 ${lorebookName} 生成编辑建议，指令: ${instruction.substring(0, 50)}...`);
        logDebug(`generateAiEdits: 将发送 ${entries.length} 个条目的完整元数据`);

        // [修改] 构建包含完整元数据的 Prompt
        // 注意：发送完整内容可能导致Token超标，这里我们发送所有元数据字段，内容字段仍用预览或省略
        const entriesForPrompt = entries.map(entry => {
            // 创建一个副本，移除或截断可能过长的 content 字段
            const entryCopy = { ...entry };
            if (entryCopy.content && entryCopy.content.length > 200) { // 限制内容长度，避免过大payload
                entryCopy.content_preview = entryCopy.content.substring(0, 197) + '...';
                delete entryCopy.content; // 移除原始长内容
            } else if (entryCopy.content === null || entryCopy.content === undefined) {
                 entryCopy.content = ''; // 确保 content 字段存在且为字符串
            }
            // 确保 depth 是数字或 null
             if (entryCopy.depth !== null && typeof entryCopy.depth !== 'number') {
                 entryCopy.depth = null; // 或者尝试解析 parseInt(entryCopy.depth, 10) || null;
             }
             // 确保 order 是数字
             if (typeof entryCopy.order !== 'number') {
                 entryCopy.order = parseInt(entryCopy.order, 10) || 0; // 提供默认值
             }
             // 确保 scan_depth 是数字或特定字符串
             if (entryCopy.scan_depth !== 'same_as_global' && typeof entryCopy.scan_depth !== 'number') {
                 entryCopy.scan_depth = 'same_as_global'; // 提供默认值
             }
             // 其他字段类型也应类似处理以增加健壮性...

            return entryCopy;
        });


        const prompt = `你是一个专业的世界书条目编辑助手。请根据以下指令修改世界书"${lorebookName}"中的条目:

    指令: ${instruction}

    以下是当前条目列表（JSON格式，包含完整的元数据，content字段可能被截断为content_preview）:
    \`\`\`json
    ${JSON.stringify(entriesForPrompt, null, 2)}
    \`\`\`

    你必须按以下格式返回修改后的条目数据（仅包含需要修改的条目和字段）：
    \`\`\`json
    [
      {
        "uid": 条目ID(必须保留原uid),
        "修改的字段1": "新值", // 例如: "enabled": false, "order": 10, "scan_depth": 5, "depth": 3
        "修改的字段2": "新值"
        // 只包含你需要修改的字段，不要包含未修改的字段
      },
      {
        "uid": 另一个条目ID,
        "修改的字段": "新值"
      }
    ]
    \`\`\`

    请确保：
    1. 返回的内容必须包含格式正确的JSON数组。
    2. 每个对象必须包含 "uid" 字段。
    3. 将JSON代码块放在 \`\`\`json 和 \`\`\` 之间。
    4. 在回复中必须包含一个且只有一个符合上述要求的JSON代码块。
    5. 只返回你需要修改的条目，如果某个条目没有变化，不要包含在返回的JSON中。
    6. 返回的字段值必须是有效的类型（例如，'enabled' 必须是 boolean，'key' 必须是 string[]，'order'/'depth'/'scan_depth' 必须是 number 或对应类型）。

    在返回JSON前，可以简短说明你的修改策略，但确保JSON格式正确且完整。`;

        try {
            // 检查 TavernHelper.generateRaw 是否可用
            if (typeof TavernHelper?.generateRaw !== 'function') {
                throw new Error("AI功能所需的 TavernHelper.generateRaw 未找到");
            }

            logDebug("使用 generateRaw 发送请求...");
            // 使用 generateRaw 发送纯净的指令
            const aiResponse = await TavernHelper.generateRaw({
                ordered_prompts: [
                    { role: 'user', content: prompt }
                ],
                // 可选配置: 增加允许的回复长度，因为输入的prompt变长了
                max_new_tokens: 2048, // 酌情调整
                // temperature: 0.5,
            });
            logDebug("generateRaw 请求完成，收到响应。");

            // --- JSON 提取逻辑 (保持不变) ---
            let parsedEdits = [];
            let jsonExtracted = false;
            let extractedJsonString = null;
            let extractionMethod = '';

            const jsonBlockMatch = aiResponse.match(/```(?:json)?\s*(\[[\s\S]*?\])\s*```/);
            if (jsonBlockMatch && jsonBlockMatch[1]) {
                extractedJsonString = jsonBlockMatch[1];
                try {
                    parsedEdits = JSON.parse(extractedJsonString);
                    if (Array.isArray(parsedEdits)) {
                        jsonExtracted = true;
                        extractionMethod = 'Standard Code Block';
                        logDebug("使用标准代码块格式成功提取JSON");
                    } else {
                        console.warn("标准代码块解析出的JSON不是数组:", parsedEdits);
                        parsedEdits = [];
                    }
                } catch (e) {
                    console.warn("标准代码块JSON解析失败:", e.message, "提取到的字符串:", extractedJsonString);
                }
            }

            if (!jsonExtracted) {
                const arrayMatch = aiResponse.match(/(\[[\s\S]*?\])/);
                if (arrayMatch && arrayMatch[1]) {
                    extractedJsonString = arrayMatch[1];
                    try {
                        const cleanedJsonString = extractedJsonString
                            .replace(/,\s*([\}\]])/g, '$1')
                            .replace(/(\/\*[\s\S]*?\*\/|\/\/.*$)/gm, '');
                        parsedEdits = JSON.parse(cleanedJsonString);
                         if (Array.isArray(parsedEdits)) {
                            jsonExtracted = true;
                            extractionMethod = 'Fallback Array Match';
                            logDebug("通过备选方法成功提取JSON");
                        } else {
                            console.warn("备选方法解析出的JSON不是数组:", parsedEdits);
                            parsedEdits = [];
                        }
                    } catch (e) {
                        console.warn("备选JSON提取失败:", e.message, "提取到的字符串:", extractedJsonString);
                    }
                }
            }

            // --- 处理提取结果 (手动输入部分保持不变) ---
            if (!jsonExtracted) {
                logDebug("未找到完整JSON格式，提供手动输入选项");
                console.warn("AI回复内容:", aiResponse);
                 const previewHTML = `
                <div>
                    <h4>无法自动解析AI回复</h4>
                    <p>未能从AI回复中提取有效的JSON数据。以下是AI的完整回复：</p>
                    <div style="background-color: var(--bg-dark); padding: 15px; border-radius: 8px; margin: 10px 0; max-height: 200px; overflow-y: auto; white-space: pre-wrap; font-size: 0.9em;">${escapeHtml(aiResponse)}</div>
                    <p>请尝试重新生成，或使用更清晰的指令。您也可以手动输入符合格式的JSON：</p>
                    <textarea id="manual-json-input" rows="8" style="width: 100%; background-color: var(--bg-dark); color: var(--text-primary); border: 1px solid var(--border-color); border-radius: 8px; padding: 10px; font-family: monospace;" placeholder='[{"uid": 1, "type": "selective", "comment": "New Comment", "order": 5}]'></textarea>
                    <div style="text-align: right; margin-top: 10px;">
                        <button id="parse-manual-json" class="btn btn-primary">解析手动输入的JSON</button>
                    </div>
                </div>`;
                return { html: previewHTML, edits: [] };
            }

            logDebug(`JSON 提取成功 (方法: ${extractionMethod})，解析出 ${parsedEdits.length} 个潜在修改。`);

            // --- 验证并合并编辑 (保持不变, 它会处理新增的字段) ---
            const mergedEditsData = validateAndMergeEdits(parsedEdits, entries);
            logDebug(`最终将应用的修改 (${mergedEditsData.length} 个条目):`, mergedEditsData);

            // 构建预览HTML (保持不变, 它会显示新增的字段)
            const previewHTML = buildEditPreviewHTML(mergedEditsData, entries, aiResponse);
            return { html: previewHTML, edits: mergedEditsData };

        } catch (error) {
            console.error("生成AI编辑建议出错:", error);
            return { html: `<p style="color: red;">生成编辑建议时出错: ${escapeHtml(error.message)}</p>`, edits: [] };
        }
    }

    // [新增] 验证和合并编辑的辅助函数
    function validateAndMergeEdits(parsedEdits, originalEntries) {
         if (!Array.isArray(parsedEdits)) {
             console.warn("validateAndMergeEdits: 输入的不是数组:", parsedEdits);
             return [];
         }
         const validEdits = [];
         parsedEdits.forEach(edit => {
             if (!edit || typeof edit !== 'object' || edit.uid === undefined) {
                 console.warn("过滤掉无效的编辑对象 (缺少uid或非对象):", edit);
                 return; // 跳过这个无效对象
             }
             const originalEntry = originalEntries.find(e => e.uid === edit.uid);
             if (!originalEntry) {
                 console.warn(`过滤掉无效的编辑对象 (未找到原始条目 uid: ${edit.uid}):`, edit);
                 return; // 跳过这个无效对象
             }

             // 创建一个新的对象，只包含有效的、实际发生变化的修改
             const updateData = { uid: edit.uid };
             let hasChanged = false;
             for (const key in edit) {
                 if (key !== 'uid' && Object.prototype.hasOwnProperty.call(edit, key)) {
                     // 比较新旧值 (使用JSON.stringify处理对象和数组的比较)
                     // 注意：直接比较可能因类型不匹配（如 null vs 0）而误判，但 setLorebookEntries 应该能处理
                     if (JSON.stringify(edit[key]) !== JSON.stringify(originalEntry[key])) {
                         updateData[key] = edit[key];
                         hasChanged = true;
                     }
                 }
             }
             // 只有当确实有字段发生变化时才添加到最终列表
             if (hasChanged) {
                 validEdits.push(updateData);
             } else {
                 logDebug(`编辑对象 for uid ${edit.uid} 未包含实际变化，已忽略:`, edit);
             }
         });

         if (validEdits.length === 0 && parsedEdits.length > 0) {
             console.warn("AI返回了编辑数据，但经过验证后没有有效的修改。");
         }
         return validEdits;
    }


    // --- 依赖的辅助函数 (需要放在脚本的其他地方或确保可用) ---

    // 构建编辑预览HTML (需要确保这个函数在你的脚本中定义了)
    function buildEditPreviewHTML(editsToApply, originalEntries, aiResponse = '') {
        let summary = '';
        if (aiResponse) {
            // 尝试提取AI回复中 ```json 之前的部分作为摘要
            const jsonStartIndex = aiResponse.indexOf('```'); // 寻找第一个代码块开始
            const potentialSummary = jsonStartIndex !== -1 ? aiResponse.substring(0, jsonStartIndex).trim() : aiResponse.trim();
            // 限制摘要长度，避免过长
            summary = potentialSummary.length > 0 && potentialSummary.length < 500
                ? potentialSummary.split('\n\n')[0] // 取第一段
                : '(AI已生成编辑建议)';
            summary = summary || '(AI已生成编辑建议)'; // 确保有默认值
        }

        if (!editsToApply || editsToApply.length === 0) {
            return `
            <div>
                <h4>修改摘要</h4>
                <p>${escapeHtml(summary)}</p>
                <h4>未检测到有效的修改</h4>
                <p class="text-muted">AI未能提供可应用的修改建议，或者提供的建议无效/未改变任何内容。</p>
                ${aiResponse ? `<div style="font-size: 0.8em; margin-top:10px; max-height: 150px; overflow-y: auto; background: var(--bg-darker); padding: 5px; border: 1px solid var(--border-color-subtle); border-radius: 4px;"><strong>原始AI回复:</strong><br>${escapeHtml(aiResponse)}</div>` : ''}
            </div>`;
        }

        return `
        <div>
            <h4>修改摘要</h4>
            <p>${escapeHtml(summary)}</p>

            <h4>将修改 ${editsToApply.length} 个条目</h4>
            <div class="edit-preview-list" style="max-height: 300px; overflow-y: auto; border: 1px solid var(--border-color-subtle); padding: 10px; border-radius: 6px; background: var(--bg-dark);">
                ${editsToApply.map(edit => {
                    const original = originalEntries.find(e => e.uid === edit.uid);
                    if (!original) return ''; // 安全检查

                    const changesHtml = Object.keys(edit)
                        .filter(key => key !== 'uid') // 排除uid本身
                        .map(key => {
                            const originalValue = original[key];
                            const newValue = edit[key];
                            // 再次确认值不同才显示 (validateAndMergeEdits已做过，这里是双重保险)
                            if (JSON.stringify(originalValue) !== JSON.stringify(newValue)) {
                                return `
                                    <div style="margin-top: 5px; padding-left: 15px; border-left: 2px solid var(--border-color);">
                                        <span style="color: var(--text-secondary); font-weight: bold;">${escapeHtml(key)}:</span>
                                        <div style="display: flex; flex-direction: column; margin-top: 2px;">
                                            <span style="text-decoration: line-through; color: #FF6384; margin-right: 5px; font-size: 0.9em; opacity: 0.8;">${formatValue(originalValue)}</span>
                                            <div style="display: flex; align-items: center;">
                                                <span style="color: #aaa; margin: 0 5px;">→</span>
                                                <span style="color: #4BC0C0; font-weight: bold; font-size: 0.9em;">${formatValue(newValue)}</span>
                                            </div>
                                        </div>
                                    </div>
                                `;
                            }
                            return ''; // 值相同，不显示
                        }).join('');

                    // 如果没有检测到实际变化，则不显示该条目 (理论上不会发生，因为editsToApply已过滤)
                    if (!changesHtml) return '';

                    return `
                    <div class="edit-preview-item" style="margin-bottom: 15px; padding-bottom: 10px; border-bottom: 1px dashed var(--border-color-subtle);">
                        <div><strong>条目:</strong> ${escapeHtml(original.comment || `UID: ${original.uid}`)} (UID: ${original.uid})</div>
                        ${changesHtml}
                    </div>
                    `;
                }).join('')}
            </div>
        </div>
        `;
    }

    // 格式化值显示 (需要确保这个函数在你的脚本中定义了)
    function formatValue(value) {
        const maxLength = 80; // 预览中显示的最大长度
        let displayValue;

        if (value === null || value === undefined) {
            return '<em style="color: var(--text-secondary);">(空)</em>';
        } else if (typeof value === 'boolean') {
            return value ? '<span style="color: #90EE90;">是</span>' : '<span style="color: #FF4500;">否</span>';
        } else if (Array.isArray(value)) {
            // 对数组元素也进行格式化和截断
            const formattedArray = value.map(v => formatValue(v)); // 递归格式化
            displayValue = `[${formattedArray.join(', ')}]`;
            // 注意：这里可能导致最终字符串很长，但截断数组本身比较复杂
        } else if (typeof value === 'object') {
            displayValue = JSON.stringify(value); // 对象直接显示JSON
        } else {
            displayValue = String(value).replace(/\n/g, '↵'); // 替换换行符以便单行显示
        }

        const escapedValue = escapeHtml(displayValue);
        if (escapedValue.length > maxLength) {
            // 使用 pre-wrap 保留可能的换行符，同时允许文本换行
            return `<span title="${escapedValue}" style="white-space: pre-wrap; word-break: break-all;">${escapedValue.substring(0, maxLength - 3)}...</span>`;
        } else {
            return `<span style="white-space: pre-wrap; word-break: break-all;">${escapedValue}</span>`;
        }
    }

    // --- AI Analysis (Modified for generateRaw, detailed data, and history) ---
    async function handleAiAnalysis(contentContainer$, stats, query) {
        logDebug(`handleAiAnalysis: 开始处理 AI 分析请求 (使用 generateRaw)，查询: "${query}"`);
        const outputArea = contentContainer$.find('#ai-output-area');
        const aiButton = contentContainer$.find('#ai-ask-button');
        const aiQueryInput = contentContainer$.find('#ai-query-input');
        const clearButton = contentContainer$.find('#ai-clear-chat-button');

        outputArea.html('<div style="padding: 10px;"><i class="fas fa-spinner fa-spin"></i> AI 正在思考...</div>').addClass('show').css('display', 'block');
        aiButton.prop('disabled', true);
        aiQueryInput.prop('disabled', true);
        clearButton.prop('disabled', true);

        try {
            // 获取各个lorebook的详细条目元数据
            const lorebooksWithEntries = {};
            for (const [name, data] of Object.entries(stats.byLorebook)) {
                try {
                    // 获取当前lorebook的所有条目
                    const entries = await TavernHelper.getLorebookEntries(name);

                    // 精简条目数据，只保留必要的元数据，完全不包含内容
                    const processedEntries = entries.map(entry => ({
                        uid: entry.uid,
                        type: entry.type,              // constant, selective, vectorized
                        enabled: entry.enabled,        // 是否启用
                        comment: entry.comment,        // 条目名称/备注
                        order: entry.order,            // 排序顺序
                        position: entry.position,      // 插入位置
                        scan_depth: entry.scan_depth,  // 扫描深度
                        depth: entry.depth,            // 插入深度
                        tk: getEntryTokens(entry),     // token数量
                        // 如果是selective或vectorized类型，包含关键词
                        key: entry.key || [],
                        // 不包含content或content_preview
                    }));

                    lorebooksWithEntries[name] = {
                        source: data.src,
                        constantTokens: data.c,
                        selectiveTokens: data.s,
                        vectorizedTokens: data.v,
                        totalEnabledTokens: data.t,
                        entries: processedEntries
                    };
                } catch (error) {
                    logDebug(`获取世界书 '${name}' 条目出错:`, error);
                    lorebooksWithEntries[name] = {
                        source: data.src,
                        constantTokens: data.c,
                        selectiveTokens: data.s,
                        vectorizedTokens: data.v,
                        totalEnabledTokens: data.t,
                        entries: [],
                        error: error.message
                    };
                }
            }

            const dataForAI = {
                summary: {
                    charName: stats.characterName,
                    charTk: stats.characterCardTokens,
                    totalConstTk: stats.totalConstantTokens,
                    totalSeleTk: stats.totalSelectiveTokens,
                    totalVectTk: stats.totalVectorizedTokens,
                    totalTk: stats.totalTokens
                },
                lorebooks: lorebooksWithEntries // 现在包含了详细条目元数据
            };

            // 构建 Prompt 和有序提示列表 (包含历史)
            const currentQueryContent = `基于以下 Token 统计数据 (JSON 格式，包含角色卡、各个世界书的统计和所有条目的元数据):\n\`\`\`json\n${JSON.stringify(dataForAI, null, 1)}\n\`\`\`\n请简洁回答用户问题。重要提示：\n- 'selective' (绿灯) Token 只有在其关键词在最近聊天历史中匹配时才会被包含在上下文中。\n- 'vectorized' (向量) Token 是基于语义相似度激活的，也可能结合关键词。\n- 这里显示的是绿灯和向量条目如果被激活时的最大潜在 Token 数。\n- 你现在可以访问每个条目的详细元数据，包括启用状态、插入位置、关键词等信息。\n\n用户问题: "${query}"\n\n回答:`;

            // 余下的代码保持不变...
            const currentUserPrompt = { role: 'user', content: currentQueryContent };
            const promptsToSend = [...aiAnalysisHistory];
            promptsToSend.push(currentUserPrompt);

            if (promptsToSend.length > MAX_AI_HISTORY) {
                promptsToSend.splice(0, promptsToSend.length - MAX_AI_HISTORY);
                logDebug(`AI分析历史已截断，保留最近 ${MAX_AI_HISTORY} 条消息`);
            }

            logDebug("handleAiAnalysis: 发送给 AI 的 Prompts (数据已简化):", promptsToSend);

            if (typeof TavernHelper?.generateRaw !== 'function') {
                throw new Error("AI 功能依赖的 TavernHelper.generateRaw 未找到。");
            }

            // 可能需要增加输出token限制，因为输入更大了
            const result = await TavernHelper.generateRaw({
                ordered_prompts: promptsToSend,
                max_new_tokens: 1024, // 增加输出长度限制
            });

            // 更新对话历史
            aiAnalysisHistory.push(currentUserPrompt);
            aiAnalysisHistory.push({ role: 'assistant', content: result || "(AI 没有提供回答)" });

            if (aiAnalysisHistory.length > MAX_AI_HISTORY) {
                aiAnalysisHistory.splice(0, aiAnalysisHistory.length - MAX_AI_HISTORY);
            }

            // 显示结果逻辑保持不变
            outputArea.html('');
            const typewriterElem = parent$('<div class="typewriter"></div>');
            outputArea.append(typewriterElem);
            const text = result || "(AI 没有提供回答)";
            let i = 0;
            const typeSpeed = 20;

            function typeWriter() {
                if (i < text.length) {
                    typewriterElem.append(document.createTextNode(text.charAt(i)));
                    i++;
                    outputArea.scrollTop(outputArea[0].scrollHeight);
                    setTimeout(typeWriter, typeSpeed);
                } else {
                    setTimeout(() => {
                        typewriterElem.removeClass('typewriter');
                        typewriterElem.css({
                            'border-right': 'none',
                            'white-space': 'pre-wrap',
                            'width': 'auto'
                        });
                        outputArea.scrollTop(outputArea[0].scrollHeight);
                    }, 500);
                }
            }
            typeWriter();

        } catch (error) {
            console.error("AI 分析出错:", error);
            outputArea.html(`<p style="color: red;">AI 分析时出错: ${escapeHtml(error.message)}</p>`);
        } finally {
            logDebug("handleAiAnalysis: AI 处理完成或失败。");
            aiButton.prop('disabled', false);
            aiQueryInput.prop('disabled', false);
            clearButton.prop('disabled', false);
        }
    }

    // [修改] 主要显示函数 - 更新事件绑定逻辑
