function ensureNumericUID(uid) {
    if (uid === undefined || uid === null) {
        console.warn("角色世界书: 收到undefined或null的UID值");
        return -1;
    }

    if (typeof uid === 'number') {
        return Math.floor(uid);
    }

    if (typeof uid === 'string') {
        uid = uid.replace(/[^0-9\-]/g, '');
        if (uid === '' || uid === '-') {
            console.warn(`角色世界书: UID字符串"${uid}"无法解析为数字`);
            return -1;
        }
    }

    let numericUid;
    try {
        numericUid = Math.floor(Number(uid));
    } catch (e) {
        console.error(`角色世界书: UID值"${uid}"转换为数字时出错`, e);
        return -1;
    }

    if (isNaN(numericUid) || !isFinite(numericUid)) {
        console.error(`角色世界书: UID值"${uid}"(${typeof uid})不是有效数字`);
        return -1;
    }

    return numericUid;
}

function errorCatched(fn, context = '角色世界书') {
    const onError = (error) => {
        let iframeName = 'UnknownIframe';
        try {
            iframeName = (typeof getIframeName === 'function') ? getIframeName() : 'UnknownIframe';
        } catch (e) {
            console.warn(`(${context}) 无法获取iframe名称:`, e);
        }
        const errorMessage = `${iframeName} (${context})中发生错误: ${error.stack || error.message || error.name}`;
        console.error(errorMessage);
        if (typeof triggerSlash === 'function') {
            try {
                triggerSlash(`echo severity=error ${errorMessage.substring(0, 500)}...`);
            } catch (e) {
                console.error(`(${context}) 无法触发slash命令报告错误:`, e);
            }
        }
        console.error(`${context}错误: ${error.message || '未知错误'}`);
    };
    return (...args) => {
        try {
            const result = fn(...args);
            if (result instanceof Promise) {
                return result.catch(error => {
                    onError(error);
                    throw error;
                });
            }
            return result;
        } catch (error) {
            onError(error);
            throw error;
        }
    };
}

/* --- API封装 --- */
const triggerSlash = window.triggerSlash;
const getIframeName = window.getIframeName;
const TavernHelper = window.TavernHelper || {};
const getLorebookEntries = window.getLorebookEntries;
const getLorebooks = window.getLorebooks;
const getLorebookSettings = window.getLorebookSettings;

const getLorebookSettingsSafe = errorCatched(async () => {
    if (typeof getLorebookSettings !== 'function') {
        console.error('角色世界书: getLorebookSettings函数不可用');
        throw new Error('getLorebookSettings函数不存在');
    }
    return await getLorebookSettings() || { selected_global_lorebooks: [] };
}, 'getLorebookSettings');

const getLorebooksSafe = errorCatched(async () => {
    if (typeof getLorebooks !== 'function') {
        console.error('角色世界书: getLorebooks函数不可用');
        throw new Error('getLorebooks函数不存在');
    }
    return await getLorebooks() || [];
}, 'getLorebooks');

// 获取当前全局启用的世界书列表
const getGlobalLorebooks = errorCatched(async () => {
    try {
        const settings = await getLorebookSettingsSafe();
        return settings.selected_global_lorebooks || [];
    } catch (error) {
        console.error('角色世界书: 获取全局世界书失败', error);
        return [];
    }
}, 'getGlobalLorebooks');

const getLorebookEntriesSafe = errorCatched(async (lorebookName) => {
    if (typeof getLorebookEntries !== 'function' && (!TavernHelper || typeof TavernHelper.getLorebookEntries !== 'function')) {
        console.error('角色世界书: getLorebookEntries函数不可用');
        throw new Error('getLorebookEntries函数不存在');
    }
    try {
        if (TavernHelper && typeof TavernHelper.getLorebookEntries === 'function') {
            const entries = await TavernHelper.getLorebookEntries(lorebookName);
            return entries || [];
        } else {
            const entries = await getLorebookEntries(lorebookName) || [];
            return entries;
        }
    } catch (error) {
