// 显示条目编辑器
const showEntryEditor = errorCatched(async (lorebookName, entryUid, isGlobal = false) => {
    const numericUid = ensureNumericUID(entryUid);
    const parentDoc = window.parent.document;
    let $editorPanel = $(`#${LOREBOOK_EDITOR_PANEL_ID}`, parentDoc);

    if (!$editorPanel.length) {
        alert('找不到编辑器面板，请刷新页面后重试');
        return;
    }

    const $form = $editorPanel.find('#entry-edit-form');
    $form.find('.save-button').text('保存').prop('disabled', false);
    $form.find('.debug-info').hide();

    $editorPanel.find('.loading-spinner').show();
    $editorPanel.find('#entry-edit-form').hide();
    $editorPanel.css('display', 'flex');

    const entry = await getLorebookEntry(lorebookName, numericUid);

    if (!entry) {
        $editorPanel.find('.loading-spinner').text('加载条目失败! 找不到指定条目').css('color', 'red');
        return;
    }

    const isConstant = entry.type === 'constant';

    // 填充表单数据
    $form.find('#entry-uid').val(numericUid);
    $form.find('#entry-lorebook').val(lorebookName);
    $form.find('#entry-is-global').val(isGlobal ? 'true' : 'false');
    $form.find('#entry-comment').val(entry.comment || '');
    $form.find('#entry-content').val(entry.content || '');
    $form.find('#entry-keys').val(Array.isArray(entry.keys) ? entry.keys.join(', ') : '');
    $form.find('#entry-position').val(entry.position || 'after_character_definition');
    $form.find('#entry-depth').val(entry.depth !== undefined && entry.depth !== null ? entry.depth : 4);
    $form.find('#entry-probability').val(entry.probability || 100);
    $form.find('#entry-constant').prop('checked', isConstant);

    // 设置UI状态
    if (isConstant) {
        $form.find('.constant-toggle-slider').css('background-color', '#2196F3');
        $form.find('.label-left').css('color', '#aaa').css('font-weight', 'normal');
        $form.find('.label-right').css('color', '#fff').css('font-weight', 'bold');
    } else {
        $form.find('.constant-toggle-slider').css('background-color', '#4CAF50');
        $form.find('.label-left').css('color', '#fff').css('font-weight', 'bold');
        $form.find('.label-right').css('color', '#aaa').css('font-weight', 'normal');
    }

    // 设置隐藏字段
    $form.find('#entry-selective').val(entry.selective === true ? 'on' : 'off');
    $form.find('#entry-addMemo').val(entry.addMemo === true ? 'on' : 'off');
    $form.find('#entry-useProbability').val(entry.useProbability === true ? 'on' : 'off');

    // 检查是否需要显示深度输入框
    toggleDepthFieldVisibility($form.find('#entry-position').val());

    $editorPanel.find('.loading-spinner').hide();
    $form.show();
}, 'showEntryEditor');

function toggleDepthFieldVisibility(positionValue) {
    const parentDoc = window.parent.document;
    const $depthRow = $(`#${LOREBOOK_EDITOR_PANEL_ID} .depth-row`, parentDoc);

    const needsDepth = positionValue === 'at_depth_as_system' || 
                      positionValue === 'at_depth_as_assistant' || 
                      positionValue === 'at_depth_as_user';

    $depthRow.find('input').prop('disabled', !needsDepth);
    if (needsDepth) {
        $depthRow.removeClass('depth-disabled');
    } else {
        $depthRow.addClass('depth-disabled');
    }
}

// 保存编辑的条目
const saveEditedEntry = errorCatched(async (formData) => {
    const lorebookName = formData.lorebook;
    const numericUid = ensureNumericUID(formData.uid);
    const isGlobal = formData.is_global === 'true';

    try {
        const entries = await TavernHelper.getLorebookEntries(lorebookName);
        if (!entries || entries.length === 0) {
            return { success: false, message: '获取条目失败' };
        }

        let originalEntry = null;
        for (const entry of entries) {
            const entryNumericUid = ensureNumericUID(entry.uid);
            if (entryNumericUid === numericUid) {
                originalEntry = entry;
                break;
            }
        }

        if (!originalEntry) {
            return { success: false, message: '未找到原始条目' };
        }

        const probability = parseInt(formData.probability);
        const useProbability = probability < 100;
        const isConstant = formData.constant === 'on';

        const updatedEntry = JSON.parse(JSON.stringify(originalEntry));
        updatedEntry.uid = originalEntry.uid;

        updatedEntry.comment = formData.comment;
        updatedEntry.content = formData.content;
        updatedEntry.keys = formData.keys.split(',').map(k => k.trim()).filter(k => k);
        updatedEntry.position = formData.position;
        updatedEntry.depth = parseInt(formData.depth);
        updatedEntry.probability = probability;
        updatedEntry.type = isConstant ? 'constant' : 'selective';
        updatedEntry.selective = formData.selective === 'on';
        updatedEntry.addMemo = formData.addMemo === 'on';
        updatedEntry.useProbability = useProbability;

        await safeSetLorebookEntries(lorebookName, [updatedEntry]);
        return { success: true, message: '保存成功', isGlobal: isGlobal };
    } catch (error) {
        console.error(`角色世界书: 保存条目时出错`, error);
        return { success: false, message: `保存失败: ${error.message || '未知错误'}` };
    }
}, 'saveEditedEntry');

// 验证条目更新是否成功
const verifyEntryUpdate = async (lorebookName, numericUid, updatedEntry) => {
    try {
        console.log(`角色世界书: 验证条目更新...`);
        // 等待短暂时间让保存生效
        await new Promise(resolve => setTimeout(resolve, 300));

        const verifyEntries = await TavernHelper.getLorebookEntries(lorebookName);

        for (const entry of verifyEntries) {
            const entryNumericUid = ensureNumericUID(entry.uid);
            if (entryNumericUid === numericUid) {
                // 检查几个关键字段
                const commentMatch = entry.comment === updatedEntry.comment;
                const contentMatch = entry.content && updatedEntry.content && 
                                    entry.content.substring(0, 20) === updatedEntry.content.substring(0, 20);
                const typeMatch = entry.type === updatedEntry.type;

                console.log(`角色世界书: 验证结果 - 标题匹配: ${commentMatch}, 内容匹配: ${contentMatch}, 类型匹配: ${typeMatch}`);

                // 至少有两个字段匹配才算成功
                return (commentMatch && typeMatch) || (commentMatch && contentMatch) || (typeMatch && contentMatch);
            }
        }

        console.log(`角色世界书: 验证时找不到条目 ${numericUid}`);
        return false;
    } catch (error) {
        console.error(`角色世界书: 验证更新时出错`, error);
        return false;
    }
};

/* --- 移动设备检测 --- */
const isMobile = () => {
    const userAgent = navigator.userAgent || navigator.vendor || window.opera;
    const isMobileUA = /android|webos|iphone|ipad|ipod|blackberry|iemobile|opera mini|mobile|tablet/i.test(userAgent);
    const isTouchDevice = ('ontouchstart' in window) && (navigator.maxTouchPoints > 0 || navigator.msMaxTouchPoints > 0);
    const isSmallScreen = window.innerWidth <= 768;

    return isMobileUA || (isTouchDevice && isSmallScreen);
};

