    }

    try {
        // 获取当前角色卡绑定的世界书
        let boundLorebooks = [];

        // 首先尝试使用TavernHelper获取主要世界书
        if (TavernHelper && typeof TavernHelper.getCurrentCharPrimaryLorebook === 'function') {
            const primaryLorebook = TavernHelper.getCurrentCharPrimaryLorebook();
