            let $entriesContainer = $listContainer.find(`.lorebook-entries-container[data-lorebook-name="${lorebookName}"]`);
            let $tableHeader = $listContainer.find(`.lorebook-table-header[data-lorebook-name="${lorebookName}"]`);

            // 如果不存在，则创建它们
            if ($lorebookTitle.length === 0) {
                // 创建世界书标题区域
                $lorebookTitle = createLorebookTitleSection(lorebookName);
                $listContainer.append($lorebookTitle);

                // 添加表头
                $tableHeader = $('<div></div>').addClass('lorebook-table-header').attr('data-lorebook-name', lorebookName);
                $tableHeader.append($('<div></div>').addClass('header-drag').html(''));
                $tableHeader.append($('<div></div>').addClass('header-expand').html(''));
                $tableHeader.append($('<div></div>').addClass('header-toggle').html('启用'));
                $tableHeader.append($('<div></div>').addClass('header-title').html('标题'));
                $tableHeader.append($('<div></div>').addClass('header-constant').html('类型'));
                $tableHeader.append($('<div></div>').addClass('header-position').html('插入位置'));
                $tableHeader.append($('<div></div>').addClass('header-depth').html('深度'));
                $tableHeader.append($('<div></div>').addClass('header-order').html('顺序'));
                $tableHeader.append($('<div></div>').addClass('header-probability').html('概率'));
                $tableHeader.append($('<div></div>').addClass('header-actions').html('选择'));

                $listContainer.append($tableHeader);

                // 添加条目容器
                $entriesContainer = $('<div></div>')
                    .addClass('lorebook-entries-container')
                    .attr('data-lorebook-name', lorebookName);
                $listContainer.append($entriesContainer);

                // 获取并加载所有条目
                try {
                    const entries = await getLorebookEntriesSafe(lorebookName);

                    // 按UID排序
                    entries.sort((a, b) => parseInt(a.uid) - parseInt(b.uid));

                    // 更新条目数量显示
                    $lorebookTitle.find('.lorebook-entries-count')
                                  .text(`共 ${entries.length} 个条目`);

                    // 如果没有条目，显示提示信息
                    if (entries.length === 0) {
                        $entriesContainer.append('<div class="no-entries-message">该世界书没有条目</div>');
                    } else {
                        // 添加所有条目
                        for (const entry of entries) {
                            addEntryItem(entry, $entriesContainer, lorebookName);
                        }

                        // 应用保存的UI排序
                        applySavedUISort($entriesContainer, lorebookName);

                        // 启用拖动排序功能
                        enableDragSort($entriesContainer);
                    }
                } catch (error) {
                    console.error(`角色世界书: 获取世界书 '${lorebookName}' 条目失败`, error);
                    $entriesContainer.append('<div class="no-entries-message">加载条目失败</div>');
                }
            } else if (forceRefresh) {
                // 如果已经存在但又需要强制刷新，则需要重新获取数据
                try {
                    const entries = await getLorebookEntriesSafe(lorebookName);

                    // 按UID排序
                    entries.sort((a, b) => parseInt(a.uid) - parseInt(b.uid));

                    // 更新条目数量显示
                    $lorebookTitle.find('.lorebook-entries-count')
                                  .text(`共 ${entries.length} 个条目`);

                    // 清空容器
                    $entriesContainer.empty();

                    // 如果没有条目，显示提示信息
                    if (entries.length === 0) {
                        $entriesContainer.append('<div class="no-entries-message">该世界书没有条目</div>');
                    } else {
                        // 添加所有条目
                        for (const entry of entries) {
                            addEntryItem(entry, $entriesContainer, lorebookName);
                        }

                        // 应用保存的UI排序
                        applySavedUISort($entriesContainer, lorebookName);

                        // 启用拖动排序功能
                        enableDragSort($entriesContainer);
                    }
                } catch (error) {
                    console.error(`角色世界书: 获取世界书 '${lorebookName}' 条目失败`, error);
                    $entriesContainer.empty().append('<div class="no-entries-message">加载条目失败</div>');
                }
            }

            // 如果不是最后一个世界书，添加分隔线
            if (lorebookName !== boundLorebooks[boundLorebooks.length - 1]) {
                // 检查是否已存在分隔线
                const $nextElement = $entriesContainer.next();
                if (!$nextElement.hasClass('lorebook-divider')) {
                    $listContainer.append('<div class="lorebook-divider"></div>');
                }
            }
        }

        // 恢复滚动位置
        setTimeout(() => {
            $listContainer.scrollTop(scrollTop);
        }, 10);

        // 设置初始化标志
        hasInitializedLorebooks = true;

    } catch (error) {
        if (forceRefresh) {
            $listContainer.empty().append('<p>加载绑定世界书时出错</p>');
        }
        console.error('角色世界书: 更新绑定世界书列表时出错', error);
    }
