            probability: 100
        };

        // 调用API创建条目
        try {
            await createLorebookEntries(lorebookName, [newEntry]);
        } catch (e) {
            console.log(`角色世界书: API调用出现异常，但继续执行`);
        }

        // 等待1秒
        await new Promise(resolve => setTimeout(resolve, 1000));

        // 刷新列表，显示新创建的条目
        const parentDoc = window.parent.document;
        const $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);

        if (isGlobal) {
            // 对于全局世界书，如果标题已展开且已加载过条目，则只刷新该世界书的条目
            const $title = $(`.lorebook-title[data-lorebook-name="${lorebookName}"]`, parentDoc);
            if ($title.attr('data-expanded') === 'true' && $title.attr('data-loaded') === 'true') {
                const $container = $(`.lorebook-entries-wrapper[data-lorebook-name="${lorebookName}"]`, parentDoc);
                if ($container.length) {
                    await loadLorebookEntries(lorebookName, $container, true);
                }
            } else {
                const $list = $panel.find(`#${GLOBAL_LOREBOOK_LIST_CONTAINER_ID}`);
                // 仅刷新标题列表
                updateGlobalLorebooksList($list, true);
            }
        } else {
            const $list = $panel.find(`#${LOREBOOK_LIST_CONTAINER_ID}`);
            // 角色世界书仍然使用全刷新
            updateBoundLorebooksList($list, true);
        }

        return true;
    } catch (error) {
        // 即使出错也尝试刷新
        try {
            const parentDoc = window.parent.document;
            const $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);

            if (isGlobal) {
                const $list = $panel.find(`#${GLOBAL_LOREBOOK_LIST_CONTAINER_ID}`);
                updateGlobalLorebooksList($list, true);
            } else {
                const $list = $panel.find(`#${LOREBOOK_LIST_CONTAINER_ID}`);
                updateBoundLorebooksList($list, true);
            }
        } catch (e) {
            console.log(`角色世界书: 刷新出错`);
        }

        return true;
    }
}, 'createNewLorebookEntry');

// 添加一个条目到列表
function addEntryItem(entry, $container, lorebookName) {
    // 确保UID为数字
    const numericUid = ensureNumericUID(entry.uid);
    console.log(`添加条目到列表: ID=${numericUid}, 名称=${entry.comment}, 来自${lorebookName}`);
    const entryTitle = entry.comment || '未命名条目';
    const isEnabled = entry.enabled !== false; // 默认为启用状态
    const isConstant = entry.type === 'constant'; // 使用type字段判断是否为常量
    console.log(`条目 ${numericUid} 的type: ${entry.type}, 解析为常量状态: ${isConstant}`);
    const position = entry.position || 'after_character_definition';
    const depth = entry.depth !== undefined && entry.depth !== null ? entry.depth : 4;
    const order = entry.order || 0;
    const probability = entry.probability || 100;

    const $item = $('<div></div>')
        .addClass(LOREBOOK_ENTRY_CLASS)
        .attr('data-entry-uid', numericUid)
        .attr('data-entry-lorebook', lorebookName)
        .attr('data-enabled', isEnabled)
        .attr('data-expanded', 'false')
        .attr('data-order', order);

    if (!isEnabled) $item.addClass('disabled-entry');

    // 条目的主显示区域
    const $header = $('<div></div>').addClass('entry-header');

    // 拖动手柄
    const $dragHandle = $('<div></div>').addClass('drag-handle')
        .html('<i class="fa-solid fa-grip-vertical"></i>');

    // 添加移动按钮（上下箭头）
    const $moveButtonsContainer = $('<div></div>')
        .addClass('move-buttons-container')
        .css({
            'display': 'none', // 默认在PC上隐藏
            'flex-direction': 'column',
            'margin': '0 5px',
            'position': 'relative',
            'z-index': '10'
        });

    // 如果是移动设备，则显示按钮
    if (isMobile()) {
        $moveButtonsContainer.css('display', 'flex');
    }

    // 上移按钮 - 使用内联样式确保显示
    const $moveUpButton = $('<button></button>')
        .addClass('move-button move-up-button')
        .html('<i class="fa-solid fa-chevron-up"></i>')
        .attr('title', '上移条目')
        .css({
            'background-color': '#444',
            'border': '1px solid #555',
            'color': '#fff',
            'cursor': 'pointer',
            'padding': '3px 0',
            'font-size': '14px',
            'line-height': '1',
            'width': '24px',
            'height': '24px',
            'border-radius': '3px',
            'margin': '2px 0',
            'text-align': 'center'
        });

    // 下移按钮 - 使用内联样式确保显示
    const $moveDownButton = $('<button></button>')
        .addClass('move-button move-down-button')
        .html('<i class="fa-solid fa-chevron-down"></i>')
        .attr('title', '下移条目')
        .css({
            'background-color': '#444',
            'border': '1px solid #555',
            'color': '#fff',
            'cursor': 'pointer',
            'padding': '3px 0',
            'font-size': '14px',
            'line-height': '1',
            'width': '24px',
            'height': '24px',
            'border-radius': '3px',
            'margin': '2px 0',
            'text-align': 'center'
        });

    console.log('角色世界书: 创建移动按钮 - 上下箭头');

    // 添加上下移动事件处理
    $moveUpButton.on('click', function(e) {
        e.stopPropagation();
        console.log('角色世界书: 点击上移按钮');
        // 添加视觉反馈
        $(this).css('background-color', '#6a4a7e').css('color', '#fff');
        setTimeout(() => {
            $(this).css('background-color', '').css('color', '');
        }, 300);
        // 使用已转换的numericUid而非原始entry.uid
        moveEntryUpDown(lorebookName, numericUid, 'up');
    });

    $moveDownButton.on('click', function(e) {
        e.stopPropagation();
        console.log('角色世界书: 点击下移按钮');
        // 添加视觉反馈
        $(this).css('background-color', '#6a4a7e').css('color', '#fff');
        setTimeout(() => {
            $(this).css('background-color', '').css('color', '');
        }, 300);
        // 使用已转换的numericUid而非原始entry.uid
        moveEntryUpDown(lorebookName, numericUid, 'down');
    });

    $moveButtonsContainer.append($moveUpButton).append($moveDownButton);

    // 展开/折叠按钮
    const $expandButton = $('<button></button>')
        .addClass('expand-button small-expand-button')
        .html('<i class="fa-solid fa-chevron-down"></i>');

    // 添加开关按钮
    const $toggle = $('<label></label>')
        .addClass(LOREBOOK_TOGGLE_SWITCH_CLASS)
        .html(`<input type="checkbox" ${isEnabled ? 'checked' : ''}>
               <span class="toggle-slider"></span>`);

    // 为滑块添加点击事件
    $toggle.find('input').on('change', function(e) {
        e.stopPropagation();
        const newState = $(this).prop('checked');
        // 使用已转换的numericUid而非原始entry.uid
        toggleEntryEnabled(lorebookName, numericUid, newState).then(success => {
            if (success) {
                $item.toggleClass('disabled-entry', !newState);
                $item.attr('data-enabled', newState);
                console.log(`已${newState ? '启用' : '禁用'}条目: ${entryTitle} (ID=${numericUid})`);

                // 保存当前滚动位置
                const parentDoc = window.parent.document;
                const $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);
                const $list = $panel.find(`#${LOREBOOK_LIST_CONTAINER_ID}`);
                const scrollTop = $list.scrollTop();

                // 更新单个条目状态，而不是刷新整个列表
                // updateBoundLorebooksList($list); // 注释掉这行，避免整个列表刷新

                // 在完成后恢复滚动位置
                setTimeout(() => {
                    $list.scrollTop(scrollTop);
                }, 10);
            } else {
                // 恢复原状态
                $(this).prop('checked', isEnabled);
                console.error(`操作失败，无法切换条目状态: ${entryTitle} (ID=${numericUid})`);
            }
        });
    });

    // 条目标题 - 可编辑
    const $titleInput = $('<input>')
        .addClass('entry-item-title')
        .attr('type', 'text')
        .attr('value', entryTitle)
        .attr('placeholder', '条目标题');

    // 激活方式开关（蓝绿灯）
    const $constantToggle = $('<div></div>').addClass('mini-constant-toggle');
    const $constantSwitch = $('<label></label>').addClass('mini-toggle-switch').html(`
        <input type="checkbox" class="constant-checkbox" ${isConstant ? 'checked' : ''}>
        <span class="mini-toggle-slider ${isConstant ? 'constant' : 'keyword'}"></span>
    `);

    $constantSwitch.find('input').on('change', function() {
        const isNowConstant = $(this).prop('checked');
        $constantSwitch.find('.mini-toggle-slider')
            .removeClass('constant keyword')
            .addClass(isNowConstant ? 'constant' : 'keyword');

        // 自动保存更改 - 使用numericUid
        saveEntryField(numericUid, lorebookName, 'type', isNowConstant ? 'constant' : 'selective');
    });

    $constantToggle.append($constantSwitch);

    // 位置选择器
    const $positionSelect = $('<select></select>').addClass('mini-position-select');
    const positions = [
        {value: 'before_character_definition', text: '角色定义前'},
        {value: 'after_character_definition', text: '角色定义后'},
        {value: 'before_example_messages', text: '示例消息前'},
        {value: 'after_example_messages', text: '示例消息后'},
        {value: 'before_author_note', text: '作者注释前'},
        {value: 'after_author_note', text: '作者注释后'},
        {value: 'at_depth_as_system', text: '@系统深度'},
        {value: 'at_depth_as_assistant', text: '@助手深度'},
        {value: 'at_depth_as_user', text: '@用户深度'}
    ];

    positions.forEach(pos => {
        const $option = $('<option></option>')
            .attr('value', pos.value)
            .text(pos.text);
        if (pos.value === position) $option.attr('selected', 'selected');
        $positionSelect.append($option);
    });

    $positionSelect.on('change', function() {
        const newPosition = $(this).val();
        // 更新深度输入框可见性
        const needsDepth = newPosition === 'at_depth_as_system' || 
                         newPosition === 'at_depth_as_assistant' || 
                         newPosition === 'at_depth_as_user';

        $depthInput.prop('disabled', !needsDepth);
        if (needsDepth) {
            $depthContainer.removeClass('depth-disabled');
        } else {
            $depthContainer.addClass('depth-disabled');
        }

        // 自动保存更改
        saveEntryField(numericUid, lorebookName, 'position', newPosition);
    });

    // 深度输入框
    const $depthContainer = $('<div></div>').addClass('depth-input-container');
    const needsDepth = position.includes('at_depth_as');

    // 始终显示，但在不需要时添加视觉提示
    if (!needsDepth) {
        $depthContainer.addClass('depth-disabled');
    }

    const $depthInput = $('<input>').addClass('mini-number-input depth-input')
        .attr('type', 'number')
        .attr('min', '0')
        .attr('max', '10')
        .attr('value', depth)
        .prop('disabled', !needsDepth);

    $depthInput.on('change', function() {
        if ($(this).prop('disabled')) return; // 如果禁用则不处理
        const newDepth = parseInt($(this).val());
        // 自动保存更改
        saveEntryField(numericUid, lorebookName, 'depth', newDepth);
    });

    $depthContainer.append($depthInput);

    // 顺序输入框
    const $orderContainer = $('<div></div>').addClass('order-input-container');
    const $orderInput = $('<input>').addClass('mini-number-input order-input')
        .attr('type', 'number')
        .attr('min', '0')
        .attr('value', order);

    $orderInput.on('change', function() {
        const newOrder = parseInt($(this).val());
        // 自动保存更改
        saveEntryField(numericUid, lorebookName, 'order', newOrder);

        // 在移动设备上，自动重新排序条目
        if (isMobile()) {
            // 保存当前滚动位置
            const parentDoc = window.parent.document;
            const $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);
            const $list = $panel.find(`#${LOREBOOK_LIST_CONTAINER_ID}`);
            const scrollTop = $list.scrollTop();

            // 显示加载提示
            const $container = $(this).closest('.lorebook-entries-container');
            if (!$container.find('.reordering-message').length) {
                $container.append('<div class="reordering-message">正在重新排序...</div>');
            }

            // 延迟执行，让UI有时间更新
            setTimeout(() => {
                // 刷新条目列表
                updateBoundLorebooksList($list);

                // 恢复滚动位置
                setTimeout(() => {
                    $list.scrollTop(scrollTop);
                    $container.find('.reordering-message').remove();
                }, 500);
            }, 100);
        }
    });

    $orderContainer.append($orderInput);

    // 概率输入框
    const $probContainer = $('<div></div>').addClass('prob-input-container');
    const $probInput = $('<input>').addClass('mini-number-input prob-input')
        .attr('type', 'number')
        .attr('min', '0')
        .attr('max', '100')
        .attr('value', probability);

    $probInput.on('change', function() {
        const newProb = parseInt($(this).val());
        // 自动保存更改
        saveEntryField(numericUid, lorebookName, 'probability', newProb);
    });

    $probContainer.append($probInput);

    // 添加选择复选框（替换原来的删除按钮）
    const $selectCheckboxContainer = $('<div></div>').addClass('select-checkbox-container');
    const $selectCheckbox = $('<input>').attr({
        type: 'checkbox',
        class: 'entry-select-checkbox',
        'data-entry-uid': numericUid,
        'data-entry-lorebook': lorebookName,
        title: '选择此条目'
    });

    // 防止点击复选框时触发条目的点击事件
    $selectCheckbox.on('click', function(e) {
        e.stopPropagation();
    });

    $selectCheckboxContainer.append($selectCheckbox);

    // 按照用户要求的顺序添加所有元素到头部
    $header.append($dragHandle)        // 拖动手柄
           .append($moveButtonsContainer) // 移动按钮
           .append($expandButton)      // 下拉按钮 
           .append($toggle)            // 激活开关
           .append($titleInput)        // 标题
           .append($constantToggle)    // 蓝绿灯
           .append($positionSelect)    // 插入位置
           .append($depthContainer)    // 深度
           .append($orderContainer)    // 顺序
           .append($probContainer)     // 触发概率
           .append($selectCheckboxContainer); // 选择复选框

    // 添加调试信息
    console.log('角色世界书: 完成条目元素添加', {
        '有上下箭头': $header.find('.move-buttons-container').length > 0,
        '有拖动手柄': $header.find('.drag-handle').length > 0,
        '条目ID': entry.uid,
        '条目标题': entryTitle
    });

    $item.append($header);

    // 创建展开区域
    const $expandArea = $('<div></div>').addClass('entry-expand-area').hide();

    // UID显示区域
    const $uidArea = $('<div></div>').addClass('uid-display-area');
    const $uidLabel = $('<label></label>').text('UID:');
    const $uidValue = $('<span></span>').addClass('uid-value').text(entry.uid);
    $uidArea.append($uidLabel).append($uidValue);

    // 内容文本框
    const $contentArea = $('<div></div>').addClass('content-edit-area');
    const $contentHeader = $('<div></div>').addClass('content-header');
    const $contentLabel = $('<label></label>').text('内容:');
    const $tokenCounter = $('<span></span>').addClass('token-counter').text('0 词符');
    $contentHeader.append($contentLabel).append($tokenCounter);

    const $contentInput = $('<textarea></textarea>').addClass('content-textarea')
        .attr('rows', '8')
        .val(entry.content || '');

    // 计算并更新token数量
    function updateTokenCount() {
        const text = $contentInput.val() || '';
        // 针对中文文本优化的token计数方法
        // 对于中文和符号，每个字符通常算作一个token
        const tokenCount = text.length;
        $tokenCounter.text(`${tokenCount} 词符`);
    }

    // 初始计算token
    updateTokenCount();

    $contentInput.on('input', updateTokenCount);

    $contentInput.on('change', function() {
        const newContent = $(this).val();
        // 自动保存更改
        saveEntryField(numericUid, lorebookName, 'content', newContent);
    });

    $contentArea.append($contentHeader).append($contentInput);

    // 关键词编辑区
    const $keywordsArea = $('<div></div>').addClass('keywords-edit-area');
    const $keywordsLabel = $('<label></label>').text('触发关键词 (逗号分隔):');
    const $keywordsInput = $('<input>').addClass('keywords-input')
        .attr('type', 'text')
        .val(Array.isArray(entry.keys) ? entry.keys.join(', ') : '');

    $keywordsInput.on('change', function() {
        const newKeywords = $(this).val().split(',').map(k => k.trim()).filter(k => k);
        // 自动保存更改
        saveEntryField(numericUid, lorebookName, 'keys', newKeywords);
    });

    $keywordsArea.append($keywordsLabel).append($keywordsInput);

    // 递归控制选项区域
    const $recursionArea = $('<div></div>').addClass('recursion-options-area');
    const $recursionLabel = $('<label></label>').addClass('recursion-label').text('递归控制:');

    // 防止递归复选框
    const $preventRecursionWrap = $('<div></div>').addClass('checkbox-wrapper');
    const $preventRecursion = $('<input>').attr({
        type: 'checkbox',
        id: `prevent-recursion-${numericUid}`, // 使用numericUid代替entry.uid
        checked: entry.prevent_recursion === true
    });
    const $preventRecursionLabel = $('<label></label>')
        .attr('for', `prevent-recursion-${numericUid}`) // 使用numericUid代替entry.uid
        .text('防止递归');

    $preventRecursion.on('change', function() {
        const isChecked = $(this).prop('checked');
        saveEntryField(numericUid, lorebookName, 'prevent_recursion', isChecked);
    });

    $preventRecursionWrap.append($preventRecursion).append($preventRecursionLabel);

    // 延迟递归复选框
    const $delayRecursionWrap = $('<div></div>').addClass('checkbox-wrapper');
    const $delayRecursion = $('<input>').attr({
        type: 'checkbox',
        id: `delay-recursion-${numericUid}`, // 使用numericUid代替entry.uid
        checked: entry.delay_until_recursion === true || typeof entry.delay_until_recursion === 'number'
    });
    const $delayRecursionLabel = $('<label></label>')
        .attr('for', `delay-recursion-${numericUid}`) // 使用numericUid代替entry.uid
        .text('延迟直到递归');

    $delayRecursion.on('change', function() {
        const isChecked = $(this).prop('checked');
        saveEntryField(numericUid, lorebookName, 'delay_until_recursion', isChecked);
    });

    $delayRecursionWrap.append($delayRecursion).append($delayRecursionLabel);

    // 排除递归复选框
    const $excludeRecursionWrap = $('<div></div>').addClass('checkbox-wrapper');
    const $excludeRecursion = $('<input>').attr({
        type: 'checkbox',
        id: `exclude-recursion-${numericUid}`, // 使用numericUid代替entry.uid
        checked: entry.exclude_recursion === true
    });
    const $excludeRecursionLabel = $('<label></label>')
        .attr('for', `exclude-recursion-${numericUid}`) // 使用numericUid代替entry.uid
        .text('排除递归');

    $excludeRecursion.on('change', function() {
        const isChecked = $(this).prop('checked');
        saveEntryField(numericUid, lorebookName, 'exclude_recursion', isChecked);
    });

    $excludeRecursionWrap.append($excludeRecursion).append($excludeRecursionLabel);

    // 将所有递归选项添加到递归区域
    $recursionArea.append($recursionLabel)
                  .append($preventRecursionWrap)
                  .append($delayRecursionWrap)
                  .append($excludeRecursionWrap);

    // 保存按钮区域
    const $actionArea = $('<div></div>').addClass('entry-action-area');
    const $saveBtn = $('<button></button>').addClass('entry-save-btn').text('保存全部').hide();

    $actionArea.append($saveBtn);

    // 添加所有编辑区域到展开区
    $expandArea.append($uidArea)
               .append($contentArea)
               .append($keywordsArea)
               .append($recursionArea)
               .append($actionArea);

    $item.append($expandArea);

    // 标题编辑事件
    $titleInput.on('change', function() {
        const newTitle = $(this).val();
        // 自动保存更改
        saveEntryField(numericUid, lorebookName, 'comment', newTitle);
    });

    // 展开/折叠事件
    $expandButton.on('click', function(e) {
        e.stopPropagation();
        const isExpanded = $item.attr('data-expanded') === 'true';
        if (isExpanded) {
            $expandArea.slideUp(200);
            $item.attr('data-expanded', 'false');
            $(this).html('<i class="fa-solid fa-chevron-down"></i>');
        } else {
            $expandArea.slideDown(200);
            $item.attr('data-expanded', 'true');
            $(this).html('<i class="fa-solid fa-chevron-up"></i>');
            // 展开时更新token计数
            updateTokenCount();
        }
    });

    $container.append($item);
}

// 添加删除条目的函数
const deleteLorebookEntry = errorCatched(async (lorebookName, entryUid, isGlobal = false) => {
    // 此函数已被批量删除功能替代
    // 为了保持向后兼容，保留函数但重定向到批量删除
    console.log('角色世界书: 单条目删除已弃用，重定向至批量删除');

    // 将单个条目转为批量删除形式
    const numericUid = ensureNumericUID(entryUid);
    return deleteSelectedEntries(lorebookName, isGlobal);
}, 'deleteLorebookEntry');

// 切换面板显示状态
const toggleLorebookPanel = errorCatched(async () => {
    console.log('角色世界书: 切换面板显示状态');
    const parentDoc = window.parent.document;
    const $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);
    const $button = $(`#${LOREBOOK_BUTTON_ID}`, parentDoc);

    if (!$panel.length) {
        console.error('角色世界书: 找不到面板元素');
        return;
    }

    if ($panel.is(':visible')) {
        console.log('角色世界书: 关闭面板');
        $panel.hide();
        if ($button.length) $button.removeClass('active');
    } else {
        console.log('角色世界书: 打开面板');

        // 判断当前活动的标签页
        const $activeTab = $panel.find(`.tab-button.${ACTIVE_TAB_CLASS}`);
        const activeTabId = $activeTab.length ? $activeTab.attr('id') : CHARACTER_TAB_ID;

        if (activeTabId === CHARACTER_TAB_ID) {
            const $list = $panel.find(`#${LOREBOOK_LIST_CONTAINER_ID}`);
            // 强制刷新面板
            await updateBoundLorebooksList($list, true);
        } else {
            const $list = $panel.find(`#${GLOBAL_LOREBOOK_LIST_CONTAINER_ID}`);
            // 强制刷新全局世界书面板
            await updateGlobalLorebooksList($list, true);
        }

        $panel.css('display', 'flex');
        if ($button.length) $button.addClass('active');
    }
}, 'toggleLorebookPanel');

// 切换标签页
const switchTab = errorCatched(async (tabId) => {
    console.log(`角色世界书: 切换标签页至 ${tabId}`);
    const parentDoc = window.parent.document;
    const $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);

    if (!$panel.length) {
        console.error('角色世界书: 找不到面板元素');
        return;
    }

    // 更新标签状态
    $panel.find('.tab-button').removeClass(ACTIVE_TAB_CLASS);
    $panel.find(`#${tabId}`).addClass(ACTIVE_TAB_CLASS);

    // 更新内容区域
    $panel.find('.tab-content').removeClass(ACTIVE_CONTENT_CLASS);
    const contentId = (tabId === CHARACTER_TAB_ID) ? CHARACTER_CONTENT_ID : GLOBAL_CONTENT_ID;
    const $contentToShow = $panel.find(`#${contentId}`);
    $contentToShow.addClass(ACTIVE_CONTENT_CLASS);

    // 刷新内容
    if (tabId === CHARACTER_TAB_ID) {
        // 刷新角色绑定世界书列表
        const $list = $contentToShow.find(`#${LOREBOOK_LIST_CONTAINER_ID}`);
        await updateBoundLorebooksList($list, true);
    } else {
        // 刷新全局世界书列表
        const $list = $contentToShow.find(`#${GLOBAL_LOREBOOK_LIST_CONTAINER_ID}`);
        await updateGlobalLorebooksList($list, true);
    }
}, 'switchTab');

