    /* 3. 创建编辑器面板 */
    let $editorPanel = $(`#${LOREBOOK_EDITOR_PANEL_ID}`, parentDoc);
    if ($editorPanel.length === 0) {
        console.log('角色世界书: 创建编辑器面板');
        const editorHtml = `
            <div id="${LOREBOOK_EDITOR_PANEL_ID}" class="lorebook-modal">
