        $('body', parentDoc).append(editorHtml);
        $editorPanel = $(`#${LOREBOOK_EDITOR_PANEL_ID}`, parentDoc);

        // 添加常量切换按钮的样式
        const constantToggleStyles = `
            <style id="constant-toggle-styles">
