    /* 4. 创建按钮HTML并插入 */
    let $button = $(`#${LOREBOOK_BUTTON_ID}`, parentDoc);
    // 如果按钮存在但不在扩展菜单中，删除它以便在正确位置重新创建
    if ($button.length > 0 && !$button.closest('#extensionsMenu').length) {
        console.log('角色世界书: 移除旧按钮实例。');
        $button.remove();
        $button = $(`#${LOREBOOK_BUTTON_ID}`, parentDoc); // 重新检查，现在应该为空
    }

    if ($button.length === 0) {
        const buttonHtml = `
            <div id="${LOREBOOK_BUTTON_ID}" class="list-group-item flex-container flexGap5 interactable" title="${LOREBOOK_BUTTON_TOOLTIP}" tabIndex="0">
                <i class="${LOREBOOK_BUTTON_ICON}"></i>
                <span>${LOREBOOK_BUTTON_TEXT_IN_MENU}</span>
            </div>
        `;
        const $extensionsMenu = $('#extensionsMenu', parentDoc);
        let inserted = false;

        if ($extensionsMenu.length) {
            try {
                $extensionsMenu.append(buttonHtml);
                inserted = true;
                console.log('角色世界书: 按钮已插入扩展菜单。');
            } catch (e) {
                console.error("角色世界书: 无法将按钮插入扩展菜单。", e);
            }
        } else {
            console.warn("角色世界书: 未找到扩展菜单 (#extensionsMenu)。无法将按钮插入菜单。");
        }

        if (inserted) {
            $button = $(`#${LOREBOOK_BUTTON_ID}`, parentDoc);
        } else if (!$extensionsMenu.length) {
            // 什么都不做，警告已记录
        } else {
            console.error("角色世界书: 插入按钮失败！");
        }
    }

