        $(this).closest('.lorebook-modal-content').find('.debug-info').toggle();
    });

    $(parentDoc).off(`click.${LOREBOOK_BUTTON_ID}`).on(`click.${LOREBOOK_BUTTON_ID}`, `#${LOREBOOK_BUTTON_ID}`, function(event) {
        event.preventDefault();
        toggleLorebookPanel();
    });

    $panel = $(`#${LOREBOOK_PANEL_ID}`, parentDoc);

    // 关闭按钮点击事件
    $panel.off('click.lorebookClose').on('click.lorebookClose', '.close-button', function() {
