        const $buttonToDeactivate = $(`#${LOREBOOK_BUTTON_ID}`, parentDoc);
        if ($panelToClose.length) $panelToClose.hide();
        if ($buttonToDeactivate.length) $buttonToDeactivate.removeClass('active');
    });

    // 标签页切换事件
    $panel.off('click.lorebookTabs').on('click.lorebookTabs', '.tab-button', function() {
